    <div class="slider-group">
        <label for="slider-slip-amount">amount of slip of hanging wall (0&nbsp;to&nbsp;1)</label>
        <input id="slider-slip-amount" type="range" min="0" max="1" step="0.01" value="0.5">
        <input id="value-slip-amount" class="value-box" type="text" value="0.50" readonly>
