</div>

<!-- VISUALIZATION AREA -->
<div id="visualization-container">
    <div id="check-container">
        <div>
            <input type="checkbox" id="checkbox-box">
            <label for="checkbox-box">box</label>
        </div>
    </div>
    <div id="canvas-container">
        <canvas id="webgl-canvas"></canvas>
        <!-- axis labels (screen-space) -->
        <div id="label-P" class="label">P</div>
        <div id="label-T" class="label">T</div>
        <div id="label-U" class="label">U</div>
    </div>
</div>

<!-- THREE.JS and helpers -->
<script src="https://cdn.jsdelivr.net/npm/three@0.160/build/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.160/examples/js/controls/OrbitControls.js"></script>

<script>
/* -------------------------- GLOBALS --------------------------- */
let scene, camera, renderer, controls;
let sphereMesh, originalPositions;
let pAxis = new THREE.Vector3(), tAxis = new THREE.Vector3(), uAxis = new THREE.Vector3();
let boxHelper;
const deformationScale = 0.35;

/* --------------------- INITIALIZATION ------------------------- */
init();
animate();

/* ------------------ UI EVENT HANDLERS ------------------------- */
['slider-strike','slider-dip','slider-slip-angle','slider-slip-amount'].forEach(id=>{
    const slider = document.getElementById(id);
    slider.addEventListener('input', ()=>{
        document.getElementById('value-'+id.split('-')[1]).value =
            (id==='slider-slip-amount' ? Number(slider.value).toFixed(2) : slider.value);
        updateSphere();
    });
});

document.getElementById('checkbox-box').addEventListener('change', e=>{
    if(boxHelper) boxHelper.visible = e.target.checked;
});

document.getElementById('btn-reset').addEventListener('click', ()=>{
    setDefaults();
    updateSphere();
});

/* ----------------------- FUNCTIONS ---------------------------- */
function setDefaults(){
    const defaults = {
        'slider-strike':180,
        'slider-dip':60,
        'slider-slip-angle':90,
        'slider-slip-amount':0.5
    };
    for(const id in defaults){
        const val = defaults[id];
        const slider = document.getElementById(id);
        slider.value = val;
        document.getElementById('value-'+id.split('-')[1]).value =
            (id==='slider-slip-amount' ? Number(val).toFixed(2) : val);
    }
    document.getElementById('checkbox-box').checked = false;
    if(boxHelper) boxHelper.visible = false;
}

function init(){
    //Renderer
    renderer = new THREE.WebGLRenderer({canvas:document.getElementById('webgl-canvas'),antialias:true});
    renderer.setPixelRatio(window.devicePixelRatio);
    //Scene & Camera
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf4f4f4);
    camera = new THREE.PerspectiveCamera(45, 2, 0.1, 100);
    camera.position.set(3,2,3);
    controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    //Lights
    scene.add(new THREE.AmbientLight(0xffffff, 0.6));
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
    dirLight.position.set(5,5,5);
    scene.add(dirLight);

    //Sphere
    const geom = new THREE.SphereGeometry(1,64,32);
    originalPositions = Float32Array.from(geom.attributes.position.array); //save original
    const colorAttr = new THREE.Float32BufferAttribute(new Float32Array(geom.attributes.position.count*3),3);
    geom.setAttribute('color', colorAttr);
    const material = new THREE.MeshStandardMaterial({vertexColors:true,flatShading:true});
    sphereMesh = new THREE.Mesh(geom, material);
    scene.add(sphereMesh);

    //Compass Rose (XZ plane circle)
    const rose = new THREE.Line(
        new THREE.CircleGeometry(1.2,64),
        new THREE.LineDashedMaterial({color:0x000000,dashSize:0.05,gapSize:0.05})
    );
    rose.geometry.rotateX(Math.PI/2);
    rose.computeLineDistances();
    scene.add(rose);

    //Cardinal direction labels
    makeTextSprite('N', new THREE.Vector3(0,0,-1.3));
    makeTextSprite('S', new THREE.Vector3(0,0,1.3));
    makeTextSprite('E', new THREE.Vector3(1.3,0,0));
    makeTextSprite('W', new THREE.Vector3(-1.3,0,0));

    //Bounding box helper
    boxHelper = new THREE.BoxHelper(sphereMesh, 0x555555);
    boxHelper.visible = false;
    scene.add(boxHelper);

    //Initial parameter default setup
    setDefaults();
    updateSphere();
    window.addEventListener('resize', onWindowResize);
    onWindowResize();
}

function makeTextSprite(text, position, size=120){
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    ctx.font = `${size}px Arial`;
    const textWidth = ctx.measureText(text).width;
    canvas.width = textWidth;
    canvas.height = size*1.2;
    ctx.font = `${size}px Arial`;
    ctx.fillStyle = '#000';
    ctx.textBaseline='top';
    ctx.fillText(text, 0, 0);
    const texture = new THREE.CanvasTexture(canvas);
    texture.minFilter = THREE.LinearFilter;
    const mat = new THREE.SpriteMaterial({map:texture, transparent:true});
    const sprite = new THREE.Sprite(mat);
    sprite.scale.set(canvas.width/canvas.height*0.3, 0.3, 1);
    sprite.position.copy(position);
    scene.add(sprite);
}

function updateSphere(){
    const strike = Number(document.getElementById('slider-strike').value)*Math.PI/180;
    const dip = Number(document.getElementById('slider-dip').value)*Math.PI/180;
    const rake = Number(document.getElementById('slider-slip-angle').value)*Math.PI/180;
    const slip = Number(document.getElementById('slider-slip-amount').value);

    computeAxes(strike,dip,rake); //updates pAxis, tAxis, uAxis

    //update labels positions
    moveLabel('label-P', pAxis.clone().normalize());
    moveLabel('label-T', tAxis.clone().normalize());
    moveLabel('label-U', uAxis.clone().normalize());

    //update vertices + color
    const posAttr = sphereMesh.geometry.attributes.position;
    const colorAttr = sphereMesh.geometry.attributes.color;
    for(let i=0;i<posAttr.count;i++){
        const ix=i*3;
        const orig = new THREE.Vector3(
            originalPositions[ix],
            originalPositions[ix+1],
            originalPositions[ix+2]
        );
        //deformation
        const factor = deformationScale*slip*((orig.dot(tAxis))**2 - (orig.dot(pAxis))**2);
        const deformed = orig.clone().addScaledVector(orig, factor);

        posAttr.array[ix]   = deformed.x;
        posAttr.array[ix+1] = deformed.y;
        posAttr.array[ix+2] = deformed.z;

        //coloring
        const compressive = (orig.dot(pAxis))**2 > (orig.dot(tAxis))**2;
        const col = compressive ? [1,1,1] : [1,0.647,0]; //white or orange
        colorAttr.array[ix]   = col[0];
        colorAttr.array[ix+1] = col[1];
        colorAttr.array[ix+2] = col[2];
    }
    posAttr.needsUpdate = true;
    colorAttr.needsUpdate = true;
    sphereMesh.geometry.computeVertexNormals(); //keep normals decent

    //Update bounding box helper
    boxHelper.update();
}

function computeAxes(phi,delta,lam){
    //seismological coordinates (x=N, y=E, z=Down)
    const n = new THREE.Vector3(
        -Math.sin(delta)*Math.sin(phi),
         Math.sin(delta)*Math.cos(phi),
        -Math.cos(delta)
    );
    const u = new THREE.Vector3(
        Math.cos(lam)*Math.cos(phi) + Math.sin(lam)*Math.cos(delta)*Math.sin(phi),
        Math.cos(lam)*Math.sin(phi) - Math.sin(lam)*Math.cos(delta)*Math.cos(phi),
        Math.sin(lam)*Math.sin(delta)
    );

    //P and T in seismo coords
    const P_seis = n.clone().add(u).multiplyScalar(1/Math.sqrt(2));
    const T_seis = u.clone().sub(n).multiplyScalar(1/Math.sqrt(2));

    //convert to three.js coords (x=East, y=Up, z=South)
    pAxis.set(P_seis.y, -P_seis.z, -P_seis.x);
    tAxis.set(T_seis.y, -T_seis.z, -T_seis.x);
    uAxis.set(u.y, -u.z, -u.x);
}

function moveLabel(id, vec3){
    const label = document.getElementById(id);
    const width = renderer.domElement.clientWidth;
    const height = renderer.domElement.clientHeight;
    const projection = vec3.project(camera);
    const x = (projection.x *  0.5 + 0.5) * width;
    const y = (-projection.y * 0.5 + 0.5) * height;
    label.style.left = `${x}px`;
    label.style.top  = `${y}px`;
}

function onWindowResize(){
    const container = document.getElementById('canvas-container');
    const w = container.clientWidth;
    const h = container.clientHeight;
    camera.aspect = w/h;
    camera.updateProjectionMatrix();
    renderer.setSize(w,h,false);
}

function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene,camera);

    //update label positions each frame (after camera move)
    moveLabel('label-P', pAxis.clone().normalize());
    moveLabel('label-T', tAxis.clone().normalize());
    moveLabel('label-U', uAxis.clone().normalize());
}
</script>
</body>
</html>
