<title>Grover’s Algorithm Visual Demo</title>
<!-- p5.js CDN -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
<style>
    body{
        font-family: Arial, Helvetica, sans-serif;
        background:#fafafa;
    }
    #app-container{
        width: 900px;
        margin:20px auto;
