    #probability-display{
        margin-top:20px;
        font-size:1.2em;
        font-weight:bold;
        text-align:center;
    }
</style>
</head>
<body>
<div id="app-container">
    <!-- Control Panel -->
    <div id="control-panel">
        <!-- Database Size Control -->
        <div class="control-group">
            <label for="slider-db-size">database size</label>
            <input type="range" id="slider-db-size" min="8" max="100" step="1" value="10">
            <div class="value-display"><span id="display-db-size">10</span></div>
        </div>
        <!-- Iterations Control -->
        <div class="control-group">
            <label for="slider-iterations">number of iterations</label>
            <input type="range" id="slider-iterations" min="0" max="20" step="1" value="2">
            <div class="value-display"><span id="display-iterations">2</span></div>
        </div>
    </div>

    <!-- Canvas -->
    <div id="canvas-container"></div>

    <!-- Probability -->
    <div id="probability-display">99.9% success probability</div>
</div>

<script>
/* ---------- DOM Elements ---------- */
const sliderN = document.getElementById('slider-db-size');
const sliderK = document.getElementById('slider-iterations');
const displayN = document.getElementById('display-db-size');
const displayK = document.getElementById('display-iterations');
const probDisplay = document.getElementById('probability-display');

/* ---------- Event Listeners ---------- */
sliderN.addEventListener('input', () => {
    displayN.textContent = sliderN.value;
    updateProbability();
    redraw();
});

sliderK.addEventListener('input', () => {
    displayK.textContent = sliderK.value;
    updateProbability();
    redraw();
});

/* ---------- Probability Calculation ---------- */
function updateProbability(){
    const N = Number(sliderN.value);
    const k = Number(sliderK.value);
    const theta = Math.asin(1/Math.sqrt(N));
    const P = Math.pow(Math.sin((2*k+1)*theta),2);
    probDisplay.textContent = (P*100).toFixed(1) + '% success probability';
}

/* ---------- p5.js Sketch ---------- */
let canvasW = 840;
let canvasH = 600;
const targetIndex = 6;

function setup(){
    let cnv = createCanvas(canvasW, canvasH);
    cnv.parent('canvas-container');
    textFont('Arial');
    noLoop();          // Redraw only on demand
    redraw();          // Initial draw
}

function draw(){
    background(255);
    const N = Number(sliderN.value);
    const k = Number(sliderK.value);

    // ---------- Compute amplitude stages ----------
    const stage1 = initState(N);                             // original
    const stage2 = inversion(stage1.slice(), N);             // after first inversion
    const stage3 = diffusion(stage2.slice());                // after first diffusion
    const stage4 = iterateGrover(stage1.slice(), N, k);      // after k iterations

    const allStages = [
        {title:'original amplitudes', data:stage1},
        {title:'after first inversion', data:stage2},
        {title:'after first diffusion', data:stage3},
        {title:`after ${k} iteration${k===1?'':'s'}`, data:stage4}
    ];

    drawStages(allStages, N);
}

/* ---------- Grover Utilities ---------- */
function initState(N){
    const amp = 1/Math.sqrt(N);
    return Array.from({length:N}, ()=>amp);
}
function inversion(arr, N){
    if(targetIndex < N){
        arr[targetIndex] *= -1;
    }
    return arr;
}
function diffusion(arr){
    const mean = arr.reduce((a,b)=>a+b,0)/arr.length;
    return arr.map(a => 2*mean - a);
}
function iterateGrover(arr, N, k){
    for(let i=0;i<k;i++){
        arr = inversion(arr, N);
        arr = diffusion(arr);
    }
    return arr;
}

/* ---------- Drawing ---------- */
function drawStages(stages, N){
    const sectionH = height / stages.length;
    const marginX = 40;
    const usableW = width - 2*marginX;
    const barSpacing = usableW / (N-1);
    const barW = Math.min(18, barSpacing*0.6);
    const yScale = (sectionH/2)*0.8;  // 80% of half section

    strokeWeight(1);
    textAlign(CENTER, CENTER);
    for(let s=0; s<stages.length; s++){
        const baseY = sectionH*s + sectionH/2;
        // baseline
        stroke(200);
        line(marginX-10, baseY, width - marginX +10, baseY);
        noStroke();

        // title
        fill(0);
        textSize(14);
        text(stages[s].title, width/2, sectionH*s + 15);

        // bars
        const amps = stages[s].data;
        for(let i=0;i<N;i++){
            const amp = amps[i];
            const x = marginX + i*barSpacing - barW/2;
            const h = amp * yScale;
            if(i===targetIndex){
                fill('#FFA500'); // orange for target
            }else{
                fill('#808080'); // grey
            }
            rect(x, baseY, barW, -h);
        }

        // parentheses for first stage
        if(s===0){
            stroke(0);
            noFill();
            const arcH = sectionH*0.8;
            const leftX = marginX - barSpacing/2;
            const rightX = marginX + (N-1)*barSpacing + barSpacing/2;
            strokeWeight(2);
            // left parenthesis
            arc(leftX, baseY, 40, arcH, HALF_PI, 3*HALF_PI);
            // right parenthesis
            arc(rightX, baseY, 40, arcH, -HALF_PI, HALF_PI);
            noStroke();
        }
    }
}
</script>
</body>
</html>
