<body>

<!-- Main container -->
<div id="main-container">
    <!-- Left column: Control Panel -->
    <div id="control-panel">
        <div class="control-group">
            <label for="slider-volatility">relative volatility</label>
            <input type="range" id="slider-volatility" min="1.1" max="10" step="0.1" value="2.5">
            <button id="btn-volatility-plus">+</button>
        </div>
        <div class="control-group">
            <label for="slider-distillate">distillate specification</label>
            <input type="range" id="slider-distillate" min="0.51" max="0.99" step="0.01" value="0.95">
            <button id="btn-distillate-plus">+</button>
        </div>
        <div class="control-group">
            <label for="slider-bottom">bottom specification</label>
            <input type="range" id="slider-bottom" min="0.01" max="0.49" step="0.01" value="0.05">
            <button id="btn-bottom-plus">+</button>
        </div>
        <div class="control-group">
            <label for="slider-feed-comp">feed composition</label>
            <!-- min & max will be set dynamically -->
            <input type="range" id="slider-feed-comp" step="0.01" value="0.5">
            <button id="btn-feed-comp-plus">+</button>
        </div>
        <div class="control-group">
            <label for="slider-feed-quality">feed quality</label>
            <input type="range" id="slider-feed-quality" min="0" max="2" step="0.05" value="1.0">
            <button id="btn-feed-quality-plus">+</button>
        </div>
    </div>

    <!-- Right column: Visualization Area -->
    <div id="visualization-area">
        <p id="stages-count-display">The number of stages is 8.</p>
        <div id="canvas-container"></div> <!-- p5.js canvas will be attached here -->
    </div>
</div>

<script>
/* --------- GLOBAL VARIABLES & DOM ELEMENTS --------- */
let sliders = {};
let plusButtons = {};
const feedStep = 0.01; // constant step for feed comp slider

/* --------- INITIALIZATION --------- */
document.addEventListener("DOMContentLoaded", () => {
    // Grab slider DOM references
    sliders = {
        volatility : document.getElementById('slider-volatility'),
        distillate : document.getElementById('slider-distillate'),
        bottom     : document.getElementById('slider-bottom'),
        feedComp   : document.getElementById('slider-feed-comp'),
        feedQual   : document.getElementById('slider-feed-quality')
    };

    // Plus buttons
    plusButtons = {
        volatility : document.getElementById('btn-volatility-plus'),
        distillate : document.getElementById('btn-distillate-plus'),
        bottom     : document.getElementById('btn-bottom-plus'),
        feedComp   : document.getElementById('btn-feed-comp-plus'),
        feedQual   : document.getElementById('btn-feed-quality-plus')
    };

    // Attach events
    Object.values(sliders).forEach(slider => {
        slider.addEventListener('input', onSliderChange);
    });

    Object.keys(plusButtons).forEach(key=>{
        plusButtons[key].addEventListener('click', ()=>incrementSlider(sliders[key]));
    });

    // Set initial min/max of feed composition slider
    updateFeedSliderLimits();

    // Initialize p5 sketch
    new p5(p => sketch(p), document.getElementById('canvas-container'));
});

/* --------- EVENT HANDLERS --------- */
function onSliderChange(e){
    enforceConstraints(e.target.id);
    drawDiagram(); // redraw
}

function incrementSlider(slider){
    const step = parseFloat(slider.step);
    let newVal = parseFloat(slider.value) + step;
    if (newVal > parseFloat(slider.max)) newVal = parseFloat(slider.max);
    slider.value = newVal.toFixed( slider.step.indexOf('.')>-1 ? slider.step.split('.')[1].length : 0 ); // preserve decimals
    slider.dispatchEvent(new Event('input', {bubbles:true}));
}

/* --------- CONSTRAINTS MANAGEMENT --------- */
function enforceConstraints(changedId){
    const xB = parseFloat(sliders.bottom.value);
    const xD = parseFloat(sliders.distillate.value);
    let xF   = parseFloat(sliders.feedComp.value);

    // Make sure bottom < feed < distillate
    const feedMin = xB + parseFloat(sliders.feedComp.step);
    const feedMax = xD - parseFloat(sliders.feedComp.step);

    if (xF <= xB){
        xF = feedMin;
    }else if (xF >= xD){
        xF = feedMax;
    }
    sliders.feedComp.value = xF.toFixed(2);

    updateFeedSliderLimits();
}

function updateFeedSliderLimits(){
    const xB = parseFloat(sliders.bottom.value);
    const xD = parseFloat(sliders.distillate.value);
    sliders.feedComp.min = (xB + feedStep).toFixed(2);
    sliders.feedComp.max = (xD - feedStep).toFixed(2);
}

/* --------- P5 SKETCH --------- */
let sketch = function(p){
    let canvasWidth, canvasHeight, margin;
    p.setup = function(){
        canvasWidth = Math.min(600, document.getElementById('canvas-container').clientWidth - 20);
        canvasHeight = canvasWidth; // square canvas
        margin = 60;
        const c = p.createCanvas(canvasWidth, canvasHeight);
        c.parent('canvas-container');
        p.noLoop();
        drawDiagram();
    };
};

/* --------- DRAWING LOGIC --------- */
function drawDiagram(){
    const p = window.__p5instance; // latest p5 instance (simplification)

    if(!p) return;
    p.clear();
    p.background(255);

    /* 1. Read Inputs */
    const alpha = parseFloat(sliders.volatility.value);
    const xD    = parseFloat(sliders.distillate.value);
    const xB    = parseFloat(sliders.bottom.value);
    const xF    = parseFloat(sliders.feedComp.value);
    const q     = parseFloat(sliders.feedQual.value);

    const width = p.width;
    const height = p.height;
    const margin = 60;

    /* Coordinate mapping functions */
    const plotX = x => p.map(x,0,1,margin,width-margin);
    const plotY = y => p.map(y,0,1,height-margin,margin); // invert y

    /* Draw axes */
    p.push();
    p.stroke('#808080');
    p.strokeWeight(1);
    p.line(plotX(0), plotY(0), plotX(1), plotY(0)); // X-axis
    p.line(plotX(0), plotY(0), plotX(0), plotY(1)); // Y-axis
    // ticks
    p.textSize(12);
    p.fill('#808080');
    p.noStroke();
    for(let t=0;t<=10;t++){
        const val = t/10;
        // X
        p.stroke('#808080');
        p.line(plotX(val), plotY(0)-5, plotX(val), plotY(0)+5);
        p.noStroke();
        p.text(val.toFixed(1), plotX(val)-10, plotY(0)+20);
        // Y
        p.stroke('#808080');
        p.line(plotX(0)-5, plotY(val), plotX(0)+5, plotY(val));
        p.noStroke();
        if(val>0){
            p.text(val.toFixed(1), plotX(0)-35, plotY(val)+4);
        }
    }
    p.pop();

    /* 3. Equilibrium Curve */
    p.stroke('#FF0000');
    p.noFill();
    p.beginShape();
    for(let i=0;i<=200;i++){
        const x = i/200;
        const y = (alpha*x)/(1 + (alpha-1)*x);
        p.vertex(plotX(x), plotY(y));
    }
    p.endShape();

    /* 4. Diagonal */
    p.stroke('#228B22');
    p.line(plotX(0), plotY(0), plotX(1), plotY(1));

    /* 5. Feed line (q-line) & Intersection point */
    let x_int, y_int;
    let feedLineFunc; // function y(x)

    if(Math.abs(q-1) < 1e-6){ // vertical line
        x_int = xF;
        y_int = (alpha*x_int)/(1 + (alpha-1)*x_int);
        p.stroke('#0000CD');
        p.line(plotX(xF), plotY(0), plotX(xF), plotY(y_int));
        p.line(plotX(xF), plotY(y_int), plotX(xF), plotY(1)); // extend both sides for clarity
        feedLineFunc = ()=>null; // not used
    }else if(Math.abs(q) < 1e-6){ // horizontal
        const yFeed = xF;
        const root = function(){
            let lo = 0, hi = 1, mid;
            for(let i=0;i<50;i++){
                mid = 0.5*(lo+hi);
                const fmid = (alpha*mid)/(1 + (alpha-1)*mid) - yFeed;
                if(fmid>0) hi=mid; else lo=mid;
            }
            return (lo+hi)/2;
        };
        x_int = root();
        y_int = yFeed;
        p.stroke('#0000CD');
        p.line(plotX(0), plotY(yFeed), plotX(x_int), plotY(yFeed));
        feedLineFunc = ()=>yFeed;
    }else{ // regular line
        const m = q/(q-1);
        feedLineFunc = x => m*(x - xF) + xF;
        // find intersection with equilibrium curve
        const root = function(){
            let lo = 0, hi = 1, mid;
            for(let i=0;i<50;i++){
                mid = 0.5*(lo+hi);
                const fmid = (alpha*mid)/(1 + (alpha-1)*mid) - feedLineFunc(mid);
                const flo  = (alpha*lo)/(1 + (alpha-1)*lo) - feedLineFunc(lo);
                if(fmid==0) break;
                if(fmid*flo<0) hi=mid; else lo=mid;
            }
            return (lo+hi)/2;
        };
        x_int = root();
        y_int = feedLineFunc(x_int);

        // draw feed line
        p.stroke('#0000CD');
        let xStart = xF, yStart = xF; // point on diagonal
        p.line(plotX(xStart), plotY(yStart), plotX(x_int), plotY(y_int));
    }

    /* 6. Operating Lines */
    // Rectifying
    p.stroke('#00FFFF');
    p.line(plotX(xD), plotY(xD), plotX(x_int), plotY(y_int));
    // Stripping
    p.stroke('#FF00FF');
    p.line(plotX(xB), plotY(xB), plotX(x_int), plotY(y_int));

    /* 7. Stage Calculation & Drawing */
    let stage_count = 0;
    let xCurr = xD;
    let yCurr = xD;

    p.stroke('#000000');
    p.strokeWeight(1);

    const eq_y = x => (alpha*x)/(1 + (alpha-1)*x);
    const invEq_x = y => { // numeric inversion
        let lo=0, hi=1, mid;
        for(let i=0;i<35;i++){
            mid = 0.5*(lo+hi);
            const fmid = eq_y(mid) - y;
            const flo  = eq_y(lo) - y;
            if(fmid==0) break;
            if(fmid*flo<0) hi=mid; else lo=mid;
        }
        return (lo+hi)/2;
    };

    const rectLine_y = x => {
        const mR = (y_int - xD)/(x_int - xD);
        return mR*(x - xD) + xD;
    };
    const stripLine_y = x => {
        const mS = (y_int - xB)/(x_int - xB);
        return mS*(x - xB) + xB;
    };

    while(true){
        // Horizontal to equilibrium
        const xNew = invEq_x(yCurr);
        p.line(plotX(xCurr), plotY(yCurr), plotX(xNew), plotY(yCurr)); // horizontal
        stage_count++;
        xCurr = xNew;

        if(xCurr <= xB){
            break; // final stage reached
        }

        // Determine which operating line
        let yNew;
        if(xCurr > x_int){
            yNew = rectLine_y(xCurr);
        }else{
            yNew = stripLine_y(xCurr);
        }
        // Vertical to operating line
        p.line(plotX(xCurr), plotY(yCurr), plotX(xCurr), plotY(yNew)); // vertical
        yCurr = yNew;
    }

    /* 8. Update Stage Count Display */
    document.getElementById('stages-count-display').textContent =
        `The number of stages is ${stage_count}.`;
}

/* Utility to access latest p5 instance */
Object.defineProperty(window, '__p5instance', {
    get: function(){
        return p5.instance;
    }
});
</script>
</body>
</html>
