        </div>
    </div>

    <!-- Section for the visualization canvas -->
    <div id="visualization-area">
        <canvas id="main-canvas"></canvas>
    </div>

</div>

<!-- CDN Includes -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

<script>
/* -------------------- GLOBAL STATE -------------------- */
let currentMonth = new Date().getMonth();       // 0-11
let mode         = '2D';                        // 2D or 3D

/* HTML references */
const sliderMonth = document.getElementById('slider-month');
const btn2D       = document.getElementById('btn-2d');
const btn3D       = document.getElementById('btn-3d');
const visArea     = document.getElementById('visualization-area');

/* canvases / engines */
let p5Instance        = null;
let threeRenderer     = null;
let threeScene        = null;
let threeCamera       = null;
let threeAnimId       = null;

/* ------------------------------------------------------------------
   INITIALISATION
------------------------------------------------------------------ */
sliderMonth.value = currentMonth;

/* ========== 2D INITIALISATION (p5.js) ========== */
function init2D(){
    destroy3D();                 // make sure 3D resources are gone
    visArea.innerHTML = '';      // remove old canvas completely

    let sketch = (s)=>{
        s.setup = ()=>{
            let c = s.createCanvas(visArea.clientWidth, visArea.clientHeight);
            c.id('main-canvas');
            c.parent('visualization-area');
            s.angleMode(s.DEGREES);
            s.noLoop();          // we only redraw on demand
        };

        /* resize handling */
        s.windowResized = ()=>{
            s.resizeCanvas(visArea.clientWidth, visArea.clientHeight);
            s.redraw();
        };

        /* main draw ====================================== */
        s.draw = ()=>{
            /* ---- background ---- */
            s.background(0);

            const cx = s.width/2;
            const cy = s.height/2;

            /* ------ SOLAR WIND (static – does NOT rotate) ------ */
            s.push();
            s.stroke('#990000');
            s.strokeWeight(1);
            for(let y=0; y<=s.height; y+=24){
                s.line(0,y, cx, y);
                /* arrow heads */
                s.line(16,y,11,y-5);
                s.line(16,y,11,y+5);
            }
            s.pop();

            /* prepare rotation for everything that tilts with month */
            s.push();
            s.translate(cx,cy);
            let tilt = 23.5 * Math.cos(2*Math.PI*currentMonth/12);
            s.rotate(-tilt);

            /* ---- MAGNETOTAIL REGION ---- */
            s.noStroke();
            s.fill(0,0,139,120);       // rgba with alpha for translucency
            s.rect(50,-120, s.width, 240);

            /* ---- EARTH ---- */
            s.noStroke();
            s.fill('#4169E1');
            s.ellipse(0,0,80,80);

            /* simple landmass */
            s.fill('#228B22');
            s.ellipse(-10,-5,26,14);
            s.ellipse(15,12,30,15);

            /* ---- POLAR CUSPS ---- */
            s.fill('#F0E68C');
            s.triangle(0,-40, -12,-70, 12,-70);   // north
            s.triangle(0, 40, -12, 70, 12, 70);   // south

            /* ---- VAN ALLEN BELTS ---- */
            s.noFill();
            s.strokeWeight(8);
            s.stroke('#006400');
            s.arc(0,0,120,120,-60,60);
            s.stroke('#32CD32');
            s.arc(0,0,140,140,-50,50);

            /* ---- MAGNETIC FIELD LINES ---- */
            s.stroke('#808080');
            s.strokeWeight(1);
            s.noFill();
            for(let k=0;k<6;k++){
                let offset = 50 + k*10;
                s.beginShape();
                for(let a=-90; a<=90; a+=4){
                    let rad = s.radians(a);
                    let x = offset*Math.cos(rad);
                    let y = offset*Math.sin(rad)*Math.sin(rad);
                    s.vertex(x,y);
                }
                for(let a=90; a>=-90; a-=4){
                    let rad = s.radians(a);
                    let x = -offset*Math.cos(rad);
                    let y = offset*Math.sin(rad)*Math.sin(rad);
                    s.vertex(x,y);
                }
                s.endShape();
            }

            /* ---- MAGNETOPAUSE & BOW SHOCK ---- */
            s.noFill();
            s.strokeWeight(2);

            // Magnetopause (cyan)
            s.stroke('#00FFFF');
            s.beginShape();
            for(let a=-90;a<=90;a+=4){
                let rad = s.radians(a);
                let r = 220 - 120*Math.cos(rad);
                s.vertex(-r*Math.cos(rad), r*Math.sin(rad));
            }
            s.endShape();

            // Bow shock (orange)
            s.stroke('#FFA500');
            s.beginShape();
            for(let a=-90;a<=90;a+=4){
                let rad = s.radians(a);
                let r = 240 - 120*Math.cos(rad);
                s.vertex(-r*Math.cos(rad), r*Math.sin(rad));
            }
            s.endShape();

            s.pop();  // undo rotation
        };
    };

    p5Instance = new p5(sketch);
    p5Instance.redraw();
}

/* ========== 3D INITIALISATION (three.js) ========== */
function init3D(){
    destroy2D();                // make sure 2D instance gone
    visArea.innerHTML='';       // clean

    /* create canvas for three.js */
    const canvas = document.createElement('canvas');
    canvas.id = 'main-canvas';
    canvas.style.width  = '100%';
    canvas.style.height = '100%';
    visArea.appendChild(canvas);

    /* ---- Renderer ---- */
    threeRenderer = new THREE.WebGLRenderer({canvas:canvas, antialias:true});
    threeRenderer.setPixelRatio(window.devicePixelRatio);
    threeRenderer.setSize(visArea.clientWidth, visArea.clientHeight);

    /* ---- Scene, Camera, Controls ---- */
    threeScene  = new THREE.Scene();
    threeScene.background = new THREE.Color(0x000000);

    threeCamera = new THREE.PerspectiveCamera(
        45,
        visArea.clientWidth/visArea.clientHeight,
        0.1,
        2000
    );
    threeCamera.position.set(0,60,160);

    const controls = new THREE.OrbitControls(threeCamera, canvas);
    controls.enableDamping = true;

    /* ---- Lights ---- */
    const ambient = new THREE.AmbientLight(0x888888);
    threeScene.add(ambient);

    const sunLight = new THREE.PointLight(0xffffff,1.2,0);
    threeScene.add(sunLight);

    /* ---- SUN ---- */
    const sunGeo = new THREE.SphereGeometry(10,32,32);
    const sunMat = new THREE.MeshBasicMaterial({color:0xffff00});
    const sunMesh = new THREE.Mesh(sunGeo,sunMat);
    threeScene.add(sunMesh);

    /* ---- ORBIT ---- */
    const a = 60;                   // semi-major axis
    const b = 50;                   // semi-minor axis
    const c = Math.sqrt(a*a - b*b); // focal distance

    const curve = new THREE.EllipseCurve(0,0,a,b,0,2*Math.PI,false,0);
    const points = curve.getPoints(200);
    const orbitGeo = new THREE.BufferGeometry().setFromPoints(
        points.map(p=>new THREE.Vector3(p.x,0,p.y))
    );
    const orbitLine = new THREE.Line(
        orbitGeo,
        new THREE.LineBasicMaterial({color:0xffffff})
    );
    threeScene.add(orbitLine);

    /* ---- EARTH ---- */
    const earthGeo = new THREE.SphereGeometry(5,32,32);
    const earthMat = new THREE.MeshStandardMaterial({color:0x4169e1});
    const earthMesh = new THREE.Mesh(earthGeo,earthMat);
    threeScene.add(earthMesh);

    /* ---- EARTH AXIS ---- */
    const axisLen   = 12;
    const axisTilt  = THREE.MathUtils.degToRad(23.5);
    const axisHelper = new THREE.ArrowHelper(
        new THREE.Vector3(0,1,0),
        new THREE.Vector3(0,0,0),
        axisLen,
        0x00ff00,
        3,
        1
    );
    earthMesh.add(axisHelper);

    /* ---- MAGNETOSPHERE ---- */
    const profile = [];
    for(let i=0;i<=18;i++){
        const t = i/18;
        const x = Math.sin(t*Math.PI)*3 + 6;  // width
        const y = (t-0.5)*20;                 // height
        profile.push(new THREE.Vector2(x,y));
    }
    const magGeo = new THREE.LatheGeometry(profile,40);
    const magMat = new THREE.MeshStandardMaterial({
        color:0x0000ff,
        transparent:true,
        opacity:0.35,
        side:THREE.DoubleSide
    });
    const magMesh = new THREE.Mesh(magGeo,magMat);
    earthMesh.add(magMesh);

    /* ---- SOLAR WIND ARROWS ---- */
    for(let i=-4;i<=4;i++){
        const arrow = new THREE.ArrowHelper(
            new THREE.Vector3(1,0,0),
            new THREE.Vector3(-25, i*5,0),
            12,
            0xff0000,
            3,
            1.5
        );
        threeScene.add(arrow);
    }

    /* ---- UPDATE EARTH POSITION & ORIENTATION ---- */
    function updateEarth(){
        const t = currentMonth/12 * 2*Math.PI;
        const ex =  a*Math.cos(t) - c;
        const ez =  b*Math.sin(t);
        earthMesh.position.set(ex,0,ez);

        /* Axis orientation: keep constant tilt but always same direction in space */
        const axisDir = new THREE.Vector3(
            Math.sin(axisTilt)*Math.cos(t),
            Math.cos(axisTilt),
            Math.sin(axisTilt)*Math.sin(t)
        ).normalize();
        axisHelper.setDirection(axisDir);

        /* Magnetosphere tail points away from sun */
        const fromSun = earthMesh.position.clone().normalize();
        const phi = Math.atan2(fromSun.z,fromSun.x);
        magMesh.rotation.set(0, -phi + Math.PI/2, 0);
    }

    updateEarth();

    /* ---- RENDER / ANIMATE ---- */
    function animate(){
        threeAnimId = requestAnimationFrame(animate);
        updateEarth();
        controls.update();
        threeRenderer.render(threeScene,threeCamera);
    }
    animate();

    /* ---- RESIZE HANDLING ---- */
    window.addEventListener('resize', ()=>{
        if(!threeRenderer) return;
        threeRenderer.setSize(visArea.clientWidth,visArea.clientHeight);
        threeCamera.aspect = visArea.clientWidth/visArea.clientHeight;
        threeCamera.updateProjectionMatrix();
    });

    /* store references for cleanup */
    threeRenderer._controls   = controls;
    threeRenderer._cancelAnim = ()=>cancelAnimationFrame(threeAnimId);
}

/* ---------- DESTRUCTION HELPERS ---------- */
function destroy2D(){
    if(p5Instance){
        p5Instance.remove();
        p5Instance = null;
    }
}

function destroy3D(){
    if(threeRenderer){
        threeRenderer._cancelAnim && threeRenderer._cancelAnim();
        threeRenderer._controls  && threeRenderer._controls.dispose();
        threeRenderer.dispose();
        threeRenderer.forceContextLoss && threeRenderer.forceContextLoss();
        threeRenderer.domElement && threeRenderer.domElement.remove();
        threeRenderer = null;
        threeScene    = null;
        threeCamera   = null;
    }
}

/* ------------------------------------------------------------------
   UI EVENT HANDLERS
------------------------------------------------------------------ */
btn2D.addEventListener('click',()=>{
    if(mode==='2D') return;
    mode='2D';
    btn2D.classList.add('active');
    btn3D.classList.remove('active');
    init2D();
});

btn3D.addEventListener('click',()=>{
