    btn3D.classList.add('active');
    btn2D.classList.remove('active');
    init3D();
});

sliderMonth.addEventListener('input', (e)=>{
    currentMonth = Number(e.target.value);

    if(mode==='2D' && p5Instance){
        p5Instance.redraw();
    }
    /* 3D is continuously updated inside its animation loop */
});

/* ------------------------------------------------------------------
   START APPLICATION
------------------------------------------------------------------ */
init2D();

</script>
