<script>
/* --------------------- Math Functions --------------------- */
// Lanczos approximation of the Gamma function
function gamma(z){
    const p = [
        676.5203681218851,
        -1259.1392167224028,
        771.32342877765313,
        -176.61502916214059,
        12.507343278686905,
        -0.13857109526572012,
        9.9843695780195716e-6,
        1.5056327351493116e-7
    ];
    if(z < 0.5){
        // Reflection formula
        return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
    }
    z -= 1;
    let x = 0.99999999999980993;
    for(let i = 0; i < p.length; i++){
        x += p[i] / (z + i + 1);
    }
    const t = z + p.length - 0.5;
    return Math.sqrt(2 * Math.PI) * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
}

// Gamma PDF f(x; alpha, beta)
function gammaPDF(x, alpha, beta){
    if(x < 0) return 0;
    return Math.pow(x, alpha - 1) * Math.exp(-x / beta) / (Math.pow(beta, alpha) * gamma(alpha));
}

/* --------------------- Plot + Stats Update --------------------- */
const sliderAlpha = document.getElementById('slider-alpha');
const sliderBeta  = document.getElementById('slider-beta');
const displayAlpha = document.getElementById('display-alpha');
const displayBeta  = document.getElementById('display-beta');
const meanValueSpan = document.getElementById('mean-value');
const varianceValueSpan = document.getElementById('variance-value');
const mgfFormula = document.getElementById('mgf-formula');

const dx = 0.1;
const xMax = 25;
let xValues = [];
for(let x = 0; x <= xMax + 1e-9; x += dx){
    xValues.push(parseFloat(x.toFixed(4)));
}

/* Initial render */
updateAll();

/* Event listeners */
sliderAlpha.addEventListener('input', updateAll);
sliderBeta.addEventListener('input', updateAll);

function updateAll(){
    const alpha = parseFloat(sliderAlpha.value);
    const beta  = parseFloat(sliderBeta.value);

    /* 1. Update Parameter Displays */
    displayAlpha.textContent = alpha.toFixed(3);
    displayBeta.textContent  = beta.toFixed(3);

    /* 2. Stats Calculations */
    const mean = alpha * beta;
    const variance = alpha * beta * beta;

    meanValueSpan.textContent = mean.toFixed(3);
    varianceValueSpan.textContent = variance.toFixed(3);

    /* MGF Formula Update */
    mgfFormula.innerHTML = `$$ (1 - ${beta.toFixed(3)}\\, t)^{-${alpha.toFixed(3)}} = \\frac{1}{(1 - ${beta.toFixed(3)}\\, t)^{${alpha.toFixed(3)}}} $$`;
    if(window.MathJax && window.MathJax.typeset){
        MathJax.typeset([mgfFormula]);
    }

    /* 3. PDF & CDF Arrays */
    const pdfValues = [];
    const cdfValues = [];
    let cumulative = 0;
    for(let i = 0; i < xValues.length; i++){
        const x = xValues[i];
        const pdf = gammaPDF(x, alpha, beta);
        pdfValues.push(pdf);

        if(i === 0){
            cdfValues.push(0);   // CDF(0) ~ 0
        }else{
            // Trapezoidal rule
            cumulative += 0.5 * (pdfValues[i] + pdfValues[i-1]) * dx;
            cdfValues.push(cumulative);
        }
    }

    // Ensure last CDF value is exactly 1 (numerical drift fix)
    const lastIdx = cdfValues.length - 1;
    cdfValues[lastIdx] = 1;

    /* 4. Mean line y-value */
    const pdfAtMean = gammaPDF(mean, alpha, beta);

    /* 5. Plot Update via Plotly.react */
    const data = [
        {
            type: 'scatter',
            mode: 'lines',
            name: 'PDF',
            x: xValues,
            y: pdfValues,
            line: {color: 'blue', width: 2.5}
        },
        {
            type: 'scatter',
            mode: 'lines',
            name: 'CDF',
            x: xValues,
            y: cdfValues,
            line: {color: 'orange', width: 2.5}
        }
    ];

    const layout = {
        xaxis: {range: [0, xMax]},
        yaxis: {range: [0, 1.1]},   // As specified
        showlegend: false,
        margin: {l: 50, r: 20, t: 20, b: 50},
        shapes: [
            {
                type: 'line',
                x0: mean,
                x1: mean,
                y0: 0,
                y1: Math.min(pdfAtMean, 1.1),   // Keep inside range
                line: {color: 'red', width: 3}
            }
        ]
    };

    Plotly.react('plot-container', data, layout, {responsive: true});
}
</script>
