<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Ternary Distillation Visualization</title>
  <style>
    html, body{
      margin:0;
      padding:0;
      height:100%;
      font-family:Arial, Helvetica, sans-serif;
    }
    #canvas-container{
      display:flex;
      justify-content:center;
      align-items:center;
      height:100%;
      background:#f3f3f3;
    }
    canvas{
      border:1px solid #666;
      /* make sure the default right-click menu does not appear over canvas while dragging */
      -webkit-touch-callout:none;
      -webkit-user-select:none;
      -khtml-user-select:none;
      -moz-user-select:none;
      -ms-user-select:none;
      user-select:none;
    }
  </style>
</head>
<body>
  <div id="canvas-container"></div>

  <!-- p5.js CDN -->
  <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>

  <!-- Application code -->
  <script>
    /*
      ------------------------------------------------------------------
      Distillation Residue Curve – interactive ternary plot
      ------------------------------------------------------------------
    */

    /* ---------- CONSTANTS ---------- */
    const CANVAS_SIZE = 650;
    const MARGIN      = 60;
    const PLOT_SIZE   = CANVAS_SIZE - 2 * MARGIN;

    /* ---------- FIXED DATA POINTS ---------- */
    const A1 = { x: 0.655, y: 0.000 }; // chloroform-rich azeotrope
    const A2 = { x: 0.000, y: 0.795 }; // acetone-rich azeotrope
    const A3 = { x: 0.605, y: 0.345 }; // interior unstable node
    const A4 = { x: 0.260, y: 0.335 }; // saddle azeotrope

    const DISTILLATION_PATH = [ A2, A4, A1 ]; // polyline order

    /* ---------- LOCATOR (interactive) ---------- */
    let locator = { x: 0.2, y: 0.22 }; // initial composition
    let isDragging = false;

    /* ---------- p5.js SKETCH ---------- */
    new p5( p => {

      /* ---------- p5 setup() ---------- */
      p.setup = function () {
        const cnv = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
        cnv.id('visualization-canvas');
        cnv.parent('canvas-container');
        p.textFont('Arial');
        p.textSize(12);
      };

      /* ---------- p5 draw() ---------- */
      p.draw = function () {
        p.background(255);

        drawAxes();
        drawHypotenuse();
        drawTicksAndNumbers();
        drawComponentLabels();
        drawAzeotropes();
        drawDistillationCurve();
        drawLocator();
      };

      /* ---------- EVENT HANDLERS ---------- */
      p.mousePressed = function () {
        if (isMouseInCanvas()) {
          const mouse = pixelToData(p.mouseX, p.mouseY);
          const locatorPix = dataToPixel(locator);
          const d = p.dist(p.mouseX, p.mouseY, locatorPix.x, locatorPix.y);
          if (d <= 15) { isDragging = true; }
        }
      };

      p.mouseDragged = function () {
        if (isDragging) {
          const mData = pixelToData(p.mouseX, p.mouseY);
          locator = getClosestPointOnBoundary(mData);
        }
      };

      p.mouseReleased = function () { isDragging = false; };

      /* ---------- GEOMETRY HELPERS ---------- */
      function dataToPixel(pt){
        return {
          x: MARGIN + pt.x * PLOT_SIZE,
          y: CANVAS_SIZE - MARGIN - pt.y * PLOT_SIZE
        };
      }

      function pixelToData(px, py){
        return {
          x: (px - MARGIN) / PLOT_SIZE,
          y: (CANVAS_SIZE - MARGIN - py) / PLOT_SIZE
        };
      }

      function isInsideTriangle(pt){
        return (pt.x >= 0) && (pt.y >= 0) && (pt.x + pt.y <= 1);
      }

      /* ---------- VISUAL ELEMENTS ---------- */
      function drawAxes(){
        p.stroke(0);
        p.strokeWeight(2);
        // x-axis
        p.line(MARGIN, CANVAS_SIZE - MARGIN, CANVAS_SIZE - MARGIN, CANVAS_SIZE - MARGIN);
        // y-axis
        p.line(MARGIN, CANVAS_SIZE - MARGIN, MARGIN, MARGIN);
      }

      function drawHypotenuse(){
        p.stroke(0);
        p.line(MARGIN, CANVAS_SIZE - MARGIN,
               CANVAS_SIZE - MARGIN, MARGIN);
      }

      function drawTicksAndNumbers(){
        p.stroke(0);
        p.fill(0);
        p.textAlign(p.CENTER, p.TOP);
        for(let i=0;i<=5;i++){
          const t = i * 0.2;
          const xPix = MARGIN + t * PLOT_SIZE;
          const yPix = CANVAS_SIZE - MARGIN - t * PLOT_SIZE;
          // x-axis ticks
          p.line(xPix, CANVAS_SIZE - MARGIN - 5, xPix, CANVAS_SIZE - MARGIN + 5);
          p.text(t.toFixed(1), xPix, CANVAS_SIZE - MARGIN + 8);
          // y-axis ticks
          p.line(MARGIN - 5, CANVAS_SIZE - MARGIN - t * PLOT_SIZE,
                 MARGIN + 5, CANVAS_SIZE - MARGIN - t * PLOT_SIZE);
          p.textAlign(p.RIGHT, p.CENTER);
          p.text(t.toFixed(1), MARGIN - 8, CANVAS_SIZE - MARGIN - t * PLOT_SIZE);
          p.textAlign(p.CENTER, p.TOP);
        }
      }

      function drawComponentLabels(){
        p.noStroke();
        p.fill('#FF0000');
        p.textAlign(p.LEFT, p.TOP);
        p.text('methanol', MARGIN + 4, CANVAS_SIZE - MARGIN + 15);
        p.textAlign(p.RIGHT, p.TOP);
        p.text('chloroform', CANVAS_SIZE - MARGIN, CANVAS_SIZE - MARGIN + 15);
        p.textAlign(p.LEFT, p.BOTTOM);
        p.text('acetone', MARGIN - 4, MARGIN - 10);
      }

      function drawAzeotropes(){
        const azeos = [
          {pt:A1, label:'A\u2081'},
          {pt:A2, label:'A\u2082'},
          {pt:A3, label:'A\u2083'},
          {pt:A4, label:'A\u2084'}
        ];
        p.fill('#990000');
        p.stroke('#660000');
        p.strokeWeight(1);
        azeos.forEach(a=>{
          const pix=dataToPixel(a.pt);
          p.circle(pix.x, pix.y, 12);
          p.noStroke();
          p.fill(0);
          p.textAlign(p.CENTER, p.BOTTOM);
          p.text(a.label, pix.x, pix.y - 8);
          p.stroke('#660000');
          p.fill('#990000');
        });
      }

      function drawLocator(){
        const pix = dataToPixel(locator);
        p.stroke(0);
        p.fill(255);
        p.strokeWeight(2);
        p.circle(pix.x, pix.y, 16);
      }

      /* ---------- DISTILLATION CURVE ---------- */
      function drawDistillationCurve(){
        const curvePts = computeResidueCurve(locator);

        if(curvePts.length<2){ return; }

        p.noFill();
        p.stroke('#0000FF');
        p.strokeWeight(2);
        p.beginShape();
        curvePts.forEach(pt=>{
          const pix = dataToPixel(pt);
          p.curveVertex(pix.x, pix.y);
        });
        p.endShape();
      }

      /* ---------- NUMERICAL INTEGRATION ---------- */
      function computeResidueCurve(start){
        const stepSize = 0.005;
        const steps    = 250;
        let forward = integrate(start, +1, stepSize, steps);
        let backward= integrate(start, -1, stepSize, steps);
        backward.reverse();                             // so order: back→start→forward
        return backward.concat([start]).concat(forward);
      }

      function integrate(start, direction, h, n){
        let pts = [];
        let current = { x:start.x, y:start.y };
        for(let i=0; i<n; i++){
          const v = getVector(current.x, current.y);
          const next = {
            x: current.x + direction * v.dx * h,
            y: current.y + direction * v.dy * h
          };
          if(!isInsideTriangle(next)){ break; }
          pts.push(next);
          current = next;
        }
        return pts;
      }

      /* ---------- VECTOR FIELD (given) ---------- */
      function getVector(x, y) {
        const M  = {x:0, y:0};
        // Vector from repeller to current point
        let v_repel_x = x - A3.x;
        let v_repel_y = y - A3.y;
        // Vector from current point to attractor
        let v_attract_x = M.x - x;
        let v_attract_y = M.y - y;

        let dist_repel_sq   = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
        let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

        let dx = (v_repel_x   / dist_repel_sq)   * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
        let dy = (v_repel_y   / dist_repel_sq)   * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

        dx += -0.1 * y;
        dy +=  0.1 * x;

        return { dx, dy };
      }

      /* ---------- LOCATOR CONSTRAINTS ---------- */
      function getClosestPointOnBoundary(pt){
        // evaluate both segments of the path and pick nearest projection
        const candidates = [];
        for(let i=0;i<DISTILLATION_PATH.length-1;i++){
          const proj = projectPointToSegment(pt, DISTILLATION_PATH[i], DISTILLATION_PATH[i+1]);
          candidates.push(proj);
        }
        // choose with minimal distance squared
        let best = candidates[0];
        for(let c of candidates){
          if(c.distSq < best.distSq){ best = c; }
        }
        return { x: best.x, y: best.y };
      }

      // projection of point P onto segment AB, returns closest point + distSq
      function projectPointToSegment(p, a, b){
        const ax=a.x, ay=a.y, bx=b.x, by=b.y, px=p.x, py=p.y;
        const abx = bx - ax;
        const aby = by - ay;
        const abLenSq = abx*abx + aby*aby + 1e-9;
        const apx = px - ax;
        const apy = py - ay;
        let t = (apx*abx + apy*aby) / abLenSq;
        t = Math.max(0, Math.min(1, t)); // clamp to segment
        const closest = { x: ax + t*abx, y: ay + t*aby };
        const dx = px - closest.x;
        const dy = py - closest.y;
        return { x: closest.x, y: closest.y, distSq: dx*dx + dy*dy };
      }

      /* ---------- MISC ---------- */
      function isMouseInCanvas(){
        return (p.mouseX >= 0 && p.mouseX <= CANVAS_SIZE &&
                p.mouseY >= 0 && p.mouseY <= CANVAS_SIZE);
      }

    }); // END new p5
  </script>
</body>
</html>
