(() => {
    /********************
     *  DOM REFERENCES  *
     ********************/
    const sliders  = {
        a: document.getElementById('slider-a'),
        b: document.getElementById('slider-b'),
        c: document.getElementById('slider-c')
    };
    const numbers  = {
        a: document.getElementById('input-a'),
        b: document.getElementById('input-b'),
        c: document.getElementById('input-c')
    };

    /********************
     *  THREE.JS SETUP  *
     ********************/
    // renderer
    const container = document.getElementById('canvas-container');
    const renderer  = new THREE.WebGLRenderer({antialias:true});
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(container.clientWidth, container.clientHeight);
    renderer.setClearColor(0x000000);
    container.appendChild(renderer.domElement);

    // scene & camera
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(45, container.clientWidth/container.clientHeight, 0.1, 100);
    camera.position.set(5,5,5);

    // controls
    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.08;

    // lighting
    scene.add(new THREE.AmbientLight(0x404040));        // soft light
    const dirLight = new THREE.DirectionalLight(0xffffff, 1);
    dirLight.position.set(3,4,5);
    scene.add(dirLight);

    // coordinate axes (-5 .. 5)
    function addAxes(){
        const material = new THREE.LineBasicMaterial({color:0x888888});
        const points = [];
        const axes = new THREE.Group();
        const len = 5;
        // X axis
        points.push(new THREE.Vector3(-len,0,0), new THREE.Vector3(len,0,0));
        let geom = new THREE.BufferGeometry().setFromPoints(points);
        axes.add(new THREE.Line(geom, material));
        // Y axis
        geom = new THREE.BufferGeometry().setFromPoints([
            new THREE.Vector3(0,-len,0), new THREE.Vector3(0,len,0)
        ]);
        axes.add(new THREE.Line(geom, material));
        // Z axis
        geom = new THREE.BufferGeometry().setFromPoints([
            new THREE.Vector3(0,0,-len), new THREE.Vector3(0,0,len)
        ]);
        axes.add(new THREE.Line(geom, material));
        scene.add(axes);
    }
    addAxes();

    /********************
     *  PARABOLOID      *
     ********************/
    let paraboloidGroup = null;
    const range = 2.0;              // extent in xy-plane
    const slices = 50, stacks = 50; // resolution

    function heightColor(z, zMax){
        // linear interpolation between #FFD700 and #FF4500
        const t = z/zMax;
        const c1 = {r:255,g:215,b:0};   // yellow
        const c2 = {r:255,g:69 ,b:0};   // orange-red
        const r = c1.r + (c2.r - c1.r)*t;
        const g = c1.g + (c2.g - c1.g)*t;
        const b = c1.b + (c2.b - c1.b)*t;
        return [r/255, g/255, b/255];   // normalized
    }

    function buildParaboloid(a,b,c){
        // parametric function
        const func = (u,v, target) => {
            const r = v * range;
            const x = a * r * Math.cos(u * 2*Math.PI);
            const y = b * r * Math.sin(u * 2*Math.PI);
            const z = c * r * r;
            target.set(x,y,z);
        };

        const geometry = new THREE.ParametricGeometry(func, slices, stacks);

        // vertex colors
        const posAttr = geometry.attributes.position;
        const colors = [];
        const zMax = c * range*range;
        for(let i=0;i<posAttr.count;i++){
            const z = posAttr.getZ(i);
            const [r,g,b] = heightColor(z, zMax);
            colors.push(r,g,b);
        }
        geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors,3));

        // solid material
        const matSolid = new THREE.MeshPhongMaterial({
            side: THREE.DoubleSide,
            vertexColors: true,
            shininess:40
        });
        const solidMesh = new THREE.Mesh(geometry, matSolid);

        // wireframe material
        const matWire = new THREE.MeshBasicMaterial({
            color:0xcccccc,
            wireframe:true
        });
        const wireMesh = new THREE.Mesh(geometry, matWire);

        // group
        const group = new THREE.Group();
        group.add(solidMesh);
        group.add(wireMesh);
        return group;
    }

    function updateParaboloid(){
        const a = parseFloat(sliders.a.value);
        const b = parseFloat(sliders.b.value);
        const c = parseFloat(sliders.c.value);

        // remove old
        if(paraboloidGroup){
            scene.remove(paraboloidGroup);
            paraboloidGroup.traverse(obj=>{
                if(obj.geometry) obj.geometry.dispose();
                if(obj.material){ 
                    if(Array.isArray(obj.material)) obj.material.forEach(m=>m.dispose());
                    else obj.material.dispose();
                }
            });
            paraboloidGroup=null;
        }
        // create new
        paraboloidGroup = buildParaboloid(a,b,c);
        scene.add(paraboloidGroup);
    }

    // build initial
    updateParaboloid();

    /********************
     *  EVENT BINDING   *
     ********************/
    function bindPair(rangeEl, numberEl){
        rangeEl.addEventListener('input', () => {
            numberEl.value = rangeEl.value;
            updateParaboloid();
        });
        numberEl.addEventListener('change', () => {
            // clamp to min/max
            const min = parseFloat(numberEl.min);
            const max = parseFloat(numberEl.max);
            let val = parseFloat(numberEl.value);
            if(isNaN(val)) val = min;
            val = Math.max(min, Math.min(max, val));
            numberEl.value = val.toFixed(1);
            rangeEl.value = numberEl.value;
            updateParaboloid();
        });
    }
    bindPair(sliders.a, numbers.a);
    bindPair(sliders.b, numbers.b);
    bindPair(sliders.c, numbers.c);

    // handle resizing
    window.addEventListener('resize',()=>{
        const w = container.clientWidth;
        const h = container.clientHeight;
        camera.aspect = w/h;
        camera.updateProjectionMatrix();
        renderer.setSize(w,h);
    });

    /********************
     *  RENDER LOOP     *
     ********************/
    function animate(){
        requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene,camera);
    }
    animate();

})(); // IIFE
</script>
</body>
</html>
