<!-- ────────── Libraries ────────── -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r152/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.152.2/examples/js/controls/OrbitControls.js"></script>

<script>
/* ==========================================================
 *               STATE & INITIALIZATION
 * ========================================================== */
const state = {
    j1: 1.5,
    m1: 0.5,
    j2: 1.0,
    m2: 0.0,
    j:  2.5
};

let isAnimating = false;
let phaseZ = 0;   // global precession around Z
let phaseJ = 0;   // precession around J (coupled model)

/* ==========================================================
 *                     DOM HELPERS
 * ========================================================== */
function clearContainer(el){ while(el.firstChild) el.removeChild(el.firstChild);}
function createButtons(containerId, values, selectedVal){
    const container = document.getElementById(containerId);
    clearContainer(container);
    values.forEach(v=>{
        const btn = document.createElement('button');
        btn.textContent = displayValue(v);
        btn.dataset.value = v;
        if(+v === +selectedVal) btn.classList.add('selected');
        btn.addEventListener('click', ()=>handleSelection(containerId, +v));
        container.appendChild(btn);
    });
}
function displayValue(v){
    // render half-integers nicely
    if(Math.abs(v*2 - Math.round(v*2))<1e-3){
        const num = Math.round(v*2);
        if(num%2===0) return (num/2).toString();          // integer
        else return (num+'/2');                            // half-integer
    }
    return v.toString();
}
function updateSelected(containerId, value){
    const btns = document.querySelectorAll(`#${containerId} button`);
    btns.forEach(b=>{
        b.classList.toggle('selected', +b.dataset.value === +value);
    });
}

/* ==========================================================
 *            QUANTUM NUMBER SELECTION LOGIC
 * ========================================================== */
function handleSelection(containerId, value){
    switch(containerId){
        case 'j1-selector':
            state.j1 = value;
            repopulateAfterJChange();
            break;
        case 'j2-selector':
            state.j2 = value;
            repopulateAfterJChange();
            break;
        case 'm1-selector':
            state.m1 = value;
            updateSelected(containerId, value);
            resetAnimation();
            rebuildScene();
            break;
        case 'm2-selector':
            state.m2 = value;
            updateSelected(containerId, value);
            resetAnimation();
            rebuildScene();
            break;
        case 'j-selector':
            state.j = value;
            updateSelected(containerId, value);
            resetAnimation();
            rebuildScene();
            break;
    }
}
function repopulateAfterJChange(){
    // m1
    const m1Vals = range(state.j1, 1);
    state.m1 = chooseDefaultM(m1Vals);
    createButtons('m1-selector', m1Vals, state.m1);

    // m2
    const m2Vals = range(state.j2, 1);
    state.m2 = chooseDefaultM(m2Vals);
    createButtons('m2-selector', m2Vals, state.m2);

    // j
    const jVals = range(Math.abs(state.j1-state.j2), 1, state.j1+state.j2);
    state.j = jVals[jVals.length-1];   // largest
    createButtons('j-selector', jVals, state.j);

    // highlight j1/j2 selections too
    updateSelected('j1-selector', state.j1);
    updateSelected('j2-selector', state.j2);

    resetAnimation();
    rebuildScene();
}
function range(startOrJ, step=1, end){
    let start, stop;
    if(end === undefined){ // called with j
        start = -startOrJ; stop = startOrJ;
    }else{
        start = startOrJ; stop = end;
    }
    const arr=[];
    for(let v=start; v<=stop+1e-6; v+=step){
        arr.push(+v.toFixed(1));
    }
    return arr;
}
function chooseDefaultM(list){
    for(const v of list) if(v>=0) return v;
    return list[list.length-1];
}

/* ==========================================================
 *                    THREE.JS SCENE
 * ========================================================== */
let scene,camera,renderer,controls,canvas;
let arrows=[], cones=[];
function initThree(){
    canvas = document.getElementById('main-canvas');
    renderer = new THREE.WebGLRenderer({canvas,antialias:true,alpha:true});
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(canvas.clientWidth, canvas.clientHeight,false);

    scene = new THREE.Scene();

    camera = new THREE.PerspectiveCamera(45, canvas.clientWidth/canvas.clientHeight, 0.1, 1000);
    camera.position.set(6,6,4);
    camera.up.set(0,0,1);

    controls = new THREE.OrbitControls(camera, renderer.domElement);

    // lights
    scene.add(new THREE.AmbientLight(0xffffff,0.6));
    const dirLight = new THREE.DirectionalLight(0xffffff,0.8);
    dirLight.position.set(5,3,5);
    scene.add(dirLight);

    window.addEventListener('resize', onWindowResize);
    rebuildScene();
    animate();
}
function onWindowResize(){
    const width = canvas.clientWidth;
    const height= canvas.clientHeight;
    camera.aspect = width/height;
    camera.updateProjectionMatrix();
    renderer.setSize(width,height,false);
}

/* ------------------ build vectors & cones ------------------ */
function clearObjects(arr){
    arr.forEach(obj=>{
        scene.remove(obj);
        if(obj.geometry) obj.geometry.dispose();
        if(obj.material) obj.material.dispose();
    });
    arr.length=0;
}

function rebuildScene(){
    clearObjects(arrows);
    clearObjects(cones);

    const L1 = Math.sqrt(state.j1*(state.j1+1));
    const L2 = Math.sqrt(state.j2*(state.j2+1));
    const LJ = Math.sqrt(state.j*(state.j+1));
    const mTot = state.m1 + state.m2;

    if(state.j >= state.j1 && state.j >= state.j2){  // Coupled model
        // ------- J vector (black) -------
        const thetaJ = Math.acos(clamp(mTot/LJ));
        const dirJ = sphericalDir(thetaJ, phaseZ);   // initial at current phaseZ
        const arrowJ = createArrow(dirJ, LJ, 0x000000);
        arrows.push(arrowJ);

        // ------- cones -------
        cones.push( createZCone(thetaJ, LJ, 0x000000) ); // around Z for J

        // angles between vectors
        const cosA1 = clamp((LJ*LJ + L1*L1 - L2*L2)/(2*LJ*L1));
        const alpha1 = Math.acos(cosA1);

        // build J1
        const dirJ1_world = rotateAroundJ(dirJ, alpha1, phaseJ, true);  // +phase
        const arrowJ1 = createArrow(dirJ1_world, L1, 0xd22);
        arrows.push(arrowJ1);

        // J2 obtained by vector difference
        const vecJ = dirJ.clone().multiplyScalar(LJ);
        const vecJ1 = dirJ1_world.clone().multiplyScalar(L1);
        const vecJ2 = vecJ.clone().sub(vecJ1);
        const dirJ2_world = vecJ2.clone().normalize();
        const arrowJ2 = createArrow(dirJ2_world, L2, 0x2222ff);
        arrows.push(arrowJ2);

        // cones around J for J1 & J2
        cones.push( createConeAroundAxis(dirJ, alpha1, L1, 0xd22) );
        const alpha2 = Math.acos( clamp( (LJ*LJ + L2*L2 - L1*L1)/(2*LJ*L2) ) );
        cones.push( createConeAroundAxis(dirJ, alpha2, L2, 0x2222ff) );

    }else{ // Uncoupled model
        // ------- J1 -------
        const theta1 = Math.acos(clamp(state.m1/L1));
        const dir1 = sphericalDir(theta1, phaseZ);
        const arrow1 = createArrow(dir1, L1, 0xd22);
        arrows.push(arrow1);

        // ------- J2 -------
        const theta2 = Math.acos(clamp(state.m2/L2));
        const dir2 = sphericalDir(theta2, phaseZ + Math.PI/3); // phase offset
        const arrow2 = createArrow(dir2, L2, 0x2222ff);
        arrows.push(arrow2);

        // cones around Z for J1 & J2
        cones.push( createZCone(theta1, L1, 0xd22) );
        cones.push( createZCone(theta2, L2, 0x2222ff) );
    }
    renderer.render(scene,camera);
}

/* ------------------ helper to build arrow ------------------ */
function createArrow(dir, length, color){
    const headLength = 0.15*length;
    const headWidth  = 0.07*length;
    const arrow = new THREE.ArrowHelper(dir, new THREE.Vector3(0,0,0), length, color, headLength, headWidth);
    scene.add(arrow);
    return arrow;
}

/* ------------------ cones ------------------ */
function createZCone(theta, length, color){
    const radius = length*Math.sin(theta);
    const height = length;
    const geo = new THREE.ConeGeometry(radius, height, 40,1, true);
    geo.translate(0,0,-height/2); // apex at origin (0,0,0) (pointing +Z)
    const mat = new THREE.MeshLambertMaterial({color:color,opacity:0.25,transparent:true,side:THREE.DoubleSide});
    const mesh = new THREE.Mesh(geo, mat);
    mesh.rotation.x = Math.PI/2; // align to +Z
    scene.add(mesh);
    return mesh;
}
function createConeAroundAxis(axisDir, angle, length, color){
    const radius = length*Math.sin(angle);
    const height = length;
    const geo = new THREE.ConeGeometry(radius,height,40,1,true);
    geo.translate(0,0,-height/2);
    const mat = new THREE.MeshLambertMaterial({color:color,opacity:0.18,transparent:true,side:THREE.DoubleSide});
    const mesh = new THREE.Mesh(geo, mat);
    // rotate so that cone axis aligns with axisDir
    const q = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), axisDir.clone().normalize());
    mesh.applyQuaternion(q);
    scene.add(mesh);
    return mesh;
}

/* ------------------ vector math helpers ------------------ */
function sphericalDir(theta, phi){
    // theta: angle from +Z, phi: azimuth about Z
    return new THREE.Vector3(
        Math.sin(theta)*Math.cos(phi),
        Math.sin(theta)*Math.sin(phi),
        Math.cos(theta)
    ).normalize();
}
function clamp(x){ return Math.max(-1, Math.min(1, x)); }

/*  Rotate vector around given axis (axisDir) by alpha (polar) and azimuth phiJ  */
function rotateAroundJ(axisDir, polarAngle, azim, returnDirOnly=true){
    // Build quaternion that aligns Z with axisDir
    const qAlign = new THREE.Quaternion().setFromUnitVectors(
        new THREE.Vector3(0,0,1),
        axisDir.clone().normalize()
    );
    // local vector in J-frame
    const vLocal = new THREE.Vector3(
        Math.sin(polarAngle)*Math.cos(azim),
        Math.sin(polarAngle)*Math.sin(azim),
        Math.cos(polarAngle)
    );
    vLocal.applyQuaternion(qAlign);
    return vLocal.normalize();
}

/* ==========================================================
 *                       ANIMATION LOOP
 * ========================================================== */
let lastTime = null;
function animate(time){
    requestAnimationFrame(animate);
    if(lastTime===null) lastTime=time;
    const dt = (time-lastTime)*0.001; // seconds
    lastTime=time;
    if(isAnimating){
        phaseZ += dt*1.2;  // speed factors
        phaseJ += dt*2.0;
        updateDynamicVectors();
    }
    controls.update();
    renderer.render(scene,camera);
}
function updateDynamicVectors(){
    // Simply rebuild vectors keeping cones static
    const existingCones = cones.slice();
    rebuildScene();
    // restore cones (they were remade) - to keep consistent we removed & recreated cones anyway
}

/* ==========================================================
 *                   ANIMATION CONTROLS
 * ========================================================== */
const btnPlayPause = document.getElementById('btn-play-pause');
const btnReset     = document.getElementById('btn-reset');

btnPlayPause.addEventListener('click', ()=>{
    isAnimating = !isAnimating;
    btnPlayPause.textContent = isAnimating ? '❚❚' : '▶';
});
btnReset.addEventListener('click', ()=>{
    resetAnimation();
    rebuildScene();
});
function resetAnimation(){
    isAnimating=false;
    btnPlayPause.textContent='▶';
    phaseZ=0;
    phaseJ=0;
}

/* ==========================================================
 *                      START-UP
 * ========================================================== */
window.addEventListener('DOMContentLoaded', ()=>{
    // generate initial selectors
    updateSelected('j1-selector', state.j1);
    updateSelected('j2-selector', state.j2);
    repopulateAfterJChange();            // populates m's and j
    initThree();
});
</script>
</body>
</html>
