</div>

<script>
/* ================== CONSTANTS ================== */
const GRID_SIZE = 10;
const CELL_SIZE = 20;
const CANVAS_SIZE = GRID_SIZE * CELL_SIZE + 1;   // 201px
const EMPTY = 0;
const OBSTACLE = 1;
const START_X = 4;
const START_Y = 4;

/* ================== STATE ================== */
let grid = [];                       // 2-D array with EMPTY / OBSTACLE
let recursiveHistory = [];           // [ [x,y], ... ]
let iterativeHistory = [];           // [ [x,y], ... ]
let activePattern = 'scattered';     // none | box | scattered

/* DOM ELEMENTS */
const slider       = document.getElementById('steps-slider');
const stepsValueEl = document.getElementById('steps-value');
const btnNone      = document.getElementById('btn-none');
const btnBox       = document.getElementById('btn-box');
const btnScattered = document.getElementById('btn-scattered');
const buttons      = [btnNone, btnBox, btnScattered];

/* ================== UTILS ================== */
function make2DArray(rows, cols, fill=EMPTY){
    const arr = new Array(rows);
    for(let y=0;y<rows;y++){
        arr[y] = new Array(cols).fill(fill);
    }
    return arr;
}

/* ---- Seeded RNG for reproducible scattered pattern ---- */
let lcg_seed = 123456;
function srand(seed){ lcg_seed = seed; }
function rand(){        // returns 0 .. 1
    lcg_seed = (lcg_seed * 1664525 + 1013904223) % 4294967296;
    return lcg_seed / 4294967296;
}

/* ================== GRID GENERATORS ================== */
function generateGrid(pattern){
    grid = make2DArray(GRID_SIZE, GRID_SIZE, EMPTY);

    switch(pattern){
        case 'none':
            // leave all empty
            break;

        case 'box':
            for(let i=0;i<GRID_SIZE;i++){
                grid[0][i] = OBSTACLE;
                grid[GRID_SIZE-1][i] = OBSTACLE;
                grid[i][0] = OBSTACLE;
                grid[i][GRID_SIZE-1] = OBSTACLE;
            }
            break;

        case 'scattered':
            srand(42);   // fixed seed
            for(let y=0;y<GRID_SIZE;y++){
                for(let x=0;x<GRID_SIZE;x++){
                    if(x === START_X && y === START_Y) continue;   // keep start free
                    if(rand() < 0.25){
                        grid[y][x] = OBSTACLE;
                    }
                }
            }
            break;
    }
}

/* ================== FLOOD FILL ALGORITHMS ================== */
function inside(x,y){
    return x>=0 && x<GRID_SIZE && y>=0 && y<GRID_SIZE;
}

function floodFillRecursive(x, y, visited){
    if(!inside(x,y)) return;
    if(grid[y][x] === OBSTACLE) return;
    if(visited[y][x]) return;

    visited[y][x] = true;
    recursiveHistory.push([x,y]);

    floodFillRecursive(x, y-1, visited); // North
    floodFillRecursive(x-1, y, visited); // West
    floodFillRecursive(x, y+1, visited); // South
    floodFillRecursive(x+1, y, visited); // East
}

function floodFillIterative(){
    const visited = make2DArray(GRID_SIZE, GRID_SIZE, false);
    const queue = [];
    queue.push([START_X, START_Y]);
    visited[START_Y][START_X] = true;

    const dirs = [[0,-1],[-1,0],[0,1],[1,0]]; // N W S E

    while(queue.length){
        const [x,y] = queue.shift();
        iterativeHistory.push([x,y]);

        for(const [dx,dy] of dirs){
            const nx = x + dx;
            const ny = y + dy;
            if(!inside(nx,ny)) continue;
            if(grid[ny][nx] === OBSTACLE) continue;
            if(visited[ny][nx]) continue;

            visited[ny][nx] = true;
            queue.push([nx,ny]);
        }
    }
}

/* ================== SIMULATION RUNNER ================== */
function runSimulation(pattern){
    activePattern = pattern;
    generateGrid(pattern);

    // histories
    recursiveHistory = [];
    iterativeHistory = [];

    // Recursive (DFS)
    const visitedRec = make2DArray(GRID_SIZE, GRID_SIZE, false);
    floodFillRecursive(START_X, START_Y, visitedRec);

    // Iterative (BFS)
    floodFillIterative();

    // Slider bounds
    const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
    slider.max = maxSteps;
    slider.value = 0;
    updateStepValue();

    // Redraw both sketches
    recursiveSketch.redraw();
    iterativeSketch.redraw();
}

/* ================== P5 SKETCH FACTORY ================== */
function createSketch(historyRef){
    return (p)=>{
        p.setup = function(){
            p.createCanvas(CANVAS_SIZE, CANVAS_SIZE).parent(p._userNode);
            p.noLoop();
        };

        p.draw = function(){
            p.background(255);
            p.stroke(0);
            p.strokeWeight(1);
            const currentStep = parseInt(slider.value);

            // Build a Set of filled cells up to currentStep
            const filled = new Set();
            for(let i=0;i<currentStep && i<historyRef.length;i++){
                const [x,y] = historyRef[i];
                filled.add(`${x},${y}`);
            }

            for(let y=0;y<GRID_SIZE;y++){
                for(let x=0;x<GRID_SIZE;x++){
                    // Determine fill color
                    if(grid[y][x] === OBSTACLE){
                        p.fill(0);
                    }else if(filled.has(`${x},${y}`)){
                        p.fill(0,0,255);
                    }else{
                        p.fill(255);
                    }
                    p.rect(x*CELL_SIZE, y*CELL_SIZE, CELL_SIZE, CELL_SIZE);
                }
            }
        };
    };
}

/* ================== CREATE SKETCH INSTANCES ================== */
const recursiveSketch = new p5(createSketch(recursiveHistory), document.getElementById('canvas-recursive'));
const iterativeSketch = new p5(createSketch(iterativeHistory), document.getElementById('canvas-iterative'));

/* ================== UI HANDLERS ================== */
function updateStepValue(){
    stepsValueEl.textContent = '+ ' + slider.value;
}

/* ---- Slider ---- */
slider.addEventListener('input', ()=>{
    updateStepValue();
    recursiveSketch.redraw();
    iterativeSketch.redraw();
});

/* ---- Pattern Buttons ---- */
function setActiveButton(btn){
    buttons.forEach(b => b.classList.remove('active'));
    btn.classList.add('active');
}
btnNone.addEventListener('click', ()=>{
    setActiveButton(btnNone);
    runSimulation('none');
});
btnBox.addEventListener('click', ()=>{
    setActiveButton(btnBox);
    runSimulation('box');
});
btnScattered.addEventListener('click', ()=>{
    setActiveButton(btnScattered);
    runSimulation('scattered');
});

/* ================== INITIALIZATION ================== */
(function init(){
    slider.min = 0;
    slider.step = 1;
    runSimulation('scattered');      // default
    // After default simulation, set initial slider to 61 if possible
    const initialVal = Math.min(61, slider.max);
    slider.value = initialVal;
    updateStepValue();
    recursiveSketch.redraw();
    iterativeSketch.redraw();
})();
</script>
</body>
</html>
