<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Stroboscopic Bouncing Ball</title>
  <style>
    body{
      margin:0;
      font-family:Arial, Helvetica, sans-serif;
      background:#222;
      color:#ddd;
    }
    #control-panel{
      display:flex;
      flex-wrap:wrap;
      gap:12px 24px;
      padding:15px 20px;
      background:#333;
      align-items:center;
    }
    #control-panel div{
      display:flex;
      align-items:center;
      min-width:260px;
    }
    #control-panel label{
      margin-right:10px;
      width:140px;
      text-align:right;
    }
    #control-panel input[type="range"]{
      width:140px;
    }
    #canvas-container{
      padding:10px;
      text-align:center;
    }
    canvas{
      border:1px solid #555;
    }
  </style>
  <!-- p5.js CDN -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
  <!-- --------------- CONTROL PANEL --------------- -->
  <div id="control-panel">
    <div>
      <label for="slider-k">k</label>
      <input type="range" id="slider-k" min="0.6" max="0.95" step="0.01" value="0.9">
    </div>
    <div>
      <label for="slider-time">time</label>
      <input type="range" id="slider-time" min="5" max="25" step="0.5" value="15">
    </div>
    <div>
      <label for="slider-vx">initial velocity in x</label>
      <input type="range" id="slider-vx" min="1" max="8" step="0.1" value="2.5">
    </div>
    <div>
      <label for="slider-dt">Δt</label>
      <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
    </div>
  </div>

  <!-- --------------- CANVAS --------------- -->
  <div id="canvas-container"></div>

<script>
/* ---------- GLOBAL CONSTANTS ---------- */
const g = 9.8;          // gravitational acceleration (m/s²)
const y0 = 10;          // initial height (m)
const lineStep = 0.01;  // time resolution for red path (s)
const padding = 40;     // canvas padding for axes (px)
let sliders = [];       // will hold slider element references

/* ---------- p5.js SETUP ---------- */
function setup(){
  const canvas = createCanvas(700,400);
  canvas.parent('canvas-container');
  sliders = ['slider-k','slider-time','slider-vx','slider-dt'].map(id =>
            document.getElementById(id));
  sliders.forEach(sl => sl.addEventListener('input', () => redraw()));
  noLoop();   // make sketch run on demand only
  redraw();   // draw initial visualization
}

/* ---------- MAIN DRAW ---------- */
function draw(){
  // Read slider values
  const k        = parseFloat(document.getElementById('slider-k').value);
  const totalT   = parseFloat(document.getElementById('slider-time').value);
  const vx       = parseFloat(document.getElementById('slider-vx').value);
  const dtStrobe = parseFloat(document.getElementById('slider-dt').value);

  // Compute trajectory
  const sim = computeTrajectory(k, totalT, vx, dtStrobe);
  const pathPts   = sim.pathPoints;
  const strobePts = sim.strobePoints;
  const maxX      = sim.maxX;
  const maxY      = sim.maxY;

  /* ----- RENDERING ----- */
  background(0);

  push();
  translate(padding, height - padding);  // move origin
  scale(1, -1);                          // flip Y to point up

  // Determine drawing scale (m -> px)
  const scaleFactor = Math.min(
       (width  - 2*padding) / maxX,
       (height - 2*padding) / maxY);

  /* Draw axes */
  stroke(100,100,150);
  strokeWeight(1);
  // x-axis
  line(0, 0, (width-2*padding), 0);
  // y-axis
  line(0, 0, 0, (height-2*padding));

  // Tick marks every 1 m
  const tickPx = 5;                      // tick length in pixels
  const tickLenMeters = tickPx / scaleFactor;

  for(let x=0; x<=maxX; x+=1){
    const xp = x * scaleFactor;
    line(xp, -tickLenMeters, xp, tickLenMeters);
  }
  for(let y=0; y<=maxY; y+=1){
    const yp = y * scaleFactor;
    line(-tickLenMeters, yp, tickLenMeters, yp);
  }

  /* Draw red trajectory line */
  noFill();
  stroke(255,0,0);
  strokeWeight(2);
  beginShape();
  pathPts.forEach(p => vertex(p.x * scaleFactor, p.y * scaleFactor));
  endShape();

  /* Draw yellow strobe points */
  noStroke();
  fill(255,255,0);
  strobePts.forEach(p =>
    ellipse(p.x * scaleFactor, p.y * scaleFactor, 8, 8)
  );

  pop();
}

/* ---------- TRAJECTORY COMPUTATION ---------- */
function computeTrajectory(k, totalTime, vx, dt){
  let currentTime = 0;
  let currentX    = 0;
  let currentY    = y0;
  let currentVy   = 0;

  const pathPoints   = [];
  const strobePoints = [];

  let lastStrobeT = 0;

  while(currentTime < totalTime - 1e-6){
    /* Time of flight until ground hit */
    const discr   = currentVy*currentVy + 2*g*currentY;
    let tFlight   = (currentVy + Math.sqrt(discr)) / g;

    // Cut arc if it exceeds totalTime
    if(currentTime + tFlight > totalTime){
      tFlight = totalTime - currentTime;
    }

    /* Sample red path */
    for(let t=0; t<=tFlight; t+=lineStep){
      const x = currentX + vx*t;
      const y = currentY + currentVy*t - 0.5*g*t*t;
      pathPoints.push({x, y:Math.max(0,y)});
    }

    /* Stroboscopic flashes during this arc */
    let nextStrobe = lastStrobeT + dt;
    while(nextStrobe <= currentTime + tFlight + 1e-8){
      const tRel = nextStrobe - currentTime;
      const xs   = currentX + vx*tRel;
      const ys   = currentY + currentVy*tRel - 0.5*g*tRel*tRel;
      strobePoints.push({x:xs, y:Math.max(0,ys)});
      lastStrobeT = nextStrobe;
      nextStrobe += dt;
    }

    /* If total time reached, break */
    if(currentTime + tFlight >= totalTime - 1e-8) break;

    /* Prepare for next bounce */
    const vyImpact = currentVy - g*tFlight;    // downward velocity (negative)
    const vyNew    = Math.sqrt(k) * Math.abs(vyImpact);
    currentTime += tFlight;
    currentX    += vx * tFlight;
    currentY     = 0;
    currentVy    = vyNew;
  }

  /* Determine bounding box */
  let maxX = 0, maxY = 0;
  pathPoints.forEach(p=>{
    if(p.x > maxX) maxX = p.x;
    if(p.y > maxY) maxY = p.y;
  });
  maxY = Math.max(maxY, y0);

  return {pathPoints, strobePoints, maxX, maxY};
}
</script>
</body>
</html>
