            width:90%;
            max-width:700px;
        }
        #canvas-container{
            display:flex;
            justify-content:center;
            align-items:center;
            width:100%;
        }
        canvas{
            /* just in case – p5 will give a default id, we’ll overwrite it */
            outline:1px solid #ccc;
        }
    </style>
    <!-- p5.js CDN -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.2/p5.js"></script>
</head>
<body>

<header>
    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>
        Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. 
        Then AA' ⟂ B'C', BB' ⟂ A'C', and CC' ⟂ A'B'.
    </p>
</header>

<section id="canvas-container">
    <!-- p5 will create and place the canvas here -->
</section>

<script>
/* === CONSTANTS === */
const CANVAS_SIZE = 500;
const BG_COLOR     = '#F0F0F0';
const RADIUS       = 200;
const CIRCLE_STROKE= '#6A5ACD';
const CONSTR_STROKE= '#FFA500';
const MAIN_STROKE_WEIGHT = 4;
const POINT_DIAM   = 12;
const DRAG_TOLERANCE= 15;

/* === STATE === */
let center;               // {x,y}
let angles = {            // in radians
  A: 210 * Math.PI/180,
  B: 330 * Math.PI/180,
  C:  90 * Math.PI/180
};
let activePoint = null;   // 'A' | 'B' | 'C' | null

function setup(){
  // Create canvas and attach to container
  const cnv = createCanvas(CANVAS_SIZE, CANVAS_SIZE);
  cnv.parent('canvas-container');
  cnv.id('p5-canvas');  // give it the requested id
  center = createVector(width/2, height/2);

  angleMode(RADIANS);
  textSize(16);
  textAlign(CENTER, CENTER);
}

function draw(){
  background(BG_COLOR);
  drawCircumcircle();

  // Compute primary points
  const pA = pointFromAngle(angles.A);
  const pB = pointFromAngle(angles.B);
  const pC = pointFromAngle(angles.C);

  // Compute derived points
  const anglesPrime = {
    Ap: bisectorAngle(angles.B, angles.C),
    Bp: bisectorAngle(angles.A, angles.C),
    Cp: bisectorAngle(angles.A, angles.B)
  };
  const pAp = pointFromAngle(anglesPrime.Ap);
  const pBp = pointFromAngle(anglesPrime.Bp);
  const pCp = pointFromAngle(anglesPrime.Cp);

  // Draw constructions
  strokeWeight(MAIN_STROKE_WEIGHT);

  // Triangle ABC
  stroke(CIRCLE_STROKE);
  line(pA.x, pA.y, pB.x, pB.y);
  line(pB.x, pB.y, pC.x, pC.y);
  line(pC.x, pC.y, pA.x, pA.y);

  // Extended bisectors (AA', BB', CC') and triangle A'B'C'
  stroke(CONSTR_STROKE);
  // Angle bisectors
  line(pA.x, pA.y, pAp.x, pAp.y);
  line(pB.x, pB.y, pBp.x, pBp.y);
  line(pC.x, pC.y, pCp.x, pCp.y);
  // Triangle A'B'C'
  line(pAp.x, pAp.y, pBp.x, pBp.y);
  line(pBp.x, pBp.y, pCp.x, pCp.y);
  line(pCp.x, pCp.y, pAp.x, pAp.y);

  // Draw points
  drawInteractivePoint(pA, 'A');
  drawInteractivePoint(pB, 'B');
  drawInteractivePoint(pC, 'C');

  drawCalculatedPoint(pAp, "A'");
  drawCalculatedPoint(pBp, "B'");
  drawCalculatedPoint(pCp, "C'");
}

/* === DRAWING HELPERS === */

function drawCircumcircle(){
  noFill();
  stroke(CIRCLE_STROKE);
  strokeWeight(MAIN_STROKE_WEIGHT);
  circle(center.x, center.y, 2*RADIUS);
}

function drawInteractivePoint(pt, label){
  // Point symbol
  push();
  stroke(0);
  fill(CONSTR_STROKE);
  circle(pt.x, pt.y, POINT_DIAM);

  // Crosshair
  strokeWeight(1.5);
  const half = POINT_DIAM/2 * 0.6;
  line(pt.x - half, pt.y, pt.x + half, pt.y);
  line(pt.x, pt.y - half, pt.x, pt.y + half);
  pop();

  // Label (offset outward radially)
  drawLabel(pt, label);
}

function drawCalculatedPoint(pt, label){
  push();
  stroke(0);
  fill(CONSTR_STROKE);
  circle(pt.x, pt.y, POINT_DIAM);
  pop();

  drawLabel(pt, label);
}

function drawLabel(pt, label){
  const dir = p5.Vector.sub(pt, center).setMag(18); // outward offset
  noStroke();
  fill(0);
  text(label, pt.x + dir.x, pt.y + dir.y);
}

/* === INTERACTION === */

function mousePressed(){
  const m = createVector(mouseX, mouseY);
  ['A','B','C'].forEach(key=>{
    if(activePoint) return;
    const p = pointFromAngle(angles[key]);
    if(p.dist(m) < DRAG_TOLERANCE){
      activePoint = key;
    }
  });
}

function mouseDragged(){
  if(activePoint){
    const angle = atan2(mouseY - center.y, mouseX - center.x);
    angles[activePoint] = angle;
  }
}

function mouseReleased(){
  activePoint = null;
}

/* === GEOMETRY HELPERS === */

function pointFromAngle(theta){
  return createVector(
    center.x + RADIUS * cos(theta),
    center.y + RADIUS * sin(theta)
  );
}

function bisectorAngle(theta1, theta2){
  // average via vector addition
  const x = cos(theta1) + cos(theta2);
  const y = sin(theta1) + sin(theta2);
  return atan2(y, x);
}
</script>

</body>
</html>
