}

function drawTree(x,y){
    // trunk
    fill('#8B4513');
    rect(x,y,20,60);
    // foliage
    fill('#006400');
    ellipse(x+10, y-20, 60,60);
}

function drawSheep(x,y){
    fill(255);
    ellipse(x,y,60,40); // body
    ellipse(x-25,y-15,30,30); // wool
    fill(0);
    ellipse(x-35,y-5,18,18); // head
    fill(255);
    ellipse(x-38,y-7,4,4); // eye
}

function drawFactory(x,y){
    // building
    fill('#696969');
    rect(x,y,70,70);
    // smokestack
    fill('#A9A9A9');
    rect(x+50,y-60,20,60);
    // smoke
