    ellipse(x+60, y-70,30,20);
    ellipse(x+70, y-85,35,25);
    ellipse(x+55, y-95,25,15);
}

function drawProcess(proc){
    // Draw arrows
    stroke(0);
    fill(0);
    proc.arrows.forEach(a=>{
        drawArrow(a.x1,a.y1,a.x2,a.y2);
    });

    // Draw label
    noStroke();
    fill(0);
    textAlign(LEFT,TOP);
    const lines = proc.labelText.split('\n');
    const lineHeight = textAscent()+2;
    let labelW = 0;
    lines.forEach(l=>{
        labelW = max(labelW, textWidth(l));
    });
    const labelH = lineHeight * lines.length;
    const x = proc.labelPos.x;
    const y = proc.labelPos.y;
    // Actually draw the text
    lines.forEach((l,i)=>{
        text(l, x, y + i*lineHeight);
    });
    // Hover detection
    if(mouseX>=x && mouseX<=x+labelW && mouseY>=y && mouseY<=y+labelH){
        hoverDesc = proc.desc;
    }
}

function drawArrow(x1,y1,x2,y2){
    push();
    stroke(0);
    fill(0);
    line(x1,y1,x2,y2);
    const angle = atan2(y2 - y1, x2 - x1);
    translate(x2,y2);
    rotate(angle);
    const size = 8;
    triangle(0,0, -size, size/2, -size, -size/2);
    pop();
}
</script>
