<script>
/* ---------- Global state ---------- */
const state = {
  t: 0.4,
  showPosition: false,
  showVelocity: false,
  showAcceleration: true,
  coordSystem: 'rectangular',
  showCircle: false
};

/* ---------- DOM Elements ---------- */
const sliderTime       = document.getElementById('slider-time');
const btnResetTime     = document.getElementById('btn-reset-time');
const radiosPosition   = document.querySelectorAll('input[name="position"]');
const radiosVelocity   = document.querySelectorAll('input[name="velocity"]');
const radiosAcceleration = document.querySelectorAll('input[name="acceleration"]');
const selectCoords     = document.getElementById('select-coords');
const radiosCircle     = document.querySelectorAll('input[name="circle"]');

/* ---------- Event Listeners ---------- */
sliderTime.addEventListener('input', ()=>{ state.t = parseFloat(sliderTime.value); redraw(); });
btnResetTime.addEventListener('click', ()=>{
  sliderTime.value = 0.4;
  state.t = 0.4;
  redraw();
});

radiosPosition.forEach(r=>r.addEventListener('change', ()=>{
  state.showPosition = document.getElementById('radio-position-on').checked;
  redraw();
}));
radiosVelocity.forEach(r=>r.addEventListener('change', ()=>{
  state.showVelocity = document.getElementById('radio-velocity-on').checked;
  redraw();
}));
radiosAcceleration.forEach(r=>r.addEventListener('change', ()=>{
  state.showAcceleration = document.getElementById('radio-acceleration-on').checked;
  redraw();
}));
radiosCircle.forEach(r=>r.addEventListener('change', ()=>{
  state.showCircle = document.getElementById('radio-circle-on').checked;
  redraw();
}));

selectCoords.addEventListener('change', ()=>{
  state.coordSystem = selectCoords.value;
  redraw();
});

/* ---------- p5.js Sketch ---------- */
let canvas, w=600, h=600, scaleFactor;

/* Helper: convert world coords to screen */
function worldToScreen(x,y){
  return {
    sx: w/2 + x*scaleFactor,
    sy: h/2 - y*scaleFactor
  };
}

/* Helper: draw arrow in world coordinates */
function drawArrow(px,py,vx,vy,options={}){
  const {col='black', solid=true, len=1} = options;
  const ctx = drawingContext;
  ctx.save();
  if(!solid) ctx.setLineDash([6,6]);
  stroke(col);
  fill(col);
  strokeWeight(2);

  const start = worldToScreen(px,py);
  const end   = worldToScreen(px+vx, py+vy);
  line(start.sx, start.sy, end.sx, end.sy);

  // arrowhead
  const angle = Math.atan2(start.sy - end.sy, end.sx - start.sx);
  const size = 8;
  push();
  translate(end.sx, end.sy);
  rotate(-angle);
  triangle(0,0, -size, size/2, -size,-size/2);
  pop();

  ctx.restore();
}

/* p5 mandatory functions */
function setup(){
  // Determine canvas size relative to container
  const container = document.getElementById('canvas-container');
  w = container.clientWidth || 600;
  h = container.clientHeight || 600;
  canvas = createCanvas(w,h);
  canvas.parent('canvas-container');
  scaleFactor = w/4; // world units (-2..2) => pixels
  noLoop();
  redraw(); // initial draw
}

function redraw(){
  clear();
  background(255);

  // axes
  stroke(0);
  strokeWeight(1);
  // x-axis
  line(0, h/2, w, h/2);
  // y-axis
  line(w/2, 0, w/2, h);

  // ticks
  const tickSpacing = scaleFactor; // 1 unit
  for(let i=-2;i<=2;i++){
    const px = w/2 + i*tickSpacing;
    line(px, h/2-4, px, h/2+4);
    if(i!==0){
      noStroke();
      fill(0);
      textAlign(CENTER,TOP);
      text(i, px, h/2+6);
      stroke(0);
    }
  }
  for(let i=-2;i<=2;i++){
    const py = h/2 - i*tickSpacing;
    line(w/2-4, py, w/2+4, py);
    if(i!==0){
      noStroke();
      fill(0);
      textAlign(LEFT,CENTER);
      text(i, w/2+6, py);
      stroke(0);
    }
  }

  // ----- Draw lemniscate curve -----
  stroke('#0066ff');
  strokeWeight(2);
  noFill();
  beginShape();
  for(let t=0;t<=Math.PI*2+0.01;t+=0.01){
    const x = Math.cos(t);
    const y = Math.sin(2*t);
    const {sx,sy} = worldToScreen(x,y);
    vertex(sx,sy);
  }
  endShape();

  // Current point & kinematics
  const t = state.t;
  const pos = {x: Math.cos(t), y: Math.sin(2*t)};
  const vel = {x: -Math.sin(t), y: 2*Math.cos(2*t)};
  const acc = {x: -Math.cos(t), y: -4*Math.sin(2*t)};

  const vMag = Math.hypot(vel.x, vel.y);
  const aMag = Math.hypot(acc.x, acc.y);

  // Basis vectors length (world units)
  const basisLen = 0.6;

  // Draw position dot
  const {sx:psx, sy:psy} = worldToScreen(pos.x,pos.y);
  fill(0);
  noStroke();
  circle(psx,psy,8);

  // Position vector from origin
  if(state.showPosition){
    drawArrow(0,0,pos.x,pos.y,{col:'black'});
  }

  // Velocity vector
  if(state.showVelocity){
    let scale=0.4; // scale factor to shorten vector
    drawArrow(pos.x,pos.y, vel.x*scale, vel.y*scale,{col:'#aa0000'});
  }

  // Acceleration vector
  if(state.showAcceleration){
    let scale=0.15;
    drawArrow(pos.x,pos.y, acc.x*scale, acc.y*scale,{col:'#cc00cc'});
  }

  // ----- Basis vectors -----
  let e1, e2, label1, label2;
  if(state.coordSystem==='rectangular'){
    e1 = {x:1, y:0};
    e2 = {x:0, y:1};
    label1 = 'î'; label2='ĵ';
  }else if(state.coordSystem==='normal-tangential'){
    const eT = {x: vel.x/vMag, y: vel.y/vMag};
    // sign for inward normal
    const cross = vel.x*acc.y - vel.y*acc.x;
    const sign = cross>=0 ? 1:-1;
    const eN = {x: -sign*eT.y, y: sign*eT.x};
    e1 = eT; e2 = eN;
    label1='ê_T'; label2='ê_N';
  }else{ // polar
    const rMag = Math.hypot(pos.x,pos.y);
    const eR = {x: pos.x/rMag, y: pos.y/rMag};
    const eTheta = {x: -eR.y, y: eR.x};
    e1=eR; e2=eTheta;
    label1='ê_r'; label2='ê_θ';
  }

  // draw basis vectors (dashed)
  drawArrow(pos.x,pos.y, e1.x*basisLen, e1.y*basisLen,{col:'black', solid:false});
  drawArrow(pos.x,pos.y, e2.x*basisLen, e2.y*basisLen,{col:'black', solid:false});

  // basis labels
  noStroke();
  fill(0);
  let tip1 = worldToScreen(pos.x+e1.x*basisLen, pos.y+e1.y*basisLen);
  let tip2 = worldToScreen(pos.x+e2.x*basisLen, pos.y+e2.y*basisLen);
  textAlign(CENTER, CENTER);
  text(label1, tip1.sx+12, tip1.sy);
  text(label2, tip2.sx+12, tip2.sy);

  // ----- Instantaneous circle -----
  if(state.showCircle){
    const cross = vel.x*acc.y - vel.y*acc.x;
    const rho = Math.pow(vMag,3)/Math.abs(cross); // radius of curvature
    const eT = {x: vel.x/vMag, y: vel.y/vMag};
    const sign = cross>=0 ? 1:-1;
    const eN = {x: -sign*eT.y, y: sign*eT.x};
    const center = {x: pos.x + rho*eN.x, y: pos.y + rho*eN.y};

    const centerScreen = worldToScreen(center.x, center.y);
    const rPix = rho*scaleFactor;

    const ctx = drawingContext;
    ctx.save();
    ctx.setLineDash([8,6]);
    noFill();
    stroke('#888');
    strokeWeight(1);
    ellipse(centerScreen.sx, centerScreen.sy, rPix*2, rPix*2);
    ctx.restore();
  }

}

/* p5 draw (unused) */
function draw(){ /* empty because we manage redraw manually */ }
</script>

</body>
</html>
