</style>
</head>
<body>
<div id="main-container">
    <!-- ------------- CONTROL PANEL ------------- -->
    <div id="control-panel">
        <!-- sliders -->
        <div class="slider-block" id="amplitude-control">
            <label for="slider-amplitude">Amplitude</label>
            <input type="range" id="slider-amplitude" min="0.1" max="1.0" step="0.001" value="0.3">
            <span id="amplitude-value">0.300</span>
        </div>

        <div class="slider-block" id="stiffness-control">
            <label for="slider-stiffness">Stiffness</label>
            <input type="range" id="slider-stiffness" min="0.1" max="10.0" step="0.01" value="1.0">
            <span id="stiffness-value">1.00</span>
        </div>

        <div class="slider-block" id="mass-control">
            <label for="slider-mass">Mass</label>
            <input type="range" id="slider-mass" min="0.1" max="10.0" step="0.01" value="1.0">
            <span id="mass-value">1.00</span>
        </div>

        <!-- time buttons -->
        <div id="time-controls">
            <label for="">Time</label>
            <button id="btn-step-back" title="step back">◀</button>
            <button id="btn-play-pause" title="play/pause">▶</button>
            <button id="btn-step-forward" title="step forward">▶|</button>
        </div>

        <!-- reset -->
        <button id="btn-reset" title="reset">✕</button>
    </div>

    <!-- ------------- VISUALIZATION AREA ------------- -->
    <div id="visualization-container">
        <div id="plot-canvas-container"></div>
        <div id="animation-canvas-container"></div>
    </div>
</div>

<script>
/* ================== GLOBAL STATE =================== */
let amplitude = 0.3,
    stiffness = 1.0,
    mass = 1.0,
    time = 0,
    isPlaying = false,
    angularFrequency = Math.sqrt(stiffness/mass),
    positionHistory = [];

let xRangeMax = 25;         // initial x-axis max
const xRangeMin = 0;
const timeStepPlay  = 0.05; // dt when playing
const timeStepStep  = 0.10; // dt for manual step
const yRangeAbs = 1.2;      // plot Y range (±)

/* ================== DOM ELEMENTS =================== */
const elAmpSlider   = document.getElementById('slider-amplitude');
const elStiffSlider = document.getElementById('slider-stiffness');
const elMassSlider  = document.getElementById('slider-mass');
const elAmpVal      = document.getElementById('amplitude-value');
const elStiffVal    = document.getElementById('stiffness-value');
const elMassVal     = document.getElementById('mass-value');

const btnPlayPause  = document.getElementById('btn-play-pause');
const btnStepBack   = document.getElementById('btn-step-back');
const btnStepFwd    = document.getElementById('btn-step-forward');
const btnReset      = document.getElementById('btn-reset');

/* ================== PARAMETER HANDLING =================== */
function formatNum(num, decimals=3){
    return num.toFixed(decimals);
}

function updateAngularFrequency(){
    angularFrequency = Math.sqrt(stiffness / mass);
}

function resetSimulation(clearSliders=false){
    if(clearSliders){
        elAmpSlider.value   = 0.3;
        elStiffSlider.value = 1.0;
        elMassSlider.value  = 1.0;
    }

    amplitude = parseFloat(elAmpSlider.value);
    stiffness = parseFloat(elStiffSlider.value);
    mass      = parseFloat(elMassSlider.value);

    elAmpVal.textContent   = formatNum(amplitude,3);
    elStiffVal.textContent = formatNum(stiffness,2);
    elMassVal.textContent  = formatNum(mass,2);

    time = 0;
    isPlaying = false;
    btnPlayPause.textContent = '▶';

    updateAngularFrequency();
    positionHistory.length = 0;      // clear
    xRangeMax = 25;

    // push initial point
    positionHistory.push({t:0, y:0});

    // request canvas redraw
}

/* ---------- Slider events ---------- */
function onSliderInput(){
    amplitude = parseFloat(elAmpSlider.value);
    stiffness = parseFloat(elStiffSlider.value);
    mass      = parseFloat(elMassSlider.value);

    elAmpVal.textContent   = formatNum(amplitude,3);
    elStiffVal.textContent = formatNum(stiffness,2);
    elMassVal.textContent  = formatNum(mass,2);

    // update derived values and reset time/history
    updateAngularFrequency();
    time = 0;
    isPlaying = false;
    btnPlayPause.textContent = '▶';
    positionHistory.length = 0;
    xRangeMax = 25;
}

elAmpSlider.addEventListener('input', onSliderInput);
elStiffSlider.addEventListener('input', onSliderInput);
elMassSlider.addEventListener('input', onSliderInput);

/* ---------- Play / Pause ---------- */
btnPlayPause.addEventListener('click', ()=>{
    isPlaying = !isPlaying;
    btnPlayPause.textContent = isPlaying ? '⏸' : '▶';
});

/* ---------- Manual Step Buttons ---------- */
btnStepFwd.addEventListener('click', ()=>{
    if(isPlaying) return;
    stepSimulation(timeStepStep);
});
btnStepBack.addEventListener('click', ()=>{
    if(isPlaying) return;
    stepSimulation(-timeStepStep);
});

/* ---------- Reset Button ---------- */
btnReset.addEventListener('click', ()=>{
    resetSimulation(true);
});

/* ================== SIMULATION CORE =================== */
function stepSimulation(dt){
    time += dt;
    if(time < 0) time = 0;

    // compute position and store
    const y = amplitude * Math.sin(angularFrequency * time);
    positionHistory.push({t:time, y});

    // extend x-range if needed
    if(time > xRangeMax - 2) xRangeMax += 10;
}

/* ================== PLOT CANVAS (LEFT) =================== */
const plotSketch = (p)=>{
    let leftPad = 40, rightPad=20, topPad=20, botPad=30;

    p.setup = function(){
        const w = document.getElementById('plot-canvas-container').clientWidth;
        const h = document.getElementById('plot-canvas-container').clientHeight || 400;
        const c = p.createCanvas(w,h);
        c.parent('plot-canvas-container');
        p.textFont('Arial');
        resetSimulation(); // put initial point
    };

    p.windowResized = function(){
        const w = document.getElementById('plot-canvas-container').clientWidth;
        const h = document.getElementById('plot-canvas-container').clientHeight || 400;
        p.resizeCanvas(w, h);
    };

    function simToPixel(x, y){
        const px = p.map(x, xRangeMin, xRangeMax, leftPad, p.width - rightPad);
        const py = p.map(y,  yRangeAbs, -yRangeAbs, topPad, p.height - botPad);
        return [px, py];
    }

    function drawGrid(){
        p.stroke(220);
        p.strokeWeight(1);
        p.noFill();

        // horizontal grid lines at -1, 0, 1
        for(let y=-1; y<=1; y+=1){
            const [x1, py] = simToPixel(0,y);
            const [x2, ]   = simToPixel(xRangeMax,y);
            p.line(x1,py,x2,py);
            p.noStroke();
            p.fill(120);
            p.textAlign(p.RIGHT, p.CENTER);
            p.text(y, x1-6, py);
            p.stroke(220);
        }

        // vertical grid every 5 units
        const step=5;
        for(let x=0;x<=xRangeMax;x+=step){
            const [px, y1] = simToPixel(x,-yRangeAbs);
            const [,  y2 ] = simToPixel(x, yRangeAbs);
            p.line(px,y1,px,y2);
            p.noStroke();
            p.fill(120);
            p.textAlign(p.CENTER, p.TOP);
            p.text(x, px, y2+4);
            p.stroke(220);
        }

        // axes
        p.stroke(0);
        // y=0 axis
        const [xA, y0] = simToPixel(0,0);
        const [xB, ]   = simToPixel(xRangeMax,0);
        p.line(xA,y0,xB,y0);
        // x=0 axis
        const [x0, yC] = simToPixel(0,-yRangeAbs);
        const [, yD]   = simToPixel(0, yRangeAbs);
        p.line(x0,yC,x0,yD);
    }

    p.draw = function(){
        // ---------------- Simulation advancing ---------------
        if(isPlaying){
            stepSimulation(timeStepPlay);
        }

        // ---------------- Drawing ----------------------------
        p.background(255);
        drawGrid();

        // plot line
        p.noFill();
        p.stroke('#6363B2');
        p.strokeWeight(2);
        p.beginShape();
        positionHistory.forEach(pt=>{
            const [px, py] = simToPixel(pt.t, pt.y);
            p.vertex(px, py);
        });
        p.endShape();
    };
};

/* ================== ANIMATION CANVAS (RIGHT) =================== */
const animSketch = (p)=>{
    const massW = 60;
    const massH = 40;
    const anchorY = 20;
    const springCoils = 12;
    const springAmp = 15;

    let canvasH, canvasW;

    p.setup = function(){
        canvasW = document.getElementById('animation-canvas-container').clientWidth;
        canvasH = document.getElementById('animation-canvas-container').clientHeight || 400;
        const c = p.createCanvas(canvasW, canvasH);
        c.parent('animation-canvas-container');
        p.textFont('Arial');
    };

    p.windowResized = function(){
        canvasW = document.getElementById('animation-canvas-container').clientWidth;
        canvasH = document.getElementById('animation-canvas-container').clientHeight || 400;
        p.resizeCanvas(canvasW, canvasH);
    };

    function ySimToPixel(ySim){
        // map y in [-1.2,1.2] to pixel (anchorY+20  ..  canvasH-60)
        return p.map(ySim, -yRangeAbs, yRangeAbs, canvasH-60, anchorY+40);
    }

    p.draw = function(){
        p.background(255);

        // current sim position
        const ySim = amplitude * Math.sin(angularFrequency * time);
        const massCenterY = ySimToPixel(ySim);

        // draw anchor
        p.stroke(0);
        p.strokeWeight(4);
        p.line(canvasW/2 - 20, anchorY, canvasW/2 + 20, anchorY);

        // draw spring
        p.stroke(0);
        p.strokeWeight(2);
        p.noFill();
        const springTop = anchorY;
        const springBottom = massCenterY - massH/2;
        const totalLen = springBottom - springTop;
        const segmentLen = totalLen / springCoils;

        p.beginShape();
        p.vertex(canvasW/2, springTop);
        for(let i=1;i<springCoils;i++){
            const dir = i%2===0 ? -1 : 1;
            const x = canvasW/2 + dir*springAmp;
            const y = springTop + i*segmentLen;
            p.vertex(x,y);
        }
        p.vertex(canvasW/2, springBottom);
        p.endShape();

        // draw mass
        p.fill('#6363B2');
        p.noStroke();
        p.rectMode(p.CENTER);
        p.rect(canvasW/2, massCenterY, massW, massH);

        // axis and labels
        p.stroke(220);
        p.strokeWeight(1);
        // vertical central axis
        p.line(canvasW/2, anchorY, canvasW/2, canvasH-20);

        // y-labels at -1,0,1
        p.noStroke();
        p.fill(120);
        [1,0,-1].forEach(v=>{
            const yPix = ySimToPixel(v);
            p.textAlign(p.LEFT,p.CENTER);
            p.text(v.toString(), canvasW/2 + massW/2 + 8, yPix);
            p.stroke(220);
            p.line(canvasW/2-25, yPix, canvasW/2+25, yPix);
            p.noStroke();
        });
    };
};

/* ============ Start p5 sketches after DOM ready ============ */
window.addEventListener('load', ()=>{
    new p5(plotSketch);
    new p5(animSketch);
});
</script>
</body>
</html>
