<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Distance Transform Demo</title>
<style>
    /* Layout */
    #main-container{
        display:flex;
        flex-direction:column;
        align-items:center;
        gap:15px;
        margin-top:20px;
        font-family:Arial,Helvetica,sans-serif;
    }
    #control-panel{
        display:flex;
        align-items:center;
        gap:8px;
    }

    /* Buttons */
    .distance-btn{
        padding:6px 14px;
        border:1px solid #555;
        background-color:#c0c0c0;
        cursor:pointer;
        border-radius:4px;
        font-size:14px;
    }
    .distance-btn.active{
        background-color:#808080;
        color:#fff;
    }

    /* Grid */
    #grid-container{
        display:grid;
        grid-template-columns:repeat(7,40px);
        grid-template-rows:repeat(9,40px);
        gap:2px;
    }
    .cell{
        width:40px;
        height:40px;
        border:1px solid #555;
        display:flex;
        align-items:center;
        justify-content:center;
        cursor:pointer;
        user-select:none;
        font-size:14px;
        font-weight:bold;
    }
    .cell-on{
        background-color:#FFD700; /* gold/yellow */
    }
    .cell-off{
        background-color:#D3D3D3; /* light gray */
    }
</style>
</head>
<body>
<div id="main-container">
    <!-- Control Panel -->
    <div id="control-panel">
        <span>distance function:</span>
        <button id="btn-manhattan" class="distance-btn active">Manhattan</button>
        <button id="btn-euclidean" class="distance-btn">squared Euclidean</button>
        <button id="btn-chebyshev" class="distance-btn">Chebyshev</button>
    </div>

    <!-- Grid Area -->
    <div id="grid-container"></div>
</div>

<script>
/* ----- CONSTANTS & INITIAL STATE ----- */
const ROWS = 9;
const COLS = 7;

let currentDistanceFunction = 'manhattan';   // default
let gridState = Array.from({length: ROWS}, () => Array(COLS).fill(false));
gridState[2][1] = true;  // initial "on" cells
gridState[6][4] = true;

/* ----- DOM REFERENCES ----- */
const gridContainer = document.getElementById('grid-container');

/* ----- GRID CREATION ----- */
function createGrid(){
    gridContainer.innerHTML = ''; // clear existing
    for(let r = 0; r < ROWS; r++){
        for(let c = 0; c < COLS; c++){
            const cell = document.createElement('div');
            cell.classList.add('cell');
            cell.dataset.row = r;
            cell.dataset.col = c;

            const span = document.createElement('span');
            cell.appendChild(span);

            cell.addEventListener('click', handleCellClick);
            gridContainer.appendChild(cell);
        }
    }
}

/* ----- EVENT HANDLERS ----- */
function handleCellClick(e){
    const {row, col} = e.currentTarget.dataset;
    gridState[row][col] = !gridState[row][col]; // toggle state
    updateGrid();
}

function handleButtonClick(e){
    document.querySelectorAll('.distance-btn').forEach(btn => btn.classList.remove('active'));
    e.currentTarget.classList.add('active');

    switch(e.currentTarget.id){
        case 'btn-manhattan':
            currentDistanceFunction = 'manhattan'; break;
        case 'btn-euclidean':
            currentDistanceFunction = 'euclidean'; break;
        case 'btn-chebyshev':
            currentDistanceFunction = 'chebyshev'; break;
    }
    updateGrid();
}

/* Attach button listeners */
document.getElementById('btn-manhattan').addEventListener('click', handleButtonClick);
document.getElementById('btn-euclidean').addEventListener('click', handleButtonClick);
document.getElementById('btn-chebyshev').addEventListener('click', handleButtonClick);

/* ----- DISTANCE CALCULATIONS ----- */
function computeDistances(){
    const sources = [];
    for(let r = 0; r < ROWS; r++){
        for(let c = 0; c < COLS; c++){
            if(gridState[r][c]) sources.push([r,c]);
        }
    }

    const distances = Array.from({length: ROWS}, () => Array(COLS).fill(Infinity));

    for(let r = 0; r < ROWS; r++){
        for(let c = 0; c < COLS; c++){
            if(gridState[r][c]){
                distances[r][c] = 0;
                continue;
            }

            sources.forEach(([sr, sc]) => {
                let d;
                switch(currentDistanceFunction){
                    case 'manhattan':
                        d = Math.abs(r - sr) + Math.abs(c - sc);
                        break;
                    case 'euclidean':
                        d = (r - sr)**2 + (c - sc)**2; // squared Euclidean
                        break;
                    case 'chebyshev':
                        d = Math.max(Math.abs(r - sr), Math.abs(c - sc));
                        break;
                }
                if(d < distances[r][c]) distances[r][c] = d;
            });
        }
    }
    return distances;
}

/* ----- RENDERING ----- */
function updateGrid(){
    const distances = computeDistances();
    const cells = gridContainer.children;
    let idx = 0;

    for(let r = 0; r < ROWS; r++){
        for(let c = 0; c < COLS; c++){
            const cell = cells[idx++];
            const span = cell.firstChild;

            if(gridState[r][c]){
                cell.classList.add('cell-on');
                cell.classList.remove('cell-off');
                span.textContent = 0;
            }else{
                cell.classList.add('cell-off');
                cell.classList.remove('cell-on');
                const d = distances[r][c];
                span.textContent = (d === Infinity) ? '' : d;
            }
        }
    }
}

/* ----- INITIALIZE ----- */
createGrid();
updateGrid();
</script>
</body>
</html>
