<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<script async id="MathJax-script"
        src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

<!-- --- JAVASCRIPT ----------------------------------------------- -->
<script>
/* ---------- Utility -------------------------------------------------- */
function $(id){return document.getElementById(id);}

/* ---------- Button handling ------------------------------------------ */
function setActive(clickedBtn, groupIds){
    groupIds.forEach(id => $(id).classList.remove('active'));
    clickedBtn.classList.add('active');
    update();
}

/* ---------- Event wiring --------------------------------------------- */
['btn-model-ep','btn-model-ar'].forEach(id=>{
    $(id).addEventListener('click',e=>{
        setActive(e.target,['btn-model-ep','btn-model-ar']);
    });
});
['btn-tmax-low','btn-tmax-high'].forEach(id=>{
    $(id).addEventListener('click',e=>{
        setActive(e.target,['btn-tmax-low','btn-tmax-high']);
    });
});
['slider-n','slider-tmin','slider-tmax','slider-tref','slider-param'].forEach(id=>{
    $(id).addEventListener('input',update);
});

/* ---------- Core update routine -------------------------------------- */
function update(){
    /* ---- read active buttons -------------------------------------- */
    const model        = $('btn-model-ep').classList.contains('active') ? 'EP' : 'AR'; // generating model
    const tmaxLimitLow = $('btn-tmax-low').classList.contains('active');

    /* ---- slider elements ----------------------------------------- */
    const s_n    = $('slider-n');
    const s_tmin = $('slider-tmin');
    const s_tmax = $('slider-tmax');
    const s_tref = $('slider-tref');
    const s_par  = $('slider-param');

    /* ---- enforce Tmax range -------------------------------------- */
    s_tmax.max = tmaxLimitLow ? 1200 : 6000;

    /* ---- correct values if out of bounds ------------------------- */
    if (+s_tmax.value > +s_tmax.max) s_tmax.value = s_tmax.max;
    if (+s_tmin.value >= +s_tmax.value){
        s_tmin.value = +s_tmax.value - 1;
    }
    if (+s_tref.value < +s_tmin.value) s_tref.value = s_tmin.value;
    if (+s_tref.value > +s_tmax.value) s_tref.value = s_tmax.value;

    /* ---- dynamic label for parameter ----------------------------- */
    const lblName = (model==='EP') ? '\\(a_{EP}\\)' : '\\(a_{Ar}\\)';
    $('label-param-name').innerHTML = lblName;

    /* ---- update span values -------------------------------------- */
    $('label-n').textContent           = s_n.value;
    $('label-tmin').textContent        = s_tmin.value;
    $('label-tmax').textContent        = s_tmax.value;
    $('label-tref').textContent        = s_tref.value;
    $('label-param-value').textContent = s_par.value;

    /* ---- generate data ------------------------------------------- */
    const n      = +s_n.value;
    const TminC  = +s_tmin.value;
    const TmaxC  = +s_tmax.value;
    const TrefC  = +s_tref.value;
    const aInput = +s_par.value;

    const tempsC = [];
    for(let i=0;i<n;i++){
        tempsC.push(TminC + i*(TmaxC-TminC)/(n-1));
    }
    const tempsK = tempsC.map(t=>t+273.15);
    const TrefK  = TrefC + 273.15;

    function yFromModel(TK,modelName,a){
        if(modelName==='EP'){
            return (TK/TrefK)*Math.exp(a*(1/TrefK - 1/TK));
        }else{ // AR
            return Math.exp(a*(1/TrefK - 1/TK));
        }
    }
    const yData = tempsK.map(TK=>yFromModel(TK,model,aInput));

    /* ---- fit using the other model ------------------------------- */
    const fitModel  = (model==='EP') ? 'AR' : 'EP';
    let bestA = 0;
    let minRSS = Number.POSITIVE_INFINITY;
    /* grid search */
    const step = 10; // coarse step for speed
    for(let a=0;a<=6000;a+=step){
        let rss=0;
        for(let i=0;i<tempsK.length;i++){
            const yPred = yFromModel(tempsK[i],fitModel,a);
            const diff  = yData[i]-yPred;
            rss += diff*diff;
        }
        if(rss<minRSS){
            minRSS=rss;
            bestA=a;
        }
    }
    /* refine around bestA with step 1 for better precision */
    let bestAref = bestA;
    minRSS = Number.POSITIVE_INFINITY;
    for(let a=Math.max(0,bestA-10);a<=bestA+10;a++){
        let rss=0;
        for(let i=0;i<tempsK.length;i++){
            const yPred = yFromModel(tempsK[i],fitModel,a);
            const diff  = yData[i]-yPred;
            rss += diff*diff;
        }
        if(rss<minRSS){
            minRSS=rss;
            bestAref=a;
        }
    }
    bestA = bestAref;

    /* ---- r^2 calculation ----------------------------------------- */
    const meanY = yData.reduce((p,c)=>p+c,0)/yData.length;
    let ss_tot=0, ss_res=0;
    for(let i=0;i<yData.length;i++){
        ss_tot += Math.pow(yData[i]-meanY,2);
        const yPred=yFromModel(tempsK[i],fitModel,bestA);
        ss_res += Math.pow(yData[i]-yPred,2);
    }
    const r2 = ss_tot===0 ? 1 : 1-ss_res/ss_tot;

    /* ---- update fit result display ------------------------------- */
    const aLabel = (fitModel==='EP') ? '\\(a_{EP}\\)' : '\\(a_{Ar}\\)';
    const resHTML = `${aLabel} = ${bestA.toLocaleString()}\\;\\; r^2 = ${r2.toFixed(3)}`;
    $('fit-results').innerHTML = resHTML;

    /* ---- draw plot ------------------------------------------------ */
    drawPlot(tempsC,yData,TminC,TmaxC,(x)=>yFromModel(x+273.15,fitModel,bestA),model,fitModel);

    /* ---- rerender MathJax ---------------------------------------- */
    if(window.MathJax){MathJax.typeset();}
}

/* ---------- Plotting ------------------------------------------------- */
function drawPlot(xsC, ys, TminC, TmaxC, fitFunc, genModel, fitModel){
    /* data points */
    const trace1 = {
        x: xsC,
        y: ys,
        mode:'markers',
        name:'data',
        marker:{
            color:'blue',
            size:10,
            line:{color:'green',width:2}
        },
        type:'scatter'
    };
    /* fit curve */
    const nFit=200;
    const xFit=[];
    const yFit=[];
    for(let i=0;i<nFit;i++){
        const x = TminC + i*(TmaxC-TminC)/(nFit-1);
        xFit.push(x);
        yFit.push(fitFunc(x));
    }
    const trace2 = {
        x:xFit,
        y:yFit,
        mode:'lines',
        name:`${(fitModel==='EP')?'Eyring–Polanyi':'Arrhenius'} fit`,
        line:{color:'green'}
    };
    /* layout */
    const layout={
        title:`${(fitModel==='EP')?'Eyring–Polanyi':'Arrhenius'} model fit to ${(genModel==='EP')?'Eyring–Polanyi':'Arrhenius'} data`,
        xaxis:{title:'T (°C)'},
        yaxis:{title:'k(T) / k(T_{ref})'},
        margin:{l:70,r:20,t:50,b:60}
    };
    Plotly.newPlot('plot-container',[trace1,trace2],layout,{responsive:true});
}

/* ---------- Initial run ---------------------------------------------- */
window.addEventListener('DOMContentLoaded',update);
</script>
</body>
</html>
