    <div id="canvas-container"></div>

<script>
/* -------------------  STATE  ------------------- */
let n = 6;  // default
const MAX_N = 10;
const MIN_N = 3;
const colors = ["#fefd00","#ee1f24","#0048a1"]; // yellow, red, blue

/* ============  P5  GLOBAL SKETCH  ============ */
let cnv;                 // p5 canvas reference
const canvasW = 900;
const canvasH = 500;

function setup(){
    cnv = createCanvas(canvasW, canvasH);
    cnv.parent("canvas-container");
    noLoop();            // redraw explicitly
    initControls();
    drawScene();
}

function draw(){
    // called via redraw()
    background("#ffffff");
    drawScene();
}

/* Draw the entire scene (both sides) */
function drawScene(){
    stroke("#000000");
    strokeWeight(1);

    /* ---------- Left : Regular 2n-gon ---------- */
    const R = 150;
    const cx = canvasW * 0.25;
    const cy = canvasH * 0.5;

    const angleStep = PI / n;  // 2n sides
    let prev = createVector(cx + R * cos(-PI/2), cy + R * sin(-PI/2)); // P0

    for(let j=1;j<=2*n;j++){
        const ang = j * angleStep - PI/2;
        const curr = createVector(cx + R * cos(ang), cy + R * sin(ang));
        fill(colors[(j-1)%3]);
        triangle(cx, cy, prev.x, prev.y, curr.x, curr.y);
        prev = curr.copy();
    }

    /* ---------- Right : Rectangles from pieces ---------- */
    // triangle base length and apothem
    const s  = 2 * R * sin(angleStep/2); // width of one isosceles triangle base
    const a  = R * cos(angleStep/2);     // apothem

    const rectW = s;
    const rectH = 2 * a;

    const fullRectCount = floor(n/2);
    const startX = canvasW * 0.55;
    const startY = canvasH * 0.5 - rectH/2;
    const gap = 20;

    for(let k=0;k<fullRectCount;k++){
        const x = startX + k*(rectW + gap);
        const y = startY;
        drawFullRectangle(x, y, rectW, rectH);
    }

    if(n % 2 === 1){ // odd -> half-rectangle
        const x = startX + fullRectCount*(rectW + gap);
        const y = startY;
        drawHalfRectangle(x, y, rectW, rectH);
    }
}

/* --------- Helpers to draw rectangles --------- */
function drawFullRectangle(x, y, w, h){
    noStroke();
    // top triangle (yellow)
    fill("#fefd00");
    triangle(x, y, x+w, y, x+w/2, y+h/2);

    // bottom triangle (red)
    fill("#ee1f24");
    triangle(x, y+h, x+w, y+h, x+w/2, y+h/2);

    // left triangle (blue)
    fill("#0048a1");
    triangle(x, y, x, y+h, x+w/2, y+h/2);

    // right triangle (yellow)
    fill("#fefd00");
    triangle(x+w, y, x+w, y+h, x+w/2, y+h/2);

    // outline + internal divisions
    stroke("#000000");
    noFill();
    rect(x, y, w, h);
    line(x, y, x+w/2, y+h/2);
    line(x+w, y, x+w/2, y+h/2);
    line(x, y+h, x+w/2, y+h/2);
    line(x+w, y+h, x+w/2, y+h/2);
}

function drawHalfRectangle(x, y, w, h){
    noStroke();
    // top triangle (blue)
    fill("#0048a1");
    triangle(x, y, x+w, y, x+w/2, y+h/2);

    // bottom triangle (red)
    fill("#ee1f24");
    triangle(x, y+h, x+w, y+h, x+w/2, y+h/2);

    // outline + central division
    stroke("#000000");
    noFill();
    rect(x, y, w, h);
    line(x, y, x+w/2, y+h/2);
    line(x+w, y, x+w/2, y+h/2);
    line(x, y+h, x+w/2, y+h/2);
    line(x+w, y+h, x+w/2, y+h/2);
}

/* -------------  Controls / Events ------------- */
function initControls(){
    // highlight default
    document.getElementById('btn-n-6').classList.add('active');

    for(let value=MIN_N; value<=MAX_N; value++){
        const btn = document.getElementById(`btn-n-${value}`);
        btn.addEventListener('click', ()=>{
            setN(value);
        });
    }
}

function setN(value){
    if(value === n) return; // no change
    n = value;
    document.getElementById('n-display').textContent = n;

    // button highlight
    for(let i=MIN_N;i<=MAX_N;i++){
        document.getElementById(`btn-n-${i}`).classList.remove('active');
    }
    document.getElementById(`btn-n-${n}`).classList.add('active');

    redraw();
}
</script>
</body>
</html>
