<body>

<!--  ----------  controls  ---------- -->
<div id="controls-container">
    <div class="slider-group">
        <label for="slider-q">Q</label>
        <input type="range" id="slider-q" min="1" max="7" step="1" value="2">
        <span id="value-q">2</span>
    </div>
    <div class="slider-group">
        <label for="slider-p">P</label>
        <input type="range" id="slider-p" min="0" max="1" step="0.01" value="1">
        <span id="value-p">1.00</span>
    </div>
    <button id="btn-reset">x</button>
</div>

<!--  ----------  visual panels  ---------- -->
<div id="main-container">
    <div id="workspace-panel">
        <h3>robot workspace</h3>
        <div id="canvas-workspace"></div>
    </div>
    <div id="phase-space-panel">
        <h3 id="phase-space-title">robot phase space</h3>
        <div id="canvas-phase-space"></div>
    </div>
</div>

<script>
/* --------------------------------------------------
   GLOBAL STATE (shared by both sketches)
-------------------------------------------------- */
const WS = 400;                     // canvas width/height
const PS = 400;                     // phase-space canvas
const base = {x:200,y:200};
const link1Len = 75;
const link2Len = 50;
const linkRad   = 10;

let obstacle1 = {x:200,y:100,r:15};
let obstacle2 = {x:200,y:300,r:15};
const defaultObst1 = {...obstacle1};
const defaultObst2 = {...obstacle2};

let startConfig = {q1:-1.5,q2:0.5};
let goalConfig  = {q1: 1.5,q2:1.0};
const defaultStart = {...startConfig};
const defaultGoal  = {...goalConfig};

let path=[];                       // array of {q1,q2}
let cumLen=[];                     // cumulative path length array
let totalPathLen=0;

let Q = 2;                         // resolution exponent
let N = 10 * (2**Q);               // grid cells per dimension
let cellSize = PS / N;

let gridObs;                       // boolean obstacle flags (Uint8Array)
let distField;                     // Int32Array distance
let parent;                        // Int32Array parent index for backtracking

// dragging flags
let isDraggingObstacle1=false, isDraggingObstacle2=false;
let isDraggingStart=false, isDraggingGoal=false;

// slider cache
const sliderQ = document.getElementById('slider-q');
const sliderP = document.getElementById('slider-p');
const valueQ  = document.getElementById('value-q');
const valueP  = document.getElementById('value-p');
const titlePhase = document.getElementById('phase-space-title');

/* --------------------------------------------------
   UTILITY FUNCTIONS
-------------------------------------------------- */
const clamp = (v,a,b)=>Math.max(a,Math.min(b,v));
function idx(i,j){ return i+j*N; }

function mapAngle(i){ return (i / (N-1))*Math.PI*2 - Math.PI; }

function linkEndPoints(q1,q2){
    // returns {p1,p2,p3}
    const p1 = {x:base.x,y:base.y};
    const p2 = {
        x: base.x + link1Len*Math.cos(q1),
        y: base.y + link1Len*Math.sin(q1)
    };
    const p3 = {
        x: p2.x + link2Len*Math.cos(q1+q2),
        y: p2.y + link2Len*Math.sin(q1+q2)
    };
    return {p1,p2,p3};
}
// distance from point c to segment ab
function distPointSeg(cx,cy,ax,ay,bx,by){
    const dx=bx-ax, dy=by-ay;
    const l2=dx*dx+dy*dy;
    if(l2===0) return Math.hypot(cx-ax,cy-ay);
    let t=((cx-ax)*dx+(cy-ay)*dy)/l2;
    t=clamp(t,0,1);
    const px=ax+t*dx, py=ay+t*dy;
    return Math.hypot(cx-px,cy-py);
}
function linkCollidesCircle(ax,ay,bx,by,cx,cy,cr){
    return distPointSeg(cx,cy,ax,ay,bx,by) < (linkRad+cr);
}
function configCollides(q1,q2){
    const {p1,p2,p3}=linkEndPoints(q1,q2);
    const c1 = linkCollidesCircle(p1.x,p1.y,p2.x,p2.y, obstacle1.x,obstacle1.y, obstacle1.r);
    const c2 = linkCollidesCircle(p2.x,p2.y,p3.x,p3.y, obstacle1.x,obstacle1.y, obstacle1.r);
    const c3 = linkCollidesCircle(p1.x,p1.y,p2.x,p2.y, obstacle2.x,obstacle2.y, obstacle2.r);
    const c4 = linkCollidesCircle(p2.x,p2.y,p3.x,p3.y, obstacle2.x,obstacle2.y, obstacle2.r);
    return c1||c2||c3||c4;
}
/* --------------------------------------------------
   C-SPACE COMPUTATION
-------------------------------------------------- */
function buildCspace(){
    N = 10 * (2**Q);
    cellSize = PS / N;
    const size = N*N;
    gridObs  = new Uint8Array(size);
    distField= new Int32Array(size).fill(-1);
    parent   = new Int32Array(size).fill(-1);

    for(let j=0;j<N;j++){
        const q2 = mapAngle(j);
        for(let i=0;i<N;i++){
            const q1 = mapAngle(i);
            if(configCollides(q1,q2)) gridObs[idx(i,j)] = 1;
        }
    }
}
function bfsFromGoal(){
    const qi = Math.round( ( (goalConfig.q1 + Math.PI) / (2*Math.PI) ) * (N-1) );
    const qj = Math.round( ( (goalConfig.q2 + Math.PI) / (2*Math.PI) ) * (N-1) );
    const startIndex = idx(qi,qj);
    if(gridObs[startIndex]) return; // goal in obstacle
    const queue = [];
    distField[startIndex] = 0;
    queue.push(startIndex);

    const dirs=[[1,0],[-1,0],[0,1],[0,-1]];
    while(queue.length){
        const cur = queue.shift();
        const x = cur%N;
        const y = (cur-x)/N;

        for(const d of dirs){
            const nx = x+d[0], ny = y+d[1];
            if(nx<0||ny<0||nx>=N||ny>=N) continue;
            const ni = idx(nx,ny);
            if(gridObs[ni]) continue;
            if(distField[ni]!==-1) continue;
            distField[ni] = distField[cur]+1;
            parent[ni] = cur;
            queue.push(ni);
        }
    }
}
function backtrackPath(){
    const si = Math.round( ( (startConfig.q1 + Math.PI) / (2*Math.PI) ) * (N-1) );
    const sj = Math.round( ( (startConfig.q2 + Math.PI) / (2*Math.PI) ) * (N-1) );
    let cur = idx(si,sj);
    if(distField[cur]===-1){ path=[]; totalPathLen=0; return;}
    const rev = [];
    rev.push(cur);
    while(distField[cur]!==0){
        cur = parent[cur];
        if(cur===-1){path=[];totalPathLen=0;return;}
        rev.push(cur);
    }
    rev.reverse();
    path = rev.map(k=>{
        const i = k%N, j = (k-i)/N;
        return {q1:mapAngle(i), q2:mapAngle(j)};
    });
    // cumulative length
    cumLen=[0];
    totalPathLen=0;
    for(let idxP=1;idxP<path.length;idxP++){
        const d = Math.hypot(path[idxP].q1 - path[idxP-1].q1,
                             path[idxP].q2 - path[idxP-1].q2);
        totalPathLen += d;
        cumLen.push(totalPathLen);
    }
}
/* --------------------------------------------------
   MAIN RECOMPUTE
-------------------------------------------------- */
function recomputeAll(){
    buildCspace();
    bfsFromGoal();
    backtrackPath();
    titlePhase.textContent = `robot phase space, path length = ${totalPathLen.toFixed(2)} rads`;
    phaseSketch.redraw();
}
function recomputePathOnly(){
    backtrackPath();
    titlePhase.textContent = `robot phase space, path length = ${totalPathLen.toFixed(2)} rads`;
    phaseSketch.redraw();
}

/* --------------------------------------------------
   SLIDER / BUTTON LISTENERS
-------------------------------------------------- */
sliderQ.addEventListener('input',()=>{
    Q = parseInt(sliderQ.value);
    valueQ.textContent = Q;
    recomputeAll();
});
sliderP.addEventListener('input',()=>{
    valueP.textContent = parseFloat(sliderP.value).toFixed(2);
    // just redraw robot + marker
    workspaceSketch.redraw();
    phaseSketch.redraw();
});
document.getElementById('btn-reset').addEventListener('click',()=>{
    obstacle1 = {...defaultObst1};
    obstacle2 = {...defaultObst2};
    startConfig= {...defaultStart};
    goalConfig = {...defaultGoal};
    sliderQ.value = 2; sliderP.value = 1;
    valueQ.textContent='2'; valueP.textContent='1.00';
    Q=2;
    recomputeAll();
});

/* --------------------------------------------------
   WORKSPACE P5 SKETCH
-------------------------------------------------- */
const workspaceSketch = new p5(s=>{
s.setup=()=>{
    s.createCanvas(WS,WS).parent('canvas-workspace');
    s.noLoop();
    recomputeAll();
};
s.draw=()=>{
    s.clear();
    // background
    s.background('#F0F0F0');
    // obstacles
    drawObstacle(obstacle1);
    drawObstacle(obstacle2);
    // robot
    const cfg = interpConfig(sliderP.value);
    drawRobot(cfg.q1,cfg.q2);
};
function interpConfig(t){
    // t between 0 and 1
    if(path.length===0) return startConfig;
    const L = totalPathLen;
    const target = t*L;
    let i=1;
    while(i<cumLen.length && cumLen[i]<target) i++;
    if(i>=cumLen.length) return path[path.length-1];
    const prevLen = cumLen[i-1];
    const segLen = cumLen[i]-cumLen[i-1];
    const alpha = segLen===0?0:(target-prevLen)/segLen;
    return {
        q1: path[i-1].q1 + alpha*(path[i].q1 - path[i-1].q1),
        q2: path[i-1].q2 + alpha*(path[i].q2 - path[i-1].q2)
    };
}
function drawRobot(q1,q2){
    s.push();
    s.translate(base.x,base.y);
    s.stroke(0); s.strokeWeight(2);
    s.fill(150);
    // first link
    s.push();
        s.rotate(q1);
        s.rect(0,-linkRad,link1Len,linkRad*2,linkRad);
        // angle indicator theta1
        s.noFill(); s.stroke('#888'); s.strokeWeight(1);
        s.arc(0,0,50,50,0,q1);
    s.pop();
    // joint
    const p2 = {x: base.x + link1Len*Math.cos(q1),
                y: base.y + link1Len*Math.sin(q1)};
    // second link
    s.push();
        s.translate(p2.x-base.x,p2.y-base.y);
        s.rotate(q1+q2);
        s.rect(0,-linkRad,link2Len,linkRad*2,linkRad);
        s.fill(0); s.noStroke();
        s.textAlign(s.CENTER,s.CENTER);
        s.textSize(10);
        s.text("Robot Arm",link2Len/2,0);
        // theta2 indicator
        s.noFill(); s.stroke('#888'); s.strokeWeight(1);
        s.arc(0,0,40,40,0,q2);
    s.pop();
    // joints
    s.noStroke();
    s.fill('#444');
    s.circle(0,0,14);
    s.circle(p2.x-base.x,p2.y-base.y,14);
    s.pop();
}
function drawObstacle(o){
    s.push();
    s.fill('#c57d56');
    s.stroke('#663300');
    s.strokeWeight(2);
    s.circle(o.x,o.y,o.r*2);
    // pattern: concentric
    s.noFill();
    s.stroke('#663300');
    for(let r=o.r-4;r>2;r-=4) s.circle(o.x,o.y,2*r);
    s.pop();
}
/* ------------- dragging obstacles -------------*/
s.mousePressed=()=>{
    if(s.dist(s.mouseX,s.mouseY,obstacle1.x,obstacle1.y)<obstacle1.r) isDraggingObstacle1=true;
    else if(s.dist(s.mouseX,s.mouseY,obstacle2.x,obstacle2.y)<obstacle2.r) isDraggingObstacle2=true;
};
s.mouseDragged=()=>{
    if(isDraggingObstacle1){
        obstacle1.x = clamp(s.mouseX,obstacle1.r,WS-obstacle1.r);
        obstacle1.y = clamp(s.mouseY,obstacle1.r,WS-obstacle1.r);
        recomputeAll();
    }else if(isDraggingObstacle2){
        obstacle2.x = clamp(s.mouseX,obstacle2.r,WS-obstacle2.r);
        obstacle2.y = clamp(s.mouseY,obstacle2.r,WS-obstacle2.r);
        recomputeAll();
    }
};
s.mouseReleased=()=>{
    isDraggingObstacle1=false;
    isDraggingObstacle2=false;
};
},'canvas-workspace');

/* --------------------------------------------------
   PHASE SPACE P5 SKETCH
-------------------------------------------------- */
const phaseSketch = new p5(s=>{
s.setup=()=>{
    s.createCanvas(PS,PS).parent('canvas-phase-space');
    s.noLoop();
};
s.draw=()=>{
    s.clear();
    s.background(255);
    // draw grid cells (very coarse optimization: skip if N>300)
    if(N<=300){
        for(let j=0;j<N;j++){
            for(let i=0;i<N;i++){
                const k=idx(i,j);
                if(gridObs[k]){
                    s.noStroke();
                    s.fill('#CC0000');
                    s.rect(i*cellSize,j*cellSize,cellSize,cellSize);
                }else if(distField[k]!==-1){
                    const d = distField[k];
                    const col = 255 - Math.min(200,d*4);
                    s.noStroke();
                    s.fill(col);
                    s.rect(i*cellSize,j*cellSize,cellSize,cellSize);
                }
            }
        }
    }
    // path
    if(path.length>0){
        s.stroke('#00FF00'); s.strokeWeight(3);
        s.noFill();
        s.beginShape();
        for(const pt of path){
            const i = Math.round(( (pt.q1 + Math.PI) / (2*Math.PI) ) * (N-1));
            const j = Math.round(( (pt.q2 + Math.PI) / (2*Math.PI) ) * (N-1));
            const cx = i*cellSize + cellSize/2;
            const cy = j*cellSize + cellSize/2;
            s.vertex(cx,cy);
        }
        s.endShape();
    }
    // start & goal markers
    drawMarker(startConfig,true);
    drawMarker(goalConfig,false);
    // moving marker along path according to P
    const cfg = interpConfig(sliderP.value);
    drawMoving(cfg);
};
function drawMarker(cfg,isStart){
    const i = Math.round(( (cfg.q1 + Math.PI) / (2*Math.PI) ) * (N-1));
    const j = Math.round(( (cfg.q2 + Math.PI) / (2*Math.PI) ) * (N-1));
    const cx = i*cellSize + cellSize/2;
    const cy = j*cellSize + cellSize/2;
    s.push();
    s.fill(isStart?'#00AA00':'#008800');
    s.stroke(255);
    s.strokeWeight(2);
    s.circle(cx,cy,cellSize*0.6);
    s.line(cx-cellSize*0.25,cy,cx+cellSize*0.25,cy);
    s.line(cx,cy-cellSize*0.25,cx,cy+cellSize*0.25);
    s.pop();
}
function drawMoving(cfg){
    const i = (cfg.q1 + Math.PI) / (2*Math.PI) * (N-1);
    const j = (cfg.q2 + Math.PI) / (2*Math.PI) * (N-1);
    const cx = i*cellSize + cellSize/2;
    const cy = j*cellSize + cellSize/2;
    s.push();
    s.fill('#00FFFF');
    s.noStroke();
    s.circle(cx,cy,cellSize*0.4);
    s.pop();
}
function interpConfig(t){
    if(path.length===0) return startConfig;
    const L = totalPathLen;
    const target = t*L;
    let i=1;
    while(i<cumLen.length && cumLen[i]<target) i++;
    if(i>=cumLen.length) return path[path.length-1];
    const prevLen = cumLen[i-1];
    const segLen = cumLen[i]-cumLen[i-1];
    const alpha = segLen===0?0:(target-prevLen)/segLen;
    return {
        q1: path[i-1].q1 + alpha*(path[i].q1 - path[i-1].q1),
        q2: path[i-1].q2 + alpha*(path[i].q2 - path[i-1].q2)
    };
}
/* ------------- dragging start/goal -------------*/
s.mousePressed=()=>{
    const hitStart = hitMarker(startConfig,s.mouseX,s.mouseY);
    const hitGoal  = hitMarker(goalConfig ,s.mouseX,s.mouseY);
    if(hitStart) isDraggingStart=true;
    else if(hitGoal) isDraggingGoal=true;
};
s.mouseDragged=()=>{
    if(!isDraggingStart && !isDraggingGoal) return;
    const col = Math.floor(s.mouseX / cellSize);
    const row = Math.floor(s.mouseY / cellSize);
    if(col<0||row<0||col>=N||row>=N) return;
    const q1 = mapAngle(col), q2 = mapAngle(row);
    if(isDraggingStart){
        if(gridObs[idx(col,row)]) return; // cannot place in obstacle
        startConfig.q1 = q1; startConfig.q2=q2;
        recomputePathOnly();
    }else if(isDraggingGoal){
        if(gridObs[idx(col,row)]) return;
        goalConfig.q1 = q1; goalConfig.q2=q2;
        recomputeAll();
    }
};
s.mouseReleased=()=>{
    isDraggingStart=false; isDraggingGoal=false;
};
function hitMarker(cfg,x,y){
    const i = Math.round(( (cfg.q1 + Math.PI) / (2*Math.PI) ) * (N-1));
    const j = Math.round(( (cfg.q2 + Math.PI) / (2*Math.PI) ) * (N-1));
    const cx = i*cellSize + cellSize/2;
    const cy = j*cellSize + cellSize/2;
    return s.dist(x,y,cx,cy) < cellSize*0.4;
}
},'canvas-phase-space');

/* --------------------------------------------------
   INITIAL DRAW
-------------------------------------------------- */
workspaceSketch.redraw();
phaseSketch.redraw();
</script>
</body>
</html>
