<body>
<div id="main-container">
    <!-- ------------------- CONTROL PANEL ------------------- -->
    <div id="control-panel">
        <!-- Problem Definition -->
        <h4>problem</h4>
        <div>
            <label>dimension</label>
            <button id="btn-2d" class="active">2D</button>
            <button id="btn-3d">3D</button>
        </div>
        <div>
            <label>random seed: <span id="seed-value">525</span></label>
            <input type="range" id="slider-seed" min="0" max="1000" step="1" value="525">
        </div>
        <div>
            <label>number: <span id="number-value">40</span></label>
            <input type="range" id="slider-number" min="5" max="150" step="1" value="40">
        </div>

