            <button id="btn-play">►</button>
            <button id="btn-pause" disabled>||</button>
            <button id="btn-reset">|◄</button>
        </div>
    </div>

    <!-- ------------------- VISUALIZATION ------------------- -->
    <div id="canvas-container"></div>
</div>

<script>
/* --------------------------------------------------
   Global State Variables
-------------------------------------------------- */
let dimension = '2D';          // '2D' | '3D'
let algorithm = 'prim';        // 'prim' | 'kruskal'
let animationState = 'stopped';// 'stopped' | 'playing' | 'paused'
let seed   = 525;
let nPoints= 40;

/* Data containers */
let points = [];               // Array of p5.Vector
let mstEdges = [];             // Full edge sequence produced by chosen algorithm
let currentStep = 0;           // Current edge index already drawn

/* p5 instance */
let sketch = null;

/* --------------------------------------------------
   UI Elements
-------------------------------------------------- */
const btn2D       = document.getElementById('btn-2d');
const btn3D       = document.getElementById('btn-3d');
const sliderSeed  = document.getElementById('slider-seed');
const sliderNum   = document.getElementById('slider-number');
const seedSpan    = document.getElementById('seed-value');
const numSpan     = document.getElementById('number-value');
const btnPrim     = document.getElementById('btn-prim');
const btnKruskal  = document.getElementById('btn-kruskal');
const btnPlay     = document.getElementById('btn-play');
const btnPause    = document.getElementById('btn-pause');
const btnReset    = document.getElementById('btn-reset');

/* --------------------------------------------------
   Utility / Helper Functions
-------------------------------------------------- */

function setActiveButton(activeBtn, otherBtn){
    activeBtn.classList.add('active');
    otherBtn.classList.remove('active');
}

function enable(el, enabled=true){
    el.disabled = !enabled;
}

function fullReset(){
    animationState = 'stopped';
    currentStep = 0;
    generatePoints();
    precomputeMST();
    enable(btnPlay,true);
    enable(btnPause,false);
    enable(btnReset,true);
}

function partialReset(){   // when only algorithm changes
    animationState = 'stopped';
    currentStep = 0;
    precomputeMST();
    enable(btnPlay,true);
    enable(btnPause,false);
    enable(btnReset,true);
}

/* --------------------------------------------------
   UI Event Listeners
-------------------------------------------------- */

// Dimension buttons
btn2D.onclick = ()=>{ if(dimension!=='2D'){ dimension='2D'; setActiveButton(btn2D,btn3D); fullReset(); recreateSketch(); } };
btn3D.onclick = ()=>{ if(dimension!=='3D'){ dimension='3D'; setActiveButton(btn3D,btn2D); fullReset(); recreateSketch(); } };

// Seed slider
sliderSeed.oninput = ()=>{
    seed = +sliderSeed.value;
    seedSpan.textContent = seed;
    fullReset();
};

// Number slider
sliderNum.oninput = ()=>{
    nPoints = +sliderNum.value;
    numSpan.textContent = nPoints;
    fullReset();
};

// Algorithm buttons
btnPrim.onclick = ()=>{ if(algorithm!=='prim'){ algorithm='prim'; setActiveButton(btnPrim,btnKruskal); partialReset(); } };
btnKruskal.onclick = ()=>{ if(algorithm!=='kruskal'){ algorithm='kruskal'; setActiveButton(btnKruskal,btnPrim); partialReset(); } };

// Process buttons
btnPlay.onclick = ()=>{
    if(animationState!=='playing'){
        animationState = 'playing';
        enable(btnPlay,false);
        enable(btnPause,true);
    }
};
btnPause.onclick = ()=>{
    if(animationState==='playing'){
        animationState = 'paused';
        enable(btnPlay,true);
        enable(btnPause,false);
    }
};
btnReset.onclick = ()=>{
    animationState = 'stopped';
    currentStep   = 0;
    enable(btnPlay,true);
    enable(btnPause,false);
};

/* --------------------------------------------------
   Geometry / Algorithm Helpers
-------------------------------------------------- */
function generatePoints(){
    points = [];
    const container = document.getElementById('canvas-container');
    const w = container.clientWidth;
    const h = container.clientHeight;
    const border = 25;

    if(sketch){
        sketch.randomSeed(seed);
        if(dimension==='2D'){
            for(let i=0;i<nPoints;i++){
                let x = sketch.random(border, w-border);
                let y = sketch.random(border, h-border);
                points.push(sketch.createVector(x, y, 0));
            }
        }else{ // 3D
            let size = 400;
            for(let i=0;i<nPoints;i++){
                let x = sketch.random(-size/2 + border,  size/2 - border);
                let y = sketch.random(-size/2 + border,  size/2 - border);
                let z = sketch.random(-size/2 + border,  size/2 - border);
                points.push(sketch.createVector(x,y,z));
            }
        }
    }
}

/* ---- Prim ---- */
function computePrim(){
    const N = points.length;
    const visited = Array(N).fill(false);
    visited[0] = true;
    let edges = [];

    while(edges.length < N-1){
        let best = null, bestA=-1, bestB=-1, bestD=Infinity;
        for(let a=0;a<N;a++){
            if(!visited[a]) continue;
            for(let b=0;b<N;b++){
                if(visited[b]) continue;
                let d = points[a].dist(points[b]);
                if(d < bestD){
                    bestD=d; bestA=a; bestB=b;
                }
            }
        }
        visited[bestB]=true;
        edges.push({a:bestA,b:bestB});
    }
    return edges;
}

/* ---- Kruskal ---- */
function computeKruskal(){
    const N = points.length;
    // Union-Find
    const parent = Array(N).fill(0).map((_,i)=>i);
    function find(x){
        while(parent[x]!==x){ parent[x]=parent[parent[x]]; x=parent[x]; }
        return x;
    }
    function unite(x,y){
        x=find(x); y=find(y);
        if(x!==y) parent[x]=y;
    }
    // collect all edges
    let all = [];
    for(let i=0;i<N;i++){
        for(let j=i+1;j<N;j++){
            all.push({a:i,b:j,d:points[i].dist(points[j])});
        }
    }
    all.sort((e1,e2)=>e1.d-e2.d);
    let edges=[];
    for(let e of all){
        if(find(e.a)!==find(e.b)){
            unite(e.a,e.b);
            edges.push({a:e.a,b:e.b});
            if(edges.length===N-1) break;
        }
    }
    return edges;
}

function precomputeMST(){
    if(points.length===0) return;
    if(algorithm==='prim'){
        mstEdges = computePrim();
    }else{
        mstEdges = computeKruskal();
    }
}

/* --------------------------------------------------
   p5 Sketch Creation / Re-Creation
-------------------------------------------------- */
function recreateSketch(){
    if(sketch){
        sketch.remove();  // dispose previous canvas
    }
    sketch = new p5( (p) => {
        let tickDelay = 10;

        p.setup = function(){
            let parent = document.getElementById('canvas-container');
            let canvas;
            if(dimension==='3D'){
                canvas = p.createCanvas(parent.clientWidth, parent.clientHeight, p.WEBGL);
            }else{
                canvas = p.createCanvas(parent.clientWidth, parent.clientHeight);
            }
            canvas.parent(parent);
            p.frameRate(60);
            generatePoints();
            precomputeMST();
        };

        p.windowResized = function(){
            let parent = document.getElementById('canvas-container');
            p.resizeCanvas(parent.clientWidth, parent.clientHeight);
        };

        p.draw = function(){
            p.background('#E0E0E0');

            // 3D helpers
            if(dimension==='3D'){
                p.orbitControl();
                p.noFill();
                p.stroke(180);
                p.box(420); // wireframe cube
            }

            // Draw already accepted edges
            drawEdges(p);

            // Draw points
            drawPoints(p);

            // Handle animation
            if(animationState==='playing' && p.frameCount % tickDelay ===0){
                if(currentStep < mstEdges.length){
                    currentStep++;
                }else{
                    // finished
                    animationState='stopped';
                    enable(btnPlay,false);
                    enable(btnPause,false);
                    enable(btnReset,false);
                }
            }
        };
    });
}

/* --------------------------------------------------
   Drawing Helpers (use inside sketch)
-------------------------------------------------- */
function drawPoints(p){
    p.noStroke();
    p.fill('blue');
    if(dimension==='2D'){
        for(let v of points){
            p.circle(v.x, v.y, 8);
        }
    }else{
        for(let v of points){
            p.push();
            p.translate(v.x, v.y, v.z);
            p.sphere(6);
            p.pop();
        }
    }
}

function drawEdges(p){
    p.stroke('red');
    p.strokeWeight(2);
    for(let i=0;i<currentStep;i++){
        let e = mstEdges[i];
        let A = points[e.a];
        let B = points[e.b];
        if(dimension==='2D'){
            p.line(A.x, A.y, B.x, B.y);
        }else{
            p.line(A.x, A.y, A.z, B.x, B.y, B.z);
        }
    }
}

/* --------------------------------------------------
   Initial Bootstrapping
-------------------------------------------------- */
window.addEventListener('load', ()=>{
    recreateSketch(); // create initial canvas & visuals
});
</script>

</body>
</html>
