<body>

<div id="main-container">

    <div id="decorative-plus-btn">+</div>

    <div id="control-panel">

        <!-- Method Selection -->
        <div class="section">
            <label for="select-method">method</label>
            <select id="select-method">
                <option value="estimation">estimation</option>
                <option value="place values">place values</option>
            </select>
        </div>

        <hr>

        <!-- First number control -->
        <div class="section">
            <label>first number</label>
            <div class="slider-row">
                <label for="slider-num1">numerical value</label>
                <input type="range" id="slider-num1" min="0" max="20" step="0.1" value="1.1">
                <span>+</span>
            </div>
        </div>

        <hr>

        <!-- Second number control -->
        <div class="section">
            <label>multiply by</label>

            <div class="toggle-row">
                <label>integers only</label>
                <button id="btn-int-yes" class="active">yes</button>
                <button id="btn-int-no" class="inactive">no</button>
            </div>

            <div class="slider-row">
                <label for="slider-num2">numerical value</label>
                <input type="range" id="slider-num2" min="0" max="20" step="1" value="2">
                <span>+</span>
            </div>
        </div>

    </div><!-- /control-panel -->

    <!-- Output -->
    <div id="output-display">
        <!-- filled by JS -->
    </div>

</div><!-- /main-container -->


<script>
/* ===== Helper functions ===== */

/* returns number of decimal places */
function decimalPlaces(num){
    const s = num.toString();
    if(!s.includes('.')) return 0;
    return s.split('.')[1].length;
}

/* Convert num to integer representation without decimals (as string) */
function intString(num){
    const dec = decimalPlaces(num);
    if(dec===0) return num.toString();
    const factor = Math.pow(10,dec);
    return Math.round(num*factor).toString();
}

/* 10^decimalPlaces factor  */
function intFactor(num){
    const dec = decimalPlaces(num);
    return Math.pow(10,dec);
}

/* Rounded integer for estimation */
function rounded(num){ return Math.round(num); }

/* ===== Main logic ===== */
const selectMethod = document.getElementById('select-method');
const sliderNum1   = document.getElementById('slider-num1');
const sliderNum2   = document.getElementById('slider-num2');
const btnIntYes = document.getElementById('btn-int-yes');
const btnIntNo  = document.getElementById('btn-int-no');
const outputDisplay = document.getElementById('output-display');

/* Toggle button styling helper */
function setToggleState(integersOnly){
    if(integersOnly){
        btnIntYes.classList.add('active');
        btnIntYes.classList.remove('inactive');
        btnIntNo.classList.add('inactive');
        btnIntNo.classList.remove('active');
    }else{
        btnIntNo.classList.add('active');
        btnIntNo.classList.remove('inactive');
        btnIntYes.classList.add('inactive');
        btnIntYes.classList.remove('active');
    }
}

/* ===== Update Display ===== */
function updateDisplay(){
    const method = selectMethod.value;
    const num1 = parseFloat(sliderNum1.value);
    const num2 = parseFloat(sliderNum2.value);

    const int1Str = intString(num1);
    const int2Str = intString(num2);
    const factor1 = intFactor(num1);
    const factor2 = intFactor(num2);
    const productInts = (parseInt(int1Str) * parseInt(int2Str)).toString();
    const productFactors = factor1 * factor2;

    const exactResult = +(num1 * num2).toFixed(8); // trimmed result
    const lines = [];

    if(method === 'estimation'){
        lines.push(`start with: ${num1} × ${num2}`);
        lines.push(`convert to: ${int1Str}. × ${int2Str}. = ${productInts}.`);
        lines.push(`estimate as: ${rounded(num1)} × ${rounded(num2)} = ${rounded(num1)*rounded(num2)}`);
        lines.push(`result is: ${exactResult}`);
    }else if(method === 'place values'){
        lines.push(`start with: ${num1} × ${num2}`);

        /* Conditional rewrite */
        if(factor1 !== 1 || factor2 !== 1){
            const part1 = factor1 === 1 ? `${int1Str}.` : `(${int1Str}. ÷ ${factor1})`;
            const part2 = factor2 === 1 ? `${int2Str}.` : `(${int2Str}. ÷ ${factor2})`;
            lines.push(`rewrite as: ${part1} × ${part2}`);

            let numerator = `${int1Str}. × ${int2Str}.`;
            let denominator = `${factor1} × ${factor2}`;
            if(factor1 === 1) denominator = `${factor2}`;
            if(factor2 === 1) denominator = `${factor1}`;
            if(factor1 === 1 && factor2 === 1) denominator = `1`;

            lines.push(`= (${numerator}) ÷ (${denominator})`);
            lines.push(`= ${productInts}. ÷ ${productFactors}`);
        }

        lines.push(`= ${exactResult}`);
    }

    /* Inject into DOM */
    outputDisplay.innerHTML = lines.map(t => `<p>${t}</p>`).join('');
}

/* ===== Event Handling ===== */
selectMethod.addEventListener('change', updateDisplay);
sliderNum1.addEventListener('input', updateDisplay);
sliderNum2.addEventListener('input', updateDisplay);

btnIntYes.addEventListener('click', ()=> {
    sliderNum2.step = 1;
    /* round current value to nearest integer */
    sliderNum2.value = Math.round(parseFloat(sliderNum2.value));
    setToggleState(true);
    updateDisplay();
});
btnIntNo.addEventListener('click', ()=> {
    sliderNum2.step = 0.1;
    setToggleState(false);
    updateDisplay();
});

/* Initial render */
updateDisplay();
</script>

</body>
</html>
