        #three-js-canvas-container canvas{width:100%!important;height:100%!important;display:block;}

        #description-area{padding:6px 10px;background:#f0f0f0;display:flex;justify-content:space-between;}
        #label-left,#label-right{margin:0;font-weight:bold;}
    </style>
</head>
<body>
<div id="demo-container">
    <!-- CONTROL PANEL -------------------------------------------------------->
    <div id="control-panel">
        <select id="select-vibration-mode">
            <option value="symmetric-stretch" selected>symmetric stretch</option>
            <option value="asymmetric-stretch">stretch (triply degenerate)</option>
            <option value="bending-e">bend (doubly degenerate)</option>
            <option value="bending-f2">bend (triply degenerate)</option>
            <option value="ir-spectrum">IR spectrum</option>
        </select>
        <span>run</span>
        <button id="btn-play">►</button>
        <button id="btn-pause">||</button>
        <button id="btn-reset">|◄</button>
    </div>

    <!-- VISUALIZATION AREA --------------------------------------------------->
    <div id="visualization-area">
        <div id="three-js-canvas-container"></div>
        <div id="plotly-chart-container"></div>
    </div>

    <!-- DESCRIPTION AREA ----------------------------------------------------->
    <div id="description-area">
        <p id="label-left" style="color:blue;">stretching A₁ Raman active&nbsp;2916&nbsp;cm⁻¹</p>
        <p id="label-right" style="color:blue;">no change in the molecular dipole moment</p>
    </div>
</div>

<script>
/* ---------------------------------- GLOBALS ---------------------------------- */
let renderer, scene, sceneVectors;
let cameraMol, cameraVec;
let controlsMol, controlsVec;
let moleculeGroup, hydrogens=[], bonds=[];
let arrowHelpers=[], netArrow;
let animId=null, time=0;
const AMP=0.25, OMEGA=2*Math.PI; // rad/s  (illustrative)

const container3D=document.getElementById("three-js-canvas-container");
const plotContainer=document.getElementById("plotly-chart-container");
const labelLeft=document.getElementById("label-left");
const labelRight=document.getElementById("label-right");
const playBtn=document.getElementById("btn-play");
const pauseBtn=document.getElementById("btn-pause");
const resetBtn=document.getElementById("btn-reset");
const selectMode=document.getElementById("select-vibration-mode");

/* ---------------------------------- INIT ---------------------------------- */
init3D();
setupMolecule();
setupVectors();
resize();
window.addEventListener('resize',resize);
drawFrame(); // initial static render
container3D.style.display="block";

/* ---------------------------------- CONTROLS ---------------------------------- */
playBtn.addEventListener('click',()=>{
    if(animId) return;
    lastTS=performance.now();
    animate();
    playBtn.disabled=true;
});
pauseBtn.addEventListener('click',()=>{
    if(animId){cancelAnimationFrame(animId); animId=null; playBtn.disabled=false;}
});
resetBtn.addEventListener('click',resetPositions);

selectMode.addEventListener('change',updateMode);
updateMode(); // set initial

/* ---------------------------------- FUNCTIONS ---------------------------------- */
function init3D(){
    renderer=new THREE.WebGLRenderer({antialias:true,alpha:true});
    renderer.setPixelRatio(window.devicePixelRatio);
    container3D.appendChild(renderer.domElement);

    // two scenes (share renderer) -----------------------------------------
    scene=new THREE.Scene();
    sceneVectors=new THREE.Scene();

    // Cameras
    cameraMol=new THREE.PerspectiveCamera(35,1,0.1,100);
    cameraMol.position.set(4,4,4);
    cameraMol.lookAt(0,0,0);

    cameraVec=new THREE.PerspectiveCamera(35,1,0.1,100);
    cameraVec.position.set(4,4,4);
    cameraVec.lookAt(0,0,0);

    // Controls
    controlsMol=new THREE.OrbitControls(cameraMol,renderer.domElement);
    controlsVec=new THREE.OrbitControls(cameraVec,renderer.domElement);

    // Lights
    const amb=new THREE.AmbientLight(0xffffff,0.6);
    scene.add(amb);
    sceneVectors.add(amb.clone());

    const dir=new THREE.DirectionalLight(0xffffff,0.6);
    dir.position.set(5,5,5);
    scene.add(dir);
    sceneVectors.add(dir.clone());
}

function setupMolecule(){
    moleculeGroup=new THREE.Group();
    scene.add(moleculeGroup);

    // Carbon
    const cGeo=new THREE.SphereGeometry(0.4,32,32);
    const cMat=new THREE.MeshLambertMaterial({color:0x222222});
    const carbon=new THREE.Mesh(cGeo,cMat);
    moleculeGroup.add(carbon);

    // Hydrogen data
    const hPositions=[
        new THREE.Vector3( 1, 1, 1),
        new THREE.Vector3( 1,-1,-1),
        new THREE.Vector3(-1, 1,-1),
        new THREE.Vector3(-1,-1, 1)
    ];
    const hGeo=new THREE.SphereGeometry(0.25,32,32);
    const hMat=new THREE.MeshLambertMaterial({color:0xddddff});

    for(let i=0;i<4;i++){
        const h=new THREE.Mesh(hGeo,hMat);
        h.position.copy(hPositions[i]);
        moleculeGroup.add(h);
        hydrogens.push({mesh:h,initial:h.position.clone()});

        // Bond
        const cyl=new THREE.Mesh(new THREE.CylinderGeometry(0.075,0.075,1,8), new THREE.MeshBasicMaterial({color:0x888888}));
        moleculeGroup.add(cyl);
        bonds.push(cyl);
        updateCylinder(cyl,new THREE.Vector3(0,0,0),h.position);
    }
}

function setupVectors(){
    // individual arrows (C->H)
    for(let i=0;i<4;i++){
        const dir=hydrogens[i].mesh.position.clone().normalize();
        const arrow=new THREE.ArrowHelper(dir, new THREE.Vector3(0,0,0), 1, 0x0000ff, 0.15,0.1);
        sceneVectors.add(arrow);
        arrowHelpers.push(arrow);
    }
    // net arrow
    netArrow=new THREE.ArrowHelper(new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,0),1.2,0xff0000,0.2,0.12);
    sceneVectors.add(netArrow);
    netArrow.visible=false;
}

/* Cylinder update from start->end */
function updateCylinder(cyl,start,end){
    const dir=new THREE.Vector3().subVectors(end,start);
    const len=dir.length();
    cyl.position.copy(start).addScaledVector(dir,0.5);
    cyl.scale.set(1,len,1);
    cyl.quaternion.setFromUnitVectors(new THREE.Vector3(0,1,0), dir.clone().normalize());
}

/* ----------- MODES DESCRIPTION & COLOR ---------------- */
const MODE_INFO={
    "symmetric-stretch":{
        left:{txt:"stretching A₁ Raman active 2916 cm⁻¹", color:"blue"},
        right:{txt:"no change in the molecular dipole moment", color:"blue"},
        ir:false
    },
    "asymmetric-stretch":{
        left:{txt:"stretching F₂ IR active 3019 cm⁻¹", color:"red"},
        right:{txt:"change in the molecular dipole moment", color:"red"},
        ir:true
    },
    "bending-e":{
        left:{txt:"bending E Raman active 1534 cm⁻¹", color:"blue"},
        right:{txt:"no change in the molecular dipole moment", color:"blue"},
        ir:false
    },
    "bending-f2":{
        left:{txt:"bending F₂ IR active 1306 cm⁻¹", color:"red"},
        right:{txt:"change in the molecular dipole moment", color:"red"},
        ir:true
    }
};

/* ------------- UPDATE MODE ---------------- */
function updateMode(){
    const mode=selectMode.value;

    if(mode==="ir-spectrum"){
        // hide 3D, show plot
        container3D.style.display="none";
        document.getElementById("description-area").style.display="none";
        plotContainer.style.display="block";
        playBtn.disabled=true; pauseBtn.disabled=true; resetBtn.disabled=true;
        if(!plotContainer._plotted){drawSpectrum();}
        // stop animation if running
        if(animId){cancelAnimationFrame(animId);animId=null; playBtn.disabled=false;}
        return;
    }

    // vibrational modes
    plotContainer.style.display="none";
    container3D.style.display="block";
    document.getElementById("description-area").style.display="flex";
    playBtn.disabled=false; pauseBtn.disabled=false; resetBtn.disabled=false;

    // stop animation & reset positions
    if(animId){cancelAnimationFrame(animId);animId=null; playBtn.disabled=false;}
    resetPositions();

    // update labels
    labelLeft.textContent=MODE_INFO[mode].left.txt;
    labelRight.textContent=MODE_INFO[mode].right.txt;
    labelLeft.style.color=MODE_INFO[mode].left.color;
    labelRight.style.color=MODE_INFO[mode].right.color;

    // recolor individual arrows (blue IR-inactive, red IR-active)
    const col=MODE_INFO[mode].ir?0xff0000:0x0000ff;
    arrowHelpers.forEach(a=>a.setColor(new THREE.Color(col)));
    netArrow.visible=false; // will be toggled in frames
    drawFrame();
}

/* ------------- ANIMATION LOOP ---------------- */
let lastTS=0;
function animate(ts){
    animId=requestAnimationFrame(animate);
    const dt=(ts-lastTS)/1000; // seconds
    lastTS=ts;
    time+=dt;
    updateAtoms();
    updateVectors();
    drawFrame();
}

function updateAtoms(){
    const mode=selectMode.value;
    const phase=Math.sin(OMEGA*time);

    if(mode==="symmetric-stretch"){ // radial in/out
        hydrogens.forEach((h,i)=>{
            const disp=h.initial.clone().normalize().multiplyScalar(AMP*phase);
            h.mesh.position.copy(h.initial).add(disp);
        });
    }
    else if(mode==="asymmetric-stretch"){
        // index 0 outward, 1 inward, others slight
        hydrogens.forEach((h,i)=>{
            let factor=0;
            if(i===0)factor=1;
            else if(i===1)factor=-1;
            else factor=0.3*(-1)**i;
            const disp=h.initial.clone().normalize().multiplyScalar(AMP*phase*factor);
            h.mesh.position.copy(h.initial).add(disp);
        });
    }
    else if(mode==="bending-e"){
        hydrogens.forEach((h,i)=>{
            // rotate around an axis perpendicular to vector (simple impl)
            const axis=new THREE.Vector3(0,1,0).cross(h.initial).normalize();
            const angle=0.3*phase;
            h.mesh.position.copy(h.initial.clone().applyAxisAngle(axis,angle));
        });
    }
    else if(mode==="bending-f2"){
        hydrogens.forEach((h,i)=>{
            const axis=new THREE.Vector3(1,0,0).cross(h.initial).normalize();
            const angle=((i%2===0)?1:-1)*0.35*phase;
            h.mesh.position.copy(h.initial.clone().applyAxisAngle(axis,angle));
        });
    }

    // update bonds
    bonds.forEach((cyl,i)=>{
        updateCylinder(cyl,new THREE.Vector3(0,0,0),hydrogens[i].mesh.position);
    });
}

function updateVectors(){
    // individual arrows
    let net=new THREE.Vector3(0,0,0);
    arrowHelpers.forEach((a,i)=>{
        const dir=hydrogens[i].mesh.position.clone().normalize();
        const len=hydrogens[i].mesh.position.length()*0.8;
        a.setDirection(dir);
        a.setLength(len,0.15,0.1);
        net.add(dir.clone().multiplyScalar(len));
    });

    // net arrow
    const mode=selectMode.value;
    const active=MODE_INFO[mode].ir;
    const mag=net.length();
    if(active && mag>0.05){
        netArrow.visible=true;
        netArrow.setDirection(net.clone().normalize());
        netArrow.setLength(mag,0.2,0.12);
    }else{
        netArrow.visible=false;
    }
}

function drawFrame(){
    const width=container3D.clientWidth;
    const height=container3D.clientHeight;
    renderer.setSize(width,height,false);

    // Left viewport (molecule)
    const w2=width/2;
    renderer.setViewport(0,0,w2,height);
    renderer.setScissor(0,0,w2,height);
    renderer.setScissorTest(true);
    renderer.setClearColor(0xffffff,0);
    controlsMol.update();
    renderer.render(scene,cameraMol);

    // Right viewport (vectors)
    renderer.setViewport(w2,0,w2,height);
    renderer.setScissor(w2,0,w2,height);
    renderer.setScissorTest(true);
    controlsVec.update();
    renderer.render(sceneVectors,cameraVec);

    renderer.setScissorTest(false);
}

function resetPositions(){
    time=0;
    hydrogens.forEach((h,i)=>h.mesh.position.copy(h.initial));
    bonds.forEach((cyl,i)=>{
        updateCylinder(cyl,new THREE.Vector3(0,0,0),hydrogens[i].mesh.position);
    });
    netArrow.visible=false;
    drawFrame();
    if(animId){cancelAnimationFrame(animId);animId=null; playBtn.disabled=false;}
}

function resize(){
    drawFrame();
}

/* --------------------------- IR SPECTRUM PLOT ------------------------------ */
function drawSpectrum(){
    // generate baseline
    const x=[], y=[];
    for(let wn=400; wn<=4000; wn+=4){
        x.push(wn);
        y.push(0.95 + (Math.random()-0.5)*0.02);
    }
    function gaussian(x,mu,sigma){return Math.exp(-0.5*((x-mu)/sigma)**2);}
    // subtract peaks
    const peaks=[
        {pos:1306, sigma:30, depth:0.8},
        {pos:3019, sigma:25, depth:0.85}
    ];
    x.forEach((xn,idx)=>{
        peaks.forEach(p=>{
            y[idx]-=p.depth*gaussian(xn,p.pos,p.sigma);
        });
    });
    // little fine structure
    for(let i=0;i<30;i++){
        const mu= (Math.random()<0.5?1260:2980)+Math.random()*100;
        const sigma=5+Math.random()*5;
        const depth=0.05+Math.random()*0.07;
        x.forEach((xn,idx)=>{ y[idx]-=depth*gaussian(xn,mu,sigma);});
    }
    const trace={x:x,y:y,mode:'lines',line:{color:'black'}};

    // make molecule images (offscreen)
    const imgStretch=dataURLForStaticScene('asymmetric-stretch');
    const imgBend   =dataURLForStaticScene('bending-f2');

    const layout={
        margin:{l:60,r:20,t:30,b:60},
        xaxis:{title:"wavenumber (cm⁻¹)",range:[400,4000]},
        yaxis:{title:"Transmittance",range:[0,1.1]},
        images:[
            {source:imgStretch,x:3100,y:0.2,xanchor:"center",yanchor:"bottom",sizex:250,sizey:0.4,opacity:1,layer:"above"},
            {source:imgBend,x:1400,y:0.2,xanchor:"center",yanchor:"bottom",sizex:250,sizey:0.4,opacity:1,layer:"above"}
        ],
        annotations:[
            {
                x:1306,y:0.15,
                ax:-40,ay:-50,
                text:"ν F₂ 1306 cm⁻¹",
                arrowcolor:"red",
                font:{color:"red"}
            },
            {
                x:3019,y:0.15,
                ax:60,ay:-50,
                text:"ν F₂ 3019 cm⁻¹",
                arrowcolor:"red",
                font:{color:"red"}
            }
        ]
    };

    Plotly.newPlot(plotContainer,[trace],layout,{responsive:true});
    plotContainer._plotted=true;
}

/* create small static scene and return dataURL */
function dataURLForStaticScene(mode){
    const tmpRenderer=new THREE.WebGLRenderer({antialias:true});
    const size=200;
    tmpRenderer.setSize(size,size);
    const sceneS=new THREE.Scene();
    const cameraS=new THREE.PerspectiveCamera(35,1,0.1,100);
    cameraS.position.set(3,3,3);cameraS.lookAt(0,0,0);
    const amb=new THREE.AmbientLight(0xffffff,0.8);
    sceneS.add(amb);
    const dir=new THREE.DirectionalLight(0xffffff,0.8);dir.position.set(5,5,5);sceneS.add(dir);

    // carbon
    const c=new THREE.Mesh(new THREE.SphereGeometry(0.4), new THREE.MeshLambertMaterial({color:0x222222}));
    sceneS.add(c);
    const hGeo=new THREE.SphereGeometry(0.25);
    const hMat=new THREE.MeshLambertMaterial({color:0xddddff});
    const positions=[
        new THREE.Vector3( 1, 1, 1),
        new THREE.Vector3( 1,-1,-1),
        new THREE.Vector3(-1, 1,-1),
        new THREE.Vector3(-1,-1, 1)
    ];
    positions.forEach(p=>{
        const h=new THREE.Mesh(hGeo,hMat);
        h.position.copy(p);
        sceneS.add(h);
        // bond
        const cyl=new THREE.Mesh(new THREE.CylinderGeometry(0.075,0.075,1,8), new THREE.MeshBasicMaterial({color:0x888888}));
        updateCylinder(cyl,new THREE.Vector3(0,0,0),p);
        sceneS.add(cyl);
    });

    // quick mode specific tweak (stretch arrows etc.)
    tmpRenderer.render(sceneS,cameraS);
    const dataURL=tmpRenderer.domElement.toDataURL();
    tmpRenderer.dispose();
    return dataURL;
}
</script>
</body>
</html>
