<body>
<div id="main-container">
    <!-- ----------- Control Panel ----------- -->
    <div id="control-panel">
        <label for="slider-time">time (seconds):</label>
        <input id="slider-time" type="range" min="0" max="100" step="0.1" value="9.8">
        <span id="span-time-value">9.8</span>
        <button id="btn-reset">+</button>
    </div>

    <!-- ----------- Material Visualization ----------- -->
    <canvas id="canvas-material-bar" width="600" height="100"></canvas>

    <!-- ----------- Plot Area ----------- -->
    <div id="div-plot"></div>
</div>

<script>
/* --------- Helper / Model functions --------- */
function stefanDistance(t){
    return 0.155*Math.sqrt(t);   // returns mm
}

/* --------- Canvas drawing --------- */
function drawMaterialBar(t){
    const canvas = document.getElementById("canvas-material-bar");
    const ctx     = canvas.getContext("2d");

    // Clear
    ctx.clearRect(0,0,canvas.width,canvas.height);

    const totalLenMM = 16;               // 0–16 mm visualised
    const glassLenMM = 6;                // fixed
    const s          = stefanDistance(t);
    const scale      = canvas.width/totalLenMM;  // px per mm
    const barHeight  = 40;               // height of rectangles
    const barY       = 20;               // top y

    // Glass gradient
    const glassGrad = ctx.createLinearGradient(0,0,glassLenMM*scale,0);
    glassGrad.addColorStop(0,"#FF0000"); // hot red
    glassGrad.addColorStop(1,"#FFFFFF"); // white
    ctx.fillStyle = glassGrad;
    ctx.fillRect(0,barY, glassLenMM*scale, barHeight);

    // Melted water
    ctx.fillStyle="#00BFFF";
    ctx.fillRect(glassLenMM*scale, barY, s*scale, barHeight);

    // Ice block
    ctx.fillStyle="#003366";
    const iceStart = (glassLenMM+s)*scale;
    ctx.fillRect(iceStart, barY, canvas.width-iceStart, barHeight);

    // Axis
    const axisY = barY+barHeight+15;
    ctx.strokeStyle="#000000";
    ctx.beginPath();
    ctx.moveTo(0,axisY);
    ctx.lineTo(canvas.width,axisY);
    ctx.stroke();

    ctx.fillStyle="#000";
    ctx.textAlign="center";
    ctx.textBaseline="top";
    const tickMM=[0,5,10,15];
    tickMM.forEach(mm=>{
        const x = mm*scale;
        ctx.beginPath();
        ctx.moveTo(x,axisY);
        ctx.lineTo(x,axisY-5);
        ctx.stroke();
        ctx.fillText(mm.toString(),x,axisY+2);
    });
}

/* --------- Plotly initialisation --------- */
function initPlot(initialT){
    const tVals=[];
    const sVals=[];
    for(let t=0;t<=100;t+=0.5){
        tVals.push(t);
        sVals.push(stefanDistance(t));
    }

    const trace={
        x:tVals,
        y:sVals,
        mode:'lines',
        line:{color:'#FFA500',width:3},
        name:'s(t)'
    };

    const initShape = indicatorShape(initialT);

    const layout={
        title:'Stefan distance versus time',
        xaxis:{title:'time (s)', range:[0,100]},
        yaxis:{title:'distance (mm)', range:[0,1.6]},
        shapes:[initShape],
        margin:{t:40,r:10,b:50,l:60}
    };

    Plotly.newPlot('div-plot',[trace],layout,{responsive:true});
}

/* Build a Plotly line-shape for the indicator */
function indicatorShape(t){
    return {
        type:'line',
        x0:t,
        x1:t,
        y0:0,
        y1:stefanDistance(t),
        xref:'x',
        yref:'y',
        line:{color:'#000',width:2}
    };
}

/* --------- Master update --------- */
function updateAll(t){
    t = Number(t);
    document.getElementById("span-time-value").textContent = t.toFixed(1);

    // Canvas
    drawMaterialBar(t);

    // Plot
    const newShape = indicatorShape(t);
    Plotly.relayout('div-plot', {'shapes[0]': newShape});
}

/* --------- Event wiring --------- */
document.addEventListener("DOMContentLoaded",()=>{
    const slider = document.getElementById("slider-time");
    const resetBtn = document.getElementById("btn-reset");

    // Initial Plot & Canvas
    initPlot(slider.value);
    drawMaterialBar(slider.value);

    // Slider listener
    slider.addEventListener("input", (e)=>{
        updateAll(e.target.value);
    });

    // Reset button
    resetBtn.addEventListener("click", ()=>{
        slider.value = 0;
        // Trigger input event manually
        slider.dispatchEvent(new Event('input', {bubbles:true}));
    });
});
</script>
</body>
</html>
