<body>
  <div id="app-container">
    <!-- CONTROL PANEL -->
    <div id="control-panel">
      <label for="date-slider">date</label>
      <input type="range" id="date-slider" min="0" max="364" step="1" value="340">
      <span id="date-display">Friday, 07 December</span>
      <button id="today-button">today</button>
    </div>

    <!-- VISUALIZATION -->
    <div id="visualization-panel">
      <div id="clocks-container">
        <!-- Local Clock -->
        <div id="local-clock-group" class="clock-group">
          <h2>Local Time</h2>
          <div id="local-clock-wrapper" class="clock-wrapper">
            <canvas id="local-clock-canvas" width="280" height="280"></canvas>
            <div id="local-time-digital" class="digital-display">--:--:--</div>
          </div>
        </div>

        <!-- Solar Clock -->
        <div id="solar-clock-group" class="clock-group">
          <h2>Solar Time</h2>
          <div id="solar-clock-wrapper" class="clock-wrapper">
            <canvas id="solar-clock-canvas" width="280" height="280"></canvas>
            <div id="solar-time-digital" class="digital-display">--:--:--</div>
          </div>
        </div>
      </div>

      <!-- Offset Display -->
      <div id="offset-display">
        offset of solar time from local time: -46 minutes
      </div>
    </div>
  </div>

<script>
/* =======================================================================
   HELPERS
======================================================================= */
function pad(n){ return (n<10?'0':'') + n; }

/* Convert day-of-year (0-364) to a Date object in the current year */
function dayOfYearToDate(doy){
  const year = new Date().getFullYear();
  const date = new Date(year, 0);          // Jan 1st
  date.setDate(doy + 1);                   // add days
  return date;
}

/* Produce readable date like "Friday, 07 December" */
function niceDateString(doy){
  const d = dayOfYearToDate(doy);
  const options = { weekday:"long", day:"2-digit", month:"long" };
  return d.toLocaleDateString(undefined, options);
}

/* =======================================================================
   GEOGRAPHICAL POSITION
======================================================================= */
let userLongitude = 0;     // default Greenwich
let userLatitude  = 51.5;  // default Greenwich (unused presently)

if(navigator.geolocation){
  navigator.geolocation.getCurrentPosition(
    pos=>{
      userLongitude = pos.coords.longitude;
      userLatitude  = pos.coords.latitude;
    },
    _err=>{
      console.warn("Using default location (Greenwich) because geolocation failed/denied.");
    }
  );
}

/* =======================================================================
   CLOCK DRAWING (Using classic Canvas API – p5.js is loaded but not needed
   for direct drawing on the pre-existing <canvas> elements.)
======================================================================= */
const localCanvas  = document.getElementById("local-clock-canvas");
const solarCanvas  = document.getElementById("solar-clock-canvas");
const localCtx     = localCanvas.getContext("2d");
const solarCtx     = solarCanvas.getContext("2d");

const RADIUS = localCanvas.width/2;

/* Draw a single analog clock ------------------------------------------*/
function drawClock(ctx, timeObj, faceColor){
  ctx.clearRect(0,0, ctx.canvas.width, ctx.canvas.height);
  ctx.save();
  ctx.translate(RADIUS, RADIUS);

  /* clock face */
  ctx.beginPath();
  ctx.arc(0,0, RADIUS-2, 0, Math.PI*2);
  ctx.fillStyle = faceColor;
  ctx.fill();
  ctx.lineWidth = 4;
  ctx.strokeStyle = "#000";
  ctx.stroke();

  /* hour marks */
  ctx.lineWidth = 4;
  for(let i=0;i<12;i++){
    ctx.beginPath();
    ctx.rotate(Math.PI/6);
    ctx.moveTo(0, -RADIUS+10);
    ctx.lineTo(0, -RADIUS+30);
    ctx.stroke();
  }

  /* minute marks */
  ctx.lineWidth = 2;
  ctx.rotate(0);
  for(let i=0;i<60;i++){
    if(i%5!==0){
      ctx.beginPath();
      ctx.moveTo(0, -RADIUS+15);
      ctx.lineTo(0, -RADIUS+25);
      ctx.stroke();
    }
    ctx.rotate(Math.PI/30);
  }

  ctx.rotate(0);

  /* numbers */
  ctx.font = "18px sans-serif";
  ctx.textAlign = "center";
  ctx.textBaseline = "middle";
  for(let num=1; num<=12; num++){
    const angle = num*Math.PI/6 - Math.PI/2;
    const x = Math.cos(angle)*(RADIUS-45);
    const y = Math.sin(angle)*(RADIUS-45);
    ctx.fillStyle="#000";
    ctx.fillText(num.toString(), x, y);
  }

  /* hands */
  const hours   = timeObj.getHours()%12;
  const minutes = timeObj.getMinutes();
  const seconds = timeObj.getSeconds();

  // Hour
  const hourAngle = ((hours + minutes/60) * Math.PI/6) - Math.PI/2;
  ctx.lineWidth = 6;
  ctx.strokeStyle="#000";
  ctx.beginPath();
  ctx.moveTo(0,0);
  ctx.lineTo(Math.cos(hourAngle)* (RADIUS*0.5),
             Math.sin(hourAngle)* (RADIUS*0.5));
  ctx.stroke();

  // Minute
  const minuteAngle = ((minutes + seconds/60) * Math.PI/30) - Math.PI/2;
  ctx.lineWidth = 4;
  ctx.beginPath();
  ctx.moveTo(0,0);
  ctx.lineTo(Math.cos(minuteAngle)* (RADIUS*0.75),
             Math.sin(minuteAngle)* (RADIUS*0.75));
  ctx.stroke();

  // Second
  const secondAngle = (seconds * Math.PI/30) - Math.PI/2;
  ctx.lineWidth = 2;
  ctx.strokeStyle="#d00";
  ctx.beginPath();
  ctx.moveTo(0,0);
  ctx.lineTo(Math.cos(secondAngle)* (RADIUS*0.85),
             Math.sin(secondAngle)* (RADIUS*0.85));
  ctx.stroke();

  /* center pin */
  ctx.fillStyle="#000";
  ctx.beginPath();
  ctx.arc(0,0,5,0,Math.PI*2);
  ctx.fill();

  ctx.restore();
}

/* =======================================================================
   SOLAR TIME CALCULATION
======================================================================= */
function calcSolarTime(localDate, doy){
  /* Equation of Time */
  const B = (360/365.24)*(doy-81) * Math.PI/180; // in radians
  const EoT = 9.87*Math.sin(2*B) - 7.53*Math.cos(B) - 1.5*Math.sin(B); // minutes

  /* Standard meridian for local time zone */
  const tzOffsetHours = -localDate.getTimezoneOffset()/60; // e.g. CET = +1
  const standardMeridian = 15 * tzOffsetHours;             // degrees

  /* Time correction factor */
  const timeOffsetMinutes = EoT + 4*(standardMeridian - userLongitude);

  /* UTC milliseconds */
  const utcMillis = localDate.getTime() + localDate.getTimezoneOffset()*60000;

  const solarMillis = utcMillis + timeOffsetMinutes*60000;
  return new Date(solarMillis);
}

/* =======================================================================
   UI & INTERACTION
======================================================================= */
const dateSlider   = document.getElementById("date-slider");
const dateDisplay  = document.getElementById("date-display");
const todayButton  = document.getElementById("today-button");
const offsetDiv    = document.getElementById("offset-display");

function updateDateDisplay(){
  dateDisplay.textContent = niceDateString( parseInt(dateSlider.value,10) );
}

/* Today button -> set slider to current day-of-year -------------------*/
todayButton.addEventListener("click", ()=>{
  const now = new Date();
  const start = new Date(now.getFullYear(),0,0);
  const diff  = now - start;
  const oneDay= 1000*60*60*24;
  const doy   = Math.floor(diff/oneDay);
  dateSlider.value = doy;
  updateDateDisplay();
});

/* Slider change -> update date text (solar clock will update automatically) */
dateSlider.addEventListener("input", updateDateDisplay);

/* INITIALIZE display text */
updateDateDisplay();

/* =======================================================================
   MAIN UPDATE LOOP (every second)
======================================================================= */
function mainTick(){
  const localTime = new Date();
  const doy       = parseInt(dateSlider.value,10);
  const solarTime = calcSolarTime(localTime, doy);

  /* Draw clocks */
  drawClock(localCtx,  localTime, RADIUS, "#E6E6FA"); // lavender
  drawClock(solarCtx,  solarTime, RADIUS, "#FAFAD2"); // light goldenrod yellow

  /* Digital displays */
  document.getElementById("local-time-digital").textContent =
    pad(localTime.getHours())+":"+pad(localTime.getMinutes())+":"+pad(localTime.getSeconds());

  document.getElementById("solar-time-digital").textContent =
    pad(solarTime.getHours())+":"+pad(solarTime.getMinutes())+":"+pad(solarTime.getSeconds());

  /* Offset display (rounded minutes) */
  const differenceMin = Math.round((solarTime - localTime)/60000);
  offsetDiv.textContent = `offset of solar time from local time: ${differenceMin} minute${Math.abs(differenceMin)!==1?'s':''}`;
}

/* Start interval */
mainTick();                   // call once immediately
setInterval(mainTick,1000);   // then every second
</script>
</body>
</html>
