            flex:1;
            position:relative;
        }
        canvas{
            position:absolute;
            left:0;top:0;
        }
    </style>
</head>
<body>
    <!-- Main container for the demo -->
    <div id="demo-container">
        <!-- Control panel section -->
        <div id="control-panel">
            <div>
                <label for="input-steps">steps</label>
                <input type="number" id="input-steps" min="0" max="6" step="1" value="2">
            </div>
            <div>
                <label for="checkbox-zoom">zoom</label>
                <input type="checkbox" id="checkbox-zoom">
            </div>
            <div>
                <label for="select-color">color</label>
                <select id="select-color">
                    <option value="DarkRainbow" selected>DarkRainbow</option>
                    <option value="SiennaTones">SiennaTones</option>
                    <option value="RustTones">RustTones</option>
                    <option value="FuchsiaTones">FuchsiaTones</option>
                    <option value="Grays">Grays</option>
                </select>
            </div>
        </div>
        <!-- Visualization canvas container -->
        <div id="canvas-container"></div>
    </div>

<script>
/* ------------------------------------------------------------------
   GLOBAL STATE (synced with the HTML controls)
------------------------------------------------------------------ */
const palettes = {
    DarkRainbow  : ['#1E4363','#2E6341'],
    SiennaTones  : ['#A0522D','#CD853F','#D2B48C'],
    RustTones    : ['#B7410E','#000033','#FFFFFF'],
    FuchsiaTones : ['#C71585','#000000','#FFFFFF'],
    Grays        : ['#404040','#808080','#C0C0C0']
};

let ctrlSteps, ctrlZoom, ctrlColor;
let activePalette = palettes.DarkRainbow;

/* ------------------------------------------------------------------
   p5.js SKETCH
------------------------------------------------------------------ */
let canvas;

function setup(){
    // create canvas that fills the canvas-container DIV
    const container = document.getElementById('canvas-container');
    const rect      = container.getBoundingClientRect();
    canvas          = createCanvas(rect.width, rect.height);
    canvas.parent('canvas-container');

    angleMode(DEGREES);
    noLoop(); // draw() runs only when we explicitly call redraw()

    // cache control elements
    ctrlSteps = document.getElementById('input-steps');
    ctrlZoom  = document.getElementById('checkbox-zoom');
    ctrlColor = document.getElementById('select-color');

    // attach events that trigger a redraw
    ctrlSteps.addEventListener('input', ()=>{ redraw(); });
    ctrlZoom .addEventListener('change', ()=>{ redraw(); });
    ctrlColor.addEventListener('change', ()=>{
        activePalette = palettes[ ctrlColor.value ];
        redraw();
    });

    // resize canvas if window size changes
    window.addEventListener('resize', resizeCanvasToContainer);

    // initial palette already set; initial render
    redraw();
}

function resizeCanvasToContainer(){
    const container = document.getElementById('canvas-container');
    const rect      = container.getBoundingClientRect();
    resizeCanvas(rect.width, rect.height);
    redraw(); // keep image crisp after resize
}

function draw(){
    background(255);

    const steps = +ctrlSteps.value;
    const zoom  = ctrlZoom.checked;

    push();
    translate(width/2, height/2); // origin in the centre

    /* -----------------------------------------------------------
       Determine a base size so that the entire fractal fits.
       The constant 0.42 was chosen empirically so that the
       snow-flake shaped island roughly fills the canvas.
    ----------------------------------------------------------- */
    const minSide = min(width, height);
    const baseSize = minSide * 0.42 / pow(sqrt(7), steps);

    /* zoomed-in view magnifies by (sqrt(7))^steps as requested */
    if(zoom){
        scale( pow(sqrt(7), steps) );
    }

    /* -----------------------------------------------------------
       Draw the initial hexagon made of 6 rhombi
    ----------------------------------------------------------- */
    for(let i=0; i<6; i++){
        const angle60 = i * 60;                // direction from centre
        const orient  = (i%3)*120;             // rhombus orientation 0/120/240
        const v       = p5.Vector.fromAngle(radians(angle60), baseSize);
        drawTiling(steps, v.x, v.y, baseSize, orient);
    }

    /* Optional central hexagon for specific palettes */
    if( (ctrlColor.value==='RustTones' || ctrlColor.value==='FuchsiaTones') &&
        activePalette.length>=3){
        noStroke();
        fill(activePalette[2]);
        const r = baseSize * 0.57;
        beginShape();
        for(let a=0; a<360; a+=60){
            const vx = r * cos(a);
            const vy = r * sin(a);
            vertex(vx, vy);
        }
        endShape(CLOSE);
    }

    pop();
}

/* ------------------------------------------------------------------
   Recursive subdivision function
------------------------------------------------------------------ */
function drawTiling(level, cx, cy, size, angleDeg){
    if(level===0){
        drawRhombus(cx, cy, size, angleDeg);
        return;
    }

    const newSize = size / sqrt(7);

    // main direction vectors of the parent rhombus
    const v1 = p5.Vector.fromAngle(radians(angleDeg),           size);
    const v2 = p5.Vector.fromAngle(radians(angleDeg + 60), size);

    const children = [];

    // child 0 : centre
    children.push( createVector(cx, cy) );

    // 4 children along v1 and v2 axes (±)
    children.push( p5.Vector.add( createVector(cx, cy), p5.Vector.mult(v1, 0.5) ) );
    children.push( p5.Vector.add( createVector(cx, cy), p5.Vector.mult(v1,-0.5) ) );
    children.push( p5.Vector.add( createVector(cx, cy), p5.Vector.mult(v2, 0.5) ) );
    children.push( p5.Vector.add( createVector(cx, cy), p5.Vector.mult(v2,-0.5) ) );

    // 2 children on the long diagonals (v1+v2 and -(v1+v2))
    children.push( p5.Vector.add( createVector(cx, cy), p5.Vector.mult( p5.Vector.add(v1,v2), 0.25) ) );
    children.push( p5.Vector.add( createVector(cx, cy), p5.Vector.mult( p5.Vector.add(v1,v2),-0.25) ) );

    // recurse
    for(const vec of children){
        drawTiling(level-1, vec.x, vec.y, newSize, angleDeg);
    }
}

/* ------------------------------------------------------------------
   Draw a single rhombus, its fill determined by orientation
------------------------------------------------------------------ */
function drawRhombus(cx, cy, size, angleDeg){
    const v1 = p5.Vector.fromAngle(radians(angleDeg),           size);
    const v2 = p5.Vector.fromAngle(radians(angleDeg + 60), size);

    // vertices
    const p0 = p5.Vector.add( p5.Vector.add(createVector(cx,cy), v1), v2);
    const p1 = p5.Vector.add( p5.Vector.add(createVector(cx,cy), v1), p5.Vector.mult(v2,-1));
    const p2 = p5.Vector.add( p5.Vector.add(createVector(cx,cy), p5.Vector.mult(v1,-1)), p5.Vector.mult(v2,-1));
    const p3 = p5.Vector.add( p5.Vector.add(createVector(cx,cy), p5.Vector.mult(v1,-1)), v2);

    // colour based on orientation
    const orientIndex = (( (angleDeg%360)+360 ) % 360) / 120; // 0,1,2
    const col = activePalette[ Math.round(orientIndex) % activePalette.length ];

    noStroke();
    fill(col);
    beginShape();
        vertex(p0.x, p0.y);
        vertex(p1.x, p1.y);
        vertex(p2.x, p2.y);
        vertex(p3.x, p3.y);
    endShape(CLOSE);

    // faint diagonal line (optional aesthetic)
    stroke(255,80);
    strokeWeight(size*0.05);
    line(p0.x, p0.y, p2.x, p2.y);
}

</script>
</body>
</html>
