<body>
<div id="container">

    <!-- ------------- CONTROL PANEL ------------- -->
    <div id="controls">
        <button id="btn-diagram" class="active">diagram</button>
        <button id="btn-phases">phases</button>

        <label><input type="checkbox" id="check-grid-lines"> grid lines</label>
        <label><input type="checkbox" id="check-tie-lines" checked> tie lines</label>
        <label><input type="checkbox" id="check-alpha-phase" checked> alpha phase</label>
        <label><input type="checkbox" id="check-beta-phase" checked> beta phase</label>
    </div>

    <!-- ------------- VISUALIZATION AREA ------------- -->
    <div id="visualization">
        <div id="canvas-container"></div>

        <div id="info-box-main"  class="info-box"></div>
        <div id="info-box-alpha" class="info-box" style="display:none;"></div>
        <div id="info-box-beta"  class="info-box" style="display:none;"></div>
    </div>

</div>

<script>
/* ------------------  GLOBAL STATE ------------------ */
let currentView     = 'diagram';

let showGridLines   = false;
let showTieLines    = true;
let showAlphaPhase  = true;
let showBetaPhase   = true;

let userPoint = {xA:0.42, xB:0.47, xC:0.11};

const phaseEnvelopeAlpha = [
    {xA:0.05, xB:0.90, xC:0.05},
    {xA:0.15, xB:0.77, xC:0.08},
    {xA:0.25, xB:0.63, xC:0.12},
    {xA:0.35, xB:0.50, xC:0.15},
    {xA:0.40, xB:0.44, xC:0.16}   // plait
];
const phaseEnvelopeBeta = [
    {xA:0.40, xB:0.44, xC:0.16},  // plait
    {xA:0.46, xB:0.34, xC:0.20},
    {xA:0.55, xB:0.20, xC:0.25},
    {xA:0.65, xB:0.10, xC:0.25},
    {xA:0.75, xB:0.05, xC:0.20}
];
const tieLines = [
    {alpha:{xA:0.05, xB:0.90, xC:0.05}, beta:{xA:0.75, xB:0.05, xC:0.20}},
    {alpha:{xA:0.15, xB:0.77, xC:0.08}, beta:{xA:0.65, xB:0.10, xC:0.25}},
    {alpha:{xA:0.25, xB:0.63, xC:0.12}, beta:{xA:0.55, xB:0.20, xC:0.25}},
    {alpha:{xA:0.35, xB:0.50, xC:0.15}, beta:{xA:0.46, xB:0.34, xC:0.20}},
    {alpha:{xA:0.40, xB:0.44, xC:0.16}, beta:{xA:0.40, xB:0.44, xC:0.16}} // plait
];

/* --------------  UTILITY FUNCTIONS --------------- */
let PA, PB, PC;          // vertices of triangle (p5.Vector)
function baryToCart(b){
    return p5.Vector.add(
            p5.Vector.add(PA.copy().mult(b.xA), PB.copy().mult(b.xB)),
            PC.copy().mult(b.xC));
}
function cartToBary(x,y){
    const x1=PA.x, y1=PA.y,
          x2=PB.x, y2=PB.y,
          x3=PC.x, y3=PC.y;

    const den = ( (y2 - y3)*(x1 - x3) + (x3 - x2)*(y1 - y3) );
    const xA = ( (y2 - y3)*(x - x3) + (x3 - x2)*(y - y3) ) / den;
    const xB = ( (y3 - y1)*(x - x3) + (x1 - x3)*(y - y3) ) / den;
    const xC = 1 - xA - xB;
    return {xA, xB, xC};
}
function isInsideTriangle(b){
    return b.xA>=0 && b.xB>=0 && b.xC>=0;
}
function polyToPoints(alphaArr,betaArr){
    const pts=[];
    alphaArr.forEach(p=>pts.push(baryToCart(p)));
    for(let i=betaArr.length-1;i>=0;i--){
        pts.push(baryToCart(betaArr[i]));
    }
    return pts;
}
function pointInPolygon(x,y,polygon){
    let inside=false;
    for(let i=0,j=polygon.length-1; i<polygon.length; j=i++){
        const xi=polygon[i].x, yi=polygon[i].y;
        const xj=polygon[j].x, yj=polygon[j].y;
        const intersect = ((yi>y)!=(yj>y)) && (x < (xj - xi)*(y - yi)/(yj - yi)+xi);
        if(intersect) inside = !inside;
    }
    return inside;
}
function distance(a,b){
    return Math.sqrt( (a.xA-b.xA)**2 + (a.xB-b.xB)**2 + (a.xC-b.xC)**2 );
}
function interpolateTieLine(comp){
    // Very simple nearest-tie-line approach
    let nearest = tieLines[0];
    let minD = distance(comp, averageComp(nearest));
    tieLines.forEach(t=>{
        const d = distance(comp, averageComp(t));
        if(d < minD){minD=d; nearest=t;}
    });
    return nearest;
}
function averageComp(tie){
    return {xA:(tie.alpha.xA+tie.beta.xA)/2,
            xB:(tie.alpha.xB+tie.beta.xB)/2,
            xC:(tie.alpha.xC+tie.beta.xC)/2};
}

/* -------------- INFO BOX HANDLING ---------------- */
const boxMain  = document.getElementById("info-box-main");
const boxAlpha = document.getElementById("info-box-alpha");
const boxBeta  = document.getElementById("info-box-beta");

function formatFrac(val){
    return Math.round(val*1000)/1000;
}
function updateInfoBoxes(){
    // MAIN
    boxMain.innerHTML = `
        <strong>mass fractions</strong><br>
        \\(x_A = ${formatFrac(userPoint.xA)}\\)<br>
        \\(x_B = ${formatFrac(userPoint.xB)}\\)<br>
        \\(x_C = ${formatFrac(userPoint.xC)}\\)
    `;
    // Position near user point
    const cp = baryToCart(userPoint);
    boxMain.style.left = (cp.x+15)+"px";
    boxMain.style.top  = (cp.y-15)+"px";

    // Determine if in 2-phase region
    const insideTwoPhase = pointInPolygon(cp.x,cp.y,polyToPoints(phaseEnvelopeAlpha,phaseEnvelopeBeta));

    if(insideTwoPhase){
        const tie = interpolateTieLine(userPoint);
        // Alpha box
        boxAlpha.innerHTML = `
            <strong>alpha phase</strong><br>
            \\(x_A^{\\alpha} = ${formatFrac(tie.alpha.xA)}\\)<br>
            \\(x_B^{\\alpha} = ${formatFrac(tie.alpha.xB)}\\)<br>
            \\(x_C^{\\alpha} = ${formatFrac(tie.alpha.xC)}\\)
        `;
        const pAlpha = baryToCart(tie.alpha);
        boxAlpha.style.left = (pAlpha.x+15)+"px";
        boxAlpha.style.top  = (pAlpha.y-15)+"px";
        boxAlpha.style.display = (showAlphaPhase && currentView==='diagram') ? 'block':'none';

        // Beta box
        boxBeta.innerHTML = `
            <strong>beta phase</strong><br>
            \\(x_A^{\\beta} = ${formatFrac(tie.beta.xA)}\\)<br>
            \\(x_B^{\\beta} = ${formatFrac(tie.beta.xB)}\\)<br>
            \\(x_C^{\\beta} = ${formatFrac(tie.beta.xC)}\\)
        `;
        const pBeta = baryToCart(tie.beta);
        boxBeta.style.left = (pBeta.x+15)+"px";
        boxBeta.style.top  = (pBeta.y-15)+"px";
        boxBeta.style.display = (showBetaPhase && currentView==='diagram') ? 'block':'none';
    }else{
        boxAlpha.style.display='none';
        boxBeta.style.display='none';
    }

    // Boxes visibility in 'phases' view
    if(currentView==='phases'){
        boxMain.style.display='none';
        boxAlpha.style.display='none';
        boxBeta.style.display='none';
    }else{
        boxMain.style.display='block';
    }

    // Re-render MathJax
    if(window.MathJax){MathJax.typesetPromise();}
}
function updateControlVisibility(){
    const checks = ["check-grid-lines","check-tie-lines","check-alpha-phase","check-beta-phase"];
    checks.forEach(id=>{
        document.getElementById(id).parentElement.style.display = (currentView==='diagram')?"inline-block":"none";
    });
}

/* -----------------  CONTROL EVENTS ---------------- */
document.getElementById("btn-diagram").addEventListener("click",()=>{
    currentView='diagram';
    document.getElementById("btn-diagram").classList.add("active");
    document.getElementById("btn-phases").classList.remove("active");
    updateControlVisibility();
    updateInfoBoxes();
});
document.getElementById("btn-phases").addEventListener("click",()=>{
    currentView='phases';
    document.getElementById("btn-phases").classList.add("active");
    document.getElementById("btn-diagram").classList.remove("active");
    updateControlVisibility();
    updateInfoBoxes();
});

/* Checkbox toggles */
document.getElementById("check-grid-lines").addEventListener("change",e=>{showGridLines=e.target.checked;});
document.getElementById("check-tie-lines").addEventListener("change",e=>{showTieLines=e.target.checked;});
document.getElementById("check-alpha-phase").addEventListener("change",e=>{showAlphaPhase=e.target.checked;});
document.getElementById("check-beta-phase").addEventListener("change",e=>{showBetaPhase=e.target.checked;});

/* ----------------------   P5.js  ---------------------- */
let sketch = s => {
    s.setup = () =>{
        const canvas = s.createCanvas(s.windowWidth, s.windowHeight - document.getElementById("controls").offsetHeight);
        canvas.parent("canvas-container");
        canvas.mousePressed(handleMousePressed);

        // define triangle vertices
        const w = s.width, h = s.height;
        PA = s.createVector(w/2, 50);
        PB = s.createVector(50, h-50);
        PC = s.createVector(w-50, h-50);

        updateInfoBoxes();
    };

    s.windowResized = () =>{
        s.resizeCanvas(s.windowWidth, s.windowHeight - document.getElementById("controls").offsetHeight);
        const w = s.width, h = s.height;
        PA.set(w/2, 50);
        PB.set(50, h-50);
        PC.set(w-50, h-50);
        updateInfoBoxes();
    };

    s.draw = () =>{
        s.clear();
        s.background(255);

        drawTriangle();
        drawAxesTicks();

        if(currentView === 'diagram'){
            if(showGridLines) drawGridLines();
            drawPhaseEnvelope();
            if(showTieLines) drawTieLines();
            drawUserPointAndProjections();

        }else if(currentView === 'phases'){
            drawPhaseRegions();
            drawPhaseEnvelope(); // boundary
            drawUserPointDot();
        }
    };

    /* ---------- Drawing Helpers ---------- */
    function drawTriangle(){
        s.stroke(0); s.strokeWeight(2); s.noFill();
        s.triangle(PA.x,PA.y, PB.x,PB.y, PC.x,PC.y);

        s.noStroke(); s.textSize(16); s.textAlign(s.CENTER, s.CENTER);
        s.fill('#008800'); s.text('A', PA.x, PA.y-15);
        s.fill('#0000FF'); s.text('B', PB.x-15, PB.y+15);
        s.fill('#FF0000'); s.text('C', PC.x+15, PC.y+15);
    }
    function drawAxesTicks(){
        s.strokeWeight(1); s.textSize(10); s.fill(0);
        for(let i=1;i<=9;i++){
            const frac = i/10;
            // edge BC (parallel to A axis)
            let p1 = p5.Vector.lerp(PB,PC,frac);
            let p2 = p5.Vector.lerp(PB,PC,frac);
            p2.y -= 6;
            s.stroke('#008800');
            s.line(p1.x,p1.y,p2.x,p2.y);
            s.noStroke();
            s.text(frac.toFixed(1), p2.x, p2.y-6);

            // edge AC (parallel to B axis)
            p1 = p5.Vector.lerp(PA,PC,frac);
            p2 = p5.Vector.lerp(PA,PC,frac);
            p2.x -= 6;
            s.stroke('#0000FF');
            s.line(p1.x,p1.y,p2.x,p2.y);
            s.noStroke();
            s.text(frac.toFixed(1), p2.x-12, p2.y);

            // edge AB (parallel to C axis)
            p1 = p5.Vector.lerp(PA,PB,frac);
            p2 = p5.Vector.lerp(PA,PB,frac);
            p2.x += 6;
            s.stroke('#FF0000');
            s.line(p1.x,p1.y,p2.x,p2.y);
            s.noStroke();
            s.text(frac.toFixed(1), p2.x+12, p2.y);
        }
    }
    function drawGridLines(){
        s.stroke('#dddddd'); s.strokeWeight(1);
        for(let i=1;i<=9;i++){
            const frac = i/10;
            // lines parallel to edges
            s.line(...p5.Vector.lerp(PB,PC,frac).array(), ...p5.Vector.lerp(PA,PC,1-frac).array());
            s.line(...p5.Vector.lerp(PA,PC,frac).array(), ...p5.Vector.lerp(PA,PB,1-frac).array());
            s.line(...p5.Vector.lerp(PA,PB,frac).array(), ...p5.Vector.lerp(PB,PC,1-frac).array());
        }
    }
    function drawPhaseEnvelope(){
        s.strokeWeight(2);
        // alpha curve
        s.stroke('#FFA500');
        s.noFill();
        s.beginShape();
        phaseEnvelopeAlpha.forEach(p=>{
            const v= baryToCart(p);
            s.vertex(v.x,v.y);
        });
        s.endShape();

        // beta curve
        s.stroke('#FF00FF');
        s.beginShape();
        phaseEnvelopeBeta.forEach(p=>{
            const v= baryToCart(p);
            s.vertex(v.x,v.y);
        });
        s.endShape();
    }
    function drawTieLines(){
        s.stroke('#888'); s.strokeWeight(1);
        tieLines.forEach(t=>{
            const a = baryToCart(t.alpha);
            const b = baryToCart(t.beta);
            s.line(a.x,a.y,b.x,b.y);
        });
    }
    function drawUserPointDot(){
        const p = baryToCart(userPoint);
        s.noStroke(); s.fill(0);
        s.circle(p.x,p.y,8);
    }
    function drawUserPointAndProjections(){
        drawUserPointDot();

        const p = baryToCart(userPoint);

        // Projection lines
        s.strokeWeight(1.5);
        // To AB (green)
        s.stroke('#008800');
        const footA = baryToCart({xA:0, xB:userPoint.xB/(userPoint.xB+userPoint.xC), xC:userPoint.xC/(userPoint.xB+userPoint.xC)});
        s.line(p.x,p.y, footA.x, footA.y);
        // To AC (blue)
        s.stroke('#0000FF');
        const footB = baryToCart({xA:userPoint.xA/(userPoint.xA+userPoint.xC), xB:0, xC:userPoint.xC/(userPoint.xA+userPoint.xC)});
        s.line(p.x,p.y, footB.x, footB.y);
        // To BC (red)
        s.stroke('#FF0000');
        const footC = baryToCart({xA:userPoint.xA/(userPoint.xA+userPoint.xB), xB:userPoint.xB/(userPoint.xA+userPoint.xB), xC:0});
        s.line(p.x,p.y, footC.x, footC.y);

        // If inside two-phase region draw tie-line endpoints
        const inside = pointInPolygon(p.x,p.y,polyToPoints(phaseEnvelopeAlpha,phaseEnvelopeBeta));
        if(inside){
            const tie = interpolateTieLine(userPoint);
            const a = baryToCart(tie.alpha);
            const b = baryToCart(tie.beta);

            s.stroke(0); s.drawingContext.setLineDash([6,4]);
            s.line(a.x,a.y,b.x,b.y);
            s.drawingContext.setLineDash([]);

            if(showAlphaPhase){
                s.noStroke(); s.fill('#FFA500');
                s.circle(a.x,a.y,8);
            }
            if(showBetaPhase){
                s.noStroke(); s.fill('#FF00FF');
                s.circle(b.x,b.y,8);
            }
        }
    }
    function drawPhaseRegions(){
        const poly = polyToPoints(phaseEnvelopeAlpha,phaseEnvelopeBeta);

        // one-phase region (triangle minus poly)
        s.noStroke(); s.fill('#fbe5e6');
        s.beginShape();
        [PA,PB,PC].forEach(v=>s.vertex(v.x,v.y));
        s.endShape(s.CLOSE);

        // two-phase region
        s.fill('#d4eafc');
        s.beginShape();
        poly.forEach(v=>s.vertex(v.x,v.y));
        s.endShape(s.CLOSE);

        // Labels
        s.fill(0); s.textSize(18); s.noStroke();
        // simple positions
        const midTwo = baryToCart({xA:0.35,xB:0.50,xC:0.15});
        const midOne = baryToCart({xA:0.1,xB:0.2,xC:0.7});
        s.text("two phases", midTwo.x, midTwo.y);
        s.text("one phase",  midOne.x, midOne.y);

        // plait point mark
        const plait = baryToCart(phaseEnvelopeAlpha[phaseEnvelopeAlpha.length-1]);
        s.fill(0); s.circle(plait.x,plait.y,8);
        s.text("plait", plait.x+12, plait.y-12);
    }

    function handleMousePressed(){
        if(currentView!=='diagram')return;
        const mx = s.mouseX, my = s.mouseY;
        const bary = cartToBary(mx,my);
        if(isInsideTriangle(bary)){
            userPoint = bary;
            updateInfoBoxes();
        }
    }
};

new p5(sketch);

/* Initial UI state */
