<script>
/* =====================================================
   Seeded PRNG (Mulberry32) – small & fast
   ===================================================== */
function mulberry32(a){
    return function(){
        var t = a += 0x6D2B79F5;
        t = Math.imul(t ^ t >>> 15, t | 1);
        t ^= t + Math.imul(t ^ t >>> 7, t | 61);
        return ((t ^ t >>> 14) >>> 0) / 4294967296;
    }
}

/* ---------------- utility helpers ---------------- */
function dist(p,q){return Math.hypot(p.x-q.x,p.y-q.y);}
function tourLength(pts,tour){
    let sum=0;
    const n=tour.length;
    for(let i=0;i<n;i++){
        sum+=dist(pts[tour[i]],pts[tour[(i+1)%n]]);
    }
    return sum;
}
function nearestNeighbor(pts){
    const n=pts.length, unvisited=new Set();
    for(let i=1;i<n;i++)unvisited.add(i);
    const tour=[0];
    let cur=0;
    while(unvisited.size){
        let best=null,bestD=1e9;
        unvisited.forEach(j=>{
            const d=dist(pts[cur],pts[j]);
            if(d<bestD){bestD=d;best=j;}
        });
        tour.push(best);
        unvisited.delete(best);
        cur=best;
    }
    return tour;
}

/* ---------------- 2-Opt ---------------- */
function twoOptImprove(pts,tour){
    const n=tour.length;
    let improved=true;
    while(improved){
        improved=false;
        for(let i=0;i<n-1;i++){
            for(let k=i+2;k<n+(i>0?0:-1);k++){
                const a=tour[i],b=tour[(i+1)%n],c=tour[k%n],d=tour[(k+1)%n];
                const delta=dist(pts[a],pts[c])+dist(pts[b],pts[d])-dist(pts[a],pts[b])-dist(pts[c],pts[d]);
                if(delta<-1e-9){
                    let s=i+1,e=k;
                    while(s<e){[tour[s%n],tour[e%n]]=[tour[e%n],tour[s%n]];s++;e--;}
                    improved=true;
                }
            }
        }
    }
    return tour;
}
function solveTwoOpt(pts){
    let t=nearestNeighbor(pts);
    return twoOptImprove(pts,t);
}

/* ---------------- Or-Opt ---------------- */
function solveOrOpt(pts){
    const n=pts.length;
    let tour=nearestNeighbor(pts),improved=true;
    while(improved){
        improved=false;
        for(let len=1;len<=3;len++){
            for(let i=0;i<n;i++){
                const seg=[];
                for(let k=0;k<len;k++)seg.push(tour[(i+k)%n]);
                const rest=tour.filter(x=>!seg.includes(x));
                for(let pos=0;pos<=rest.length;pos++){
                    if(pos===i)continue;
                    const cand=[...rest.slice(0,pos),...seg,...rest.slice(pos)];
                    if(tourLength(pts,cand)<tourLength(pts,tour)-1e-9){
                        tour=cand;improved=true;break;
                    }
                }
                if(improved)break;
            }
            if(improved)break;
        }
    }
    return tour;
}

/* ---------------- 3-Opt (simplified) ---------------- */
function threeOptSwap(t,i,j,k){
    const n=t.length;
    const p1=t.slice(0,i+1),
          p2=t.slice(i+1,j+1),
          p3=t.slice(j+1,k+1),
          p4=t.slice(k+1,n);
    return p1.concat(p2.reverse(),p3,p4);
}
function solve3Opt(pts){
    let tour=solveTwoOpt(pts),n=tour.length,improved=true;
    while(improved){
        improved=false;
        outer:for(let i=0;i<n-4;i++){
            for(let j=i+2;j<n-2;j++){
                for(let k=j+2;k<n+(i?0:-1);k++){
                    const A=tour[i],B=tour[i+1],C=tour[j],D=tour[j+1],E=tour[k%n],F=tour[(k+1)%n];
                    const base=dist(pts[A],pts[B])+dist(pts[C],pts[D])+dist(pts[E],pts[F]);
                    const alt=dist(pts[A],pts[C])+dist(pts[B],pts[E])+dist(pts[D],pts[F]);
                    if(alt<base-1e-9){
                        tour=threeOptSwap(tour,i,j,k);n=tour.length;improved=true;break outer;
                    }
                }
            }
        }
    }
    return tour;
}

/* ---------------- CCA (≈ MST preorder) ---------------- */
function solveCCA(pts){
    const n=pts.length, adj=[...Array(n)].map(()=>[]);
    /* ‑-- Prim MST ‑-- */
    const parent=Array(n).fill(-1),key=Array(n).fill(1e9),inMST=Array(n).fill(false);
    key[0]=0;
    for(let count=0;count<n;count++){
        let u=-1,min=1e9;
        for(let v=0;v<n;v++) if(!inMST[v]&&key[v]<min){min=key[v];u=v;}
        inMST[u]=true;
        for(let v=0;v<n;v++){
            const d=dist(pts[u],pts[v]);
            if(!inMST[v]&&d<key[v]){key[v]=d;parent[v]=u;}
        }
    }
    for(let v=1;v<n;v++){adj[v].push(parent[v]);adj[parent[v]].push(v);}
    /* DFS preorder gives tour */
    const tour=[],seen=Array(n).fill(false);
    (function dfs(u){seen[u]=true;tour.push(u);adj[u].forEach(w=>{if(!seen[w])dfs(w);});})(0);
    return tour;
}

/* ---------------- OrZweig (≈2.5-Opt) ---------------- */
function solveOrZweig(pts){
    let t=solveTwoOpt(pts);
    return t; /* quick variant */
}

/* =====================================================
   GLOBAL STATE
   ===================================================== */
let state={
    points:[],tours:{},lengths:{},timings:{}
};

/* =====================================================
   CALCULATE EVERYTHING
   ===================================================== */
function calculateAll(){
    /* generate points */
    const N=+document.getElementById('slider-points').value;
    const seed=+document.getElementById('seed-input').value||0;
    const rnd=mulberry32(seed);
    const pts=[...Array(N)].map(()=>({x:rnd(),y:rnd()}));
    state.points=pts;

    /* solvers */
    const solvers={
        'OrZweig':solveOrZweig,
        'OrOpt':solveOrOpt,
        'TwoOpt':solveTwoOpt,
        'CCA':solveCCA,
        '3-Opt':solve3Opt
    };
    const tours={},lengths={},timings={};
    for(const name in solvers){
        const t0=performance.now();
        const tour=solvers[name](pts);
        const t1=performance.now();
        tours[name]=tour;
        lengths[name]=tourLength(pts,tour);
        timings[name]=(t1-t0)/1000;
    }
    state.tours=tours;state.lengths=lengths;state.timings=timings;

    /* update table */
    const rows=['orzweig','oropt','twoopt','cca','3opt'];
    const map={'orzweig':'OrZweig','oropt':'OrOpt','twoopt':'TwoOpt','cca':'CCA','3opt':'3-Opt'};
    rows.forEach(r=>{
        document.getElementById('timing-'+r).textContent=timings[map[r]].toFixed(6);
        document.getElementById('result-'+r).textContent=lengths[map[r]].toFixed(5);
    });

    /* winner? */
    const mathMin=Math.min(lengths.OrZweig,lengths.OrOpt,lengths.TwoOpt,lengths.CCA),
          opt3=lengths['3-Opt'],eps=1e-6;
    let res='Tie';
    if(opt3+eps<mathMin)res='3-Opt wins';
    else if(mathMin+eps<opt3)res='Mathematica wins';
    document.getElementById('winner-display').textContent=res;

    /* redraw both canvases */
    leftSketch.redraw();rightSketch.redraw();
}

/* =====================================================
   EVENT WIRES
   ===================================================== */
document.getElementById('btn-random-set').onclick=()=>{
    document.getElementById('seed-input').value=Math.floor(Math.random()*99999)+1;
    calculateAll();
};
document.getElementById('btn-reset-seed').onclick=()=>{
    document.getElementById('seed-input').value=0;
    calculateAll();
};
document.getElementById('slider-points').onchange=calculateAll;
document.querySelectorAll('input[name="math-method"]').forEach(el=>{
    el.onchange=()=>{
        document.getElementById('left-plot-title').textContent=
            document.querySelector('input[name="math-method"]:checked').value;
        leftSketch.redraw();
    };
});

/* =====================================================
   p5 SKETCHES
   ===================================================== */
const margin=40;
function makeSketch(wrapper,side){
    return new p5(p=>{
        p.setup=()=>{p.createCanvas(400,400).parent(wrapper);p.noLoop();};
        function axes(){
            p.stroke(180);p.strokeWeight(1);
            for(let i=0;i<=5;i++){
                const t=i/5;
                const x=margin+t*(p.width-2*margin),
                      y=margin+t*(p.height-2*margin);
                p.line(x,margin,x,p.height-margin);  // vertical
                p.line(margin,y,p.width-margin,y);   // horizontal
                p.noStroke();p.fill(0);p.textSize(10);
                const lab=t.toFixed(1);
                p.text(lab,x-10,p.height-margin+12);
                p.text(lab,margin-28,p.height-y+4);
                p.stroke(180);
            }
        }
        p.draw=()=>{
            const pts=state.points;if(!pts.length)return;
            p.clear();axes();
            /* which tour & colour? */
            const tour=side==='left'
                ?state.tours[document.querySelector('input[name="math-method"]:checked').value]
                :state.tours['3-Opt'];
            const col=side==='left'?'#0072B2':'#E69F00';

            /* path */
            if(tour){
                p.stroke(col);p.strokeWeight(2);p.noFill();p.beginShape();
                tour.forEach(idx=>{
                    const pt=pts[idx];
                    const x=margin+pt.x*(p.width-2*margin),
                          y=p.height-(margin+pt.y*(p.height-2*margin));
                    p.vertex(x,y);
                });
                /* close loop */
                const pt=pts[tour[0]];
                p.vertex(margin+pt.x*(p.width-2*margin),
                         p.height-(margin+pt.y*(p.height-2*margin)));
                p.endShape();
            }

            /* points */
            p.noStroke();p.fill('red');
            pts.forEach(pt=>{
                const x=margin+pt.x*(p.width-2*margin),
                      y=p.height-(margin+pt.y*(p.height-2*margin));
                p.circle(x,y,6);
            });
        };
    },wrapper);
}
const leftSketch = makeSketch('canvas-left-wrapper','left');
const rightSketch = makeSketch('canvas-right-wrapper','right');

/* =====================================================
   FIRST RUN
   ===================================================== */
window.addEventListener('load',calculateAll);
</script>
</body>
</html>
