  #canvas-container{
    flex:1 1 auto;
    display:flex;
    justify-content:center;
    align-items:center;
  }
</style>
</head>
<body>

<!-- Main container -->
<div id="main-container">

  <!-- Left Column: Control Panel -->
  <div id="control-panel">
    <!-- Initialization Slider -->
    <div>
      <label for="slider-initialization">initialization</label>
      <input type="range" id="slider-initialization" min="0" max="65535" step="1" value="462">
      <span id="value-initialization">+ 462</span>
    </div>

    <!-- Max Steps Slider -->
    <div>
      <label for="slider-max-steps">max steps</label>
      <input type="range" id="slider-max-steps" min="1" max="50" step="1" value="9">
      <span id="value-max-steps">+ 9</span>
    </div>

    <!-- Checkboxes -->
    <div>
      <input type="checkbox" id="checkbox-cycle4">
      <label for="checkbox-cycle4">length 4 cycle</label>
    </div>
    <div>
      <input type="checkbox" id="checkbox-cycle2">
      <label for="checkbox-cycle2">length 2 cycles</label>
    </div>
    <div>
      <input type="checkbox" id="checkbox-cycle1">
      <label for="checkbox-cycle1">length 1 cycle points</label>
    </div>
    <div>
      <input type="checkbox" id="checkbox-all-points">
      <label for="checkbox-all-points">all map points</label>
    </div>
    <div>
      <input type="checkbox" id="checkbox-modify">
      <label for="checkbox-modify">modify initialization</label>
    </div>
  </div>

  <!-- Right Column: Visualization Area -->
  <div id="visualization-area">
    <p id="info-text">result of initial condition 462 does not repeat in 10 steps</p>
    <div id="canvas-container"></div> <!-- p5.js will attach canvas here -->
  </div>

</div>

<script>
/* ----------  GLOBAL STATE  ---------- */
let initValue  = 462;
let maxSteps   = 9;
let showCycle4 = false;
let showCycle2 = false;
let showCycle1 = false;
let showAllPts = false;
let modifyInit = false; // tracked but not used in rendering

let sequence = [];             // current automaton path (n-values)
const canvasSize = 512;
const scale      = canvasSize / 256; // 2 px per logical unit

// Pre-computed overlays
let allMapPoints     = null; // array of [x,y]
let fixedPointCoords = null; // array of [x,y]

/* ----------  DOM HOOK-UPS  ---------- */
const sliderInit   = document.getElementById('slider-initialization');
const spanInit     = document.getElementById('value-initialization');
const sliderSteps  = document.getElementById('slider-max-steps');
const spanSteps    = document.getElementById('value-max-steps');
const infoText     = document.getElementById('info-text');

const chkCycle4    = document.getElementById('checkbox-cycle4');
const chkCycle2    = document.getElementById('checkbox-cycle2');
const chkCycle1    = document.getElementById('checkbox-cycle1');
const chkAllPts    = document.getElementById('checkbox-all-points');
const chkModify    = document.getElementById('checkbox-modify');

/* ----------  AUTOMATON DEFINITION  ---------- */
function bitReverse15(x){
  let y = 0;
  for(let i=0;i<15;i++){
    y = (y<<1) | ( (x>>i) & 1 );
  }
  return y;
}

function f(n){
  return (n + bitReverse15(Math.floor(n/2))) & 0xFFFF; // modulo 65536, ensure 16-bit
}

function nToCoord(n){
  return [n & 0xFF, (n>>8)&0xFF];
}

/* ----------  SEQUENCE & CYCLE DETECTION  ---------- */
function recomputeSequence(){
  sequence = [initValue];
  for(let i=0; i<maxSteps; i++){
    sequence.push( f(sequence[sequence.length-1]) );
  }
  detectCycleAndUpdateInfo();
}

function detectCycleAndUpdateInfo(){
  let found = false;
  for(let j=1; j<sequence.length; j++){
    for(let i=0; i<j; i++){
      if(sequence[i] === sequence[j]){
        const k = j - i;
        infoText.textContent = `result of initial condition ${initValue} repeats in ${k} steps`;
        found = true;
        break;
      }
    }
    if(found) break;
  }
  if(!found){
    infoText.textContent = `result of initial condition ${initValue} does not repeat in ${maxSteps+1} steps`;
  }
}

/* ----------  PRECOMPUTATION  ---------- */
function precomputeAllMapPoints(){
  // Creates array of 65,536 points : the image of f(n)
  allMapPoints = new Array(65536);
  for(let n=0;n<65536;n++){
    const nn = f(n);
    const [x,y] = nToCoord(nn);
    allMapPoints[n] = [x*scale, y*scale];
  }
}

function precomputeFixedPoints(){
  fixedPointCoords = [];
  for(let n=0;n<65536;n++){
    if(f(n) === n){
      const [x,y] = nToCoord(n);
      fixedPointCoords.push([x*scale, y*scale]);
    }
  }
}

/* ----------  EVENT HANDLERS  ---------- */
function onInitSlider(){
  initValue = parseInt(sliderInit.value,10);
  spanInit.textContent = `+ ${initValue}`;
  recomputeSequence();
  redraw();
}

function onStepSlider(){
  maxSteps = parseInt(sliderSteps.value,10);
  spanSteps.textContent = `+ ${maxSteps}`;
  recomputeSequence();
  redraw();
}

function onCheckbox(){
  showCycle4 = chkCycle4.checked;
  showCycle2 = chkCycle2.checked;
  showCycle1 = chkCycle1.checked;
  showAllPts = chkAllPts.checked;
  modifyInit = chkModify.checked; // stored, currently unused
  redraw();
}

/* ----------  p5.js SKETCH  ---------- */
let sketch = function(p){
  p.setup = function(){
    let cnv = p.createCanvas(canvasSize, canvasSize);
    cnv.parent('canvas-container');
    p.noLoop();
    p.pixelDensity(1);
    recomputeSequence();
    precomputeAllMapPoints();
    precomputeFixedPoints();
    p.redraw();
  };

  p.draw = function(){
    // BACKGROUND
    p.background('#e0f0e0');

    /* ---- ALL MAP POINTS ---- */
    if(showAllPts){
      p.stroke('#808080');
      p.strokeWeight(1);
      for(let pt of allMapPoints){
        p.point(pt[0]+0.5, pt[1]+0.5); // +0.5 for crisp 1-px dot
      }
    }

    /* ---- AUTOMATON PATH ---- */
    // Convert sequence to coords
    const coords = sequence.map(n => {
      const [x,y] = nToCoord(n);
      return [x*scale, y*scale];
    });
    // polyline
    p.stroke('#800080');
    p.strokeWeight(2);
    p.noFill();
    p.beginShape();
    for(let [cx,cy] of coords){
      p.vertex(cx,cy);
    }
    p.endShape();

    // start point red
    p.noStroke();
    p.fill('#FF0000');
    p.circle(coords[0][0], coords[0][1], 6);

    // end point blue
    p.fill('#0000FF');
    p.circle(coords[coords.length-1][0], coords[coords.length-1][1], 6);

    /* ---- LENGTH 1 CYCLE POINTS ---- */
    if(showCycle1){
      p.stroke('#00FFFF');
      p.strokeWeight(3);
      for(let [cx,cy] of fixedPointCoords){
        p.point(cx+0.5, cy+0.5);
      }
    }

    /* ---- LENGTH 2 CYCLES ---- */
    if(showCycle2){
      const pairs = [[10922,21845],[13107,26214],[13653,27306],[15339,30678],
                     [21844,43691],[26213,52427],[27305,54611],[30677,61355],[43690,54613]];
      p.stroke('#008000');
      p.strokeWeight(2);
      for(let [n1,n2] of pairs){
        const [x1,y1] = nToCoord(n1);
        const [x2,y2] = nToCoord(n2);
        p.line(x1*scale,y1*scale, x2*scale,y2*scale);
      }
    }

    /* ---- LENGTH 4 CYCLE ---- */
    if(showCycle4){
      const cyc = [43690, 54612, 32768, 49152];
      let cycCoords = cyc.map(n => {
        const [x,y] = nToCoord(n);
        return [x*scale, y*scale];
      });
      // Lines of cycle
      p.stroke('#008000');
      p.strokeWeight(2);
      p.noFill();
      for(let i=0;i<cycCoords.length;i++){
        const [x1,y1] = cycCoords[i];
        const [x2,y2] = cycCoords[(i+1)%cycCoords.length];
        p.line(x1,y1,x2,y2);
      }
      // Bounding box
      p.stroke('#FFA500');
      p.strokeWeight(1.5);
      p.noFill();
      const xs = cycCoords.map(pt=>pt[0]);
      const ys = cycCoords.map(pt=>pt[1]);
      const xmin = Math.min(...xs), xmax = Math.max(...xs);
      const ymin = Math.min(...ys), ymax = Math.max(...ys);
      p.rect(xmin, ymin, xmax-xmin, ymax-ymin);

      // Perpendicular bisectors (short segments)
      p.stroke('#008000');
      p.strokeWeight(1);
      const bisLen = 40;
      for(let i=0;i<cycCoords.length;i++){
        const [x1,y1] = cycCoords[i];
        const [x2,y2] = cycCoords[(i+1)%cycCoords.length];
        const mx = (x1+x2)/2;
        const my = (y1+y2)/2;
        let dx = x2 - x1;
        let dy = y2 - y1;
        // perpendicular vector
        let px = -dy;
        let py = dx;
        const mag = Math.hypot(px,py) || 1;
        px = px / mag * bisLen;
        py = py / mag * bisLen;
        p.line(mx - px, my - py, mx + px, my + py);
      }
    }
  };
};

new p5(sketch);

/* ----------  EVENT LISTENER BINDINGS  ---------- */
sliderInit.addEventListener('input', onInitSlider);
sliderSteps.addEventListener('input', onStepSlider);

chkCycle4.addEventListener('change', onCheckbox);
chkCycle2.addEventListener('change', onCheckbox);
chkCycle1.addEventListener('change', onCheckbox);
chkAllPts.addEventListener('change', onCheckbox);
chkModify.addEventListener('change', onCheckbox);
</script>
</body>
</html>
