<script>
/* ======================================================================
                            ASTRONOMICAL DATA
   ======================================================================*/
const familyData = {
    "Zodiac":        ["Aries","Taurus","Gemini","Cancer","Leo","Virgo","Libra","Scorpius","Sagittarius","Capricornus","Aquarius","Pisces","Cygnus"],
    "Ursa Major Family":["Ursa Major","Ursa Minor","Draco"],
    "Perseus Family": ["Perseus","Andromeda","Cassiopeia","Cepheus"],
    "Hercules Family":["Hercules","Corona Borealis","Sagitta"],
    "Orion Family":  ["Orion","Canis Major","Canis Minor"],
    "Bayer Group":   ["Hydrus","Dorado"],
    "La Caille Family":["Norma","Circinus"]
};
const allConstellations = Object.values(familyData).flat();

// simplified star definitions for one constellation (Cygnus) as example
const constellationData = {
    "Cygnus":{
        stars:[
            {name:"Deneb",   ra:20.690, dec:45.28},
            {name:"Sadr",    ra:20.371, dec:40.26},
            {name:"Gienah",  ra:19.771, dec:33.97},
            {name:"Albireo", ra:19.512, dec:27.96}
        ],
        // indices into the stars array defining line segments
        lines:[[0,1],[1,2],[2,3]]
    }
};

/* ======================================================================
                                THREE.JS
   ======================================================================*/
let scene, camera, renderer, labelRenderer, controls;
let sphereMesh, starPoints, equatorLine, eclipticLine, ariesPoint,
    zenithGroup, constellationGroup;

const SPHERE_RADIUS = 10;

init();
animate();

function init(){
    // ---------- Scene ----------
    scene = new THREE.Scene();

    // ---------- Camera & Renderer ----------
    const container = document.getElementById('visualization-container');
    const {clientWidth:W, clientHeight:H} = container;

    camera = new THREE.PerspectiveCamera(60, W/H, 0.1, 1000);
    camera.position.set(0,0,25);
    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize(W,H);
    container.appendChild(renderer.domElement);

    // CSS2DRenderer (for pole labels)
    labelRenderer = new THREE.CSS2DRenderer();
    labelRenderer.setSize(W,H);
    labelRenderer.domElement.style.position = 'absolute';
    labelRenderer.domElement.style.top = '0px';
    labelRenderer.domElement.style.pointerEvents='none';
    container.appendChild(labelRenderer.domElement);

    // ---------- Controls ----------
    controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    // ---------- Celestial Sphere ----------
    sphereMesh = createCelestialSphere();
    scene.add(sphereMesh);

    // ---------- 1000 Brightest Stars (mock random) ----------
    starPoints = createRandomStars(1000);
    scene.add(starPoints);

    // ---------- Equator & Ecliptic ----------
    equatorLine  = createGreatCircle(0, 0x0088ff); // tilt 0
    scene.add(equatorLine);

    eclipticLine = createGreatCircle(23.5*Math.PI/180, 0xaa00ff); // tilt 23.5°
    scene.add(eclipticLine);

    // ---------- First Point of Aries ----------
    const ariesGeo  = new THREE.SphereGeometry(0.18,16,16);
    const ariesMat  = new THREE.MeshBasicMaterial({color:0xffff00});
    ariesPoint = new THREE.Mesh(ariesGeo,ariesMat);
    ariesPoint.position.set(SPHERE_RADIUS,0,0);
    scene.add(ariesPoint);

    // ---------- Zenith (point + line) ----------
    zenithGroup = createZenithGroup();
    scene.add(zenithGroup);

    // ---------- Polar Axis Labels ----------
    addPoleLabels();

    // ---------- Constellation Group ----------
    constellationGroup = new THREE.Group();
    scene.add(constellationGroup);

    // ---------- UI EVENT LISTENERS ----------
    setupUI();
    window.addEventListener('resize', onWindowResize);

    // initial constellation list
    populateConsteList(document.getElementById('select-family').value);
}

/* -----------------------------  UI HANDLERS  ------------------------- */
function setupUI(){
    // Dropdowns
    const familySel = document.getElementById('select-family');
    const constlSel = document.getElementById('select-constellation');
    familySel.addEventListener('change', ()=>populateConsteList(familySel.value));
    constlSel.addEventListener('change', ()=>highlightConstellation(constlSel.value));

    // Checkboxes
    document.getElementById('check-sphere').addEventListener('change',e=>sphereMesh.visible=e.target.checked);
    document.getElementById('check-stars').addEventListener('change',e=>starPoints.visible=e.target.checked);
    document.getElementById('check-equator').addEventListener('change',e=>equatorLine.visible=e.target.checked);
    document.getElementById('check-ecliptic').addEventListener('change',e=>eclipticLine.visible=e.target.checked);
    document.getElementById('check-aries').addEventListener('change',e=>ariesPoint.visible=e.target.checked);
    document.getElementById('check-zenith').addEventListener('change',e=>zenithGroup.visible=e.target.checked);

    // Sliders
    const raSlider  = document.getElementById('slider-ra');
    const decSlider = document.getElementById('slider-dec');
    raSlider.addEventListener('input', ()=>{document.getElementById('ra-val').textContent=raSlider.value; updateZenith();});
    decSlider.addEventListener('input',()=>{document.getElementById('dec-val').textContent=decSlider.value;updateZenith();});
}

/* ---------------------  Constellation Dropdown Logic  ---------------- */
function populateConsteList(family){
    const constlSel = document.getElementById('select-constellation');
    constlSel.innerHTML='';
    // Always include None
    constlSel.appendChild(new Option("None","None"));

    let list;
    if(family==="all"){
        list=[...allConstellations];
    }else{
        list=familyData[family]||[];
    }
    list.forEach(c=>constlSel.appendChild(new Option(c,c)));

    if(family==="Zodiac"){
        constlSel.value="Cygnus"; // per requirement
    }else if(list.length>0){
        constlSel.value=list[0];
    }else{
        constlSel.value="None";
    }
    highlightConstellation(constlSel.value);
}

/* ------------------  Highlight Selected Constellation  --------------- */
function highlightConstellation(name){
    // remove previous
    while(constellationGroup.children.length>0){
        constellationGroup.remove(constellationGroup.children[0]);
    }
    if(name==="None" || !constellationData[name])return;

    const data = constellationData[name];
    // Stars
    const starPos=[];
    data.stars.forEach(s=>{
        const {x,y,z}=raDecToCartesian(s.ra*15, s.dec, SPHERE_RADIUS); // ra in hours -> deg
        starPos.push(x,y,z);
    });
    const starGeo = new THREE.BufferGeometry();
    starGeo.setAttribute('position', new THREE.Float32BufferAttribute(starPos,3));
    const starMat = new THREE.PointsMaterial({color:0xffaa00, size:0.35});
    const starPts = new THREE.Points(starGeo,starMat);
    constellationGroup.add(starPts);

    // Lines
    const linePos=[];
    data.lines.forEach(pair=>{
        const a=data.stars[pair[0]];
        const b=data.stars[pair[1]];
        const aC=raDecToCartesian(a.ra*15,a.dec,SPHERE_RADIUS);
        const bC=raDecToCartesian(b.ra*15,b.dec,SPHERE_RADIUS);
        linePos.push(aC.x,aC.y,aC.z,bC.x,bC.y,bC.z);
    });
    const lineGeo = new THREE.BufferGeometry();
    lineGeo.setAttribute('position', new THREE.Float32BufferAttribute(linePos,3));
    const lineMat = new THREE.LineBasicMaterial({color:0xff0000});
    const lines = new THREE.LineSegments(lineGeo,lineMat);
    constellationGroup.add(lines);
}

/* -----------------------  Zenith Update  ----------------------------- */
function updateZenith(){
    const ra  = parseFloat(document.getElementById('slider-ra').value);   // degrees
    const dec = parseFloat(document.getElementById('slider-dec').value);  // degrees
    const coord = raDecToCartesian(ra,dec,SPHERE_RADIUS);

    const sphereMeshZenith = zenithGroup.children[0];
    const lineMesh         = zenithGroup.children[1];

    sphereMeshZenith.position.set(coord.x,coord.y,coord.z);

    const positions = lineMesh.geometry.attributes.position.array;
    // update endpoint (index 3,4,5)
    positions[3]=coord.x;
    positions[4]=coord.y;
    positions[5]=coord.z;
    lineMesh.geometry.attributes.position.needsUpdate = true;
}

/* ======================================================================
                        THREE.JS HELPERS & BUILDERS
   ======================================================================*/
function createCelestialSphere(){
    const geo = new THREE.SphereGeometry(SPHERE_RADIUS,64,64);
    const colors=[];
    geo.vertices = undefined; // ensure we use BufferGeometry attributes
    const posAttr = geo.getAttribute('position');
    for(let i=0;i<posAttr.count;i++){
        const y = posAttr.getY(i);
        // Map y (-R..R) to 0..1
        const t = (y+SPHERE_RADIUS)/(2*SPHERE_RADIUS);
        const color = new THREE.Color();
        // gradient: equator reddish (#ff8800), poles bluish (#0066ff)
        color.setRGB( (1-t)*1 + t*0, (1-t)*0.53 + t*0.4, (1-t)*0 + t*1 );
        colors.push( color.r, color.g, color.b );
    }
    geo.setAttribute('color', new THREE.Float32BufferAttribute(colors,3));
    const mat = new THREE.MeshBasicMaterial({
        vertexColors:true,
        transparent:true,
        opacity:0.4,
        side:THREE.DoubleSide
    });
    return new THREE.Mesh(geo,mat);
}

function createRandomStars(n){
    const positions=[];
    for(let i=0;i<n;i++){
        // random uniformly on sphere
        const u = Math.random()*2-1; // cos(dec)
        const phi = Math.random()*2*Math.PI;
        const r = SPHERE_RADIUS;
        const x = r*Math.sqrt(1-u*u)*Math.cos(phi);
        const y = r*u;
        const z = r*Math.sqrt(1-u*u)*Math.sin(phi);
        positions.push(x,y,z);
    }
    const geo = new THREE.BufferGeometry();
    geo.setAttribute('position', new THREE.Float32BufferAttribute(positions,3));
    const mat = new THREE.PointsMaterial({color:0xffffff, size:0.15});
    return new THREE.Points(geo,mat);
}

function createGreatCircle(tiltRad, color){
    const segments = 256;
    const geo = new THREE.BufferGeometry();
    const positions=[];
    for(let i=0;i<=segments;i++){
        const theta= (i/segments)*2*Math.PI;
        const x = SPHERE_RADIUS*Math.cos(theta);
        const y = 0;
        const z = SPHERE_RADIUS*Math.sin(theta);
        // apply tilt around Z-axis --> rotation around X-axis is typical, but we want plane tilt 23.5° on XZ-plane (around Z maybe)
        const v = new THREE.Vector3(x,y,z);
        v.applyAxisAngle(new THREE.Vector3(0,0,1), tiltRad);
        positions.push(v.x,v.y,v.z);
    }
    geo.setAttribute('position', new THREE.Float32BufferAttribute(positions,3));
    const mat = new THREE.LineBasicMaterial({color:color});
    return new THREE.LineLoop(geo,mat);
}

function createZenithGroup(){
    const group = new THREE.Group();
    // small sphere
    const zSphere = new THREE.Mesh(
        new THREE.SphereGeometry(0.25,16,16),
        new THREE.MeshBasicMaterial({color:0x000000})
    );
    group.add(zSphere);
    // line to origin
    const lineGeo = new THREE.BufferGeometry();
    lineGeo.setAttribute('position', new THREE.Float32BufferAttribute([0,0,0, 0,0,0],3)); // start & end; end will be updated
    const lineMat = new THREE.LineBasicMaterial({color:0x000000});
    const line = new THREE.Line(lineGeo,lineMat);
    group.add(line);
    return group;
}

function addPoleLabels(){
    const northDiv = document.createElement('div');
    northDiv.className='label';
    northDiv.textContent='N';
    const northLabel = new THREE.CSS2DObject(northDiv);
    northLabel.position.set(0,SPHERE_RADIUS+0.5,0);
    scene.add(northLabel);

    const southDiv = document.createElement('div');
    southDiv.className='label';
    southDiv.textContent='S';
    const southLabel = new THREE.CSS2DObject(southDiv);
    southLabel.position.set(0,-SPHERE_RADIUS-0.5,0);
    scene.add(southLabel);

    // Optional: polar axis line
    const axisGeo = new THREE.BufferGeometry();
    axisGeo.setAttribute('position', new THREE.Float32BufferAttribute([0,SPHERE_RADIUS,0, 0,-SPHERE_RADIUS,0],3));
    const axisMat = new THREE.LineBasicMaterial({color:0x000000});
    const axisLine = new THREE.Line(axisGeo,axisMat);
    scene.add(axisLine);
}

/* ------------------  Coordinate Conversion Helpers  ----------------- */
function raDecToCartesian(raDeg, decDeg, radius){
    const ra = THREE.Math.degToRad(raDeg);   // longitude
    const dec= THREE.Math.degToRad(decDeg);  // latitude
    const x = radius * Math.cos(dec) * Math.cos(ra);
    const y = radius * Math.sin(dec);
    const z = -radius * Math.cos(dec) * Math.sin(ra); // negative for right-handed system
    return {x,y,z};
}

/* ------------------------  Window Resize  ---------------------------- */
function onWindowResize(){
    const container = document.getElementById('visualization-container');
    const W = container.clientWidth;
    const H = container.clientHeight;
    camera.aspect = W/H;
    camera.updateProjectionMatrix();
    renderer.setSize(W,H);
    labelRenderer.setSize(W,H);
}

/* -----------------------------  Render  ------------------------------ */
function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene,camera);
    labelRenderer.render(scene,camera);
}
</script>
</body>
</html>
