<script>
/* ─────────────────── FFT (Radix-2, iterative) ─────────────────── */
function fft(real, imag){
    const n = real.length;
    if((n & (n - 1)) !== 0) throw "Array length must be power of 2";
    // Bit reversal
    for(let i=1, j=0; i<n; i++){
        let bit = n>>1;
        for(; j & bit; bit >>=1){ j ^= bit; }
        j ^= bit;
        if(i<j){
            [real[i], real[j]] = [real[j], real[i]];
            [imag[i], imag[j]] = [imag[j], imag[i]];
        }
    }
    // Cooley-Tukey
    for(let len=2; len<=n; len<<=1){
        const ang = -2*Math.PI/len;
        const wlenR = Math.cos(ang);
        const wlenI = Math.sin(ang);
        for(let i=0; i<n; i+=len){
            let wr=1, wi=0;
            for(let j=0; j<len/2; j++){
                const uR = real[i+j];
                const uI = imag[i+j];
                const vR = real[i+j+len/2]*wr - imag[i+j+len/2]*wi;
                const vI = real[i+j+len/2]*wi + imag[i+j+len/2]*wr;
                real[i+j]           = uR + vR;
                imag[i+j]           = uI + vI;
                real[i+j+len/2]     = uR - vR;
                imag[i+j+len/2]     = uI - vI;
                const nextWr = wr*wlenR - wi*wlenI;
                const nextWi = wr*wlenI + wi*wlenR;
                wr = nextWr; wi = nextWi;
            }
        }
    }
}
/* ─────────────────── Globals ─────────────────── */
const N  = 2048;
const fs = 32000;
const f  = 1000;
const A  = 1;
let normalizedMag = [];      // Stored spectrum (linear)
let currentScale  = 'linear'; // 'linear' | 'decibel'
/* ─────────────────── DOM References ─────────────────── */
const sliderC1  = document.getElementById('slider-c1');
const sliderC2  = document.getElementById('slider-c2');
const sliderLvl = document.getElementById('slider-level');
const checkboxClip = document.getElementById('checkbox-clipping');
const lvlGroup     = document.getElementById('level-control-group');

const displayC1 = document.getElementById('c1-value-display');
const displayC2 = document.getElementById('c2-value-display');
const displayLvl= document.getElementById('level-value-display');

const btnLinear  = document.getElementById('btn-linear');
const btnDecibel = document.getElementById('btn-decibel');

/* ─────────────────── Utility ─────────────────── */
function linspace(start, step, n){
    const arr = new Array(n);
    for(let i=0;i<n;i++) arr[i] = start + i*step;
    return arr;
}

/* ─────────────────── Simulation Update ─────────────────── */
function updateSimulation(){
    const c1 = parseFloat(sliderC1.value);
    const c2 = parseFloat(sliderC2.value);
    const clippingEnabled = checkboxClip.checked;
    const level = parseFloat(sliderLvl.value);

    // Time vector
    const dt = 1/fs;
    const t  = linspace(0, dt, N);

    // Input & output signal
    const x = new Array(N);
    const y = new Array(N);
    for(let i=0;i<N;i++){
        x[i] = A*Math.sin(2*Math.PI*f*t[i]);
        y[i] = x[i] + c1*Math.pow(x[i],2) + c2*Math.pow(x[i],3);
        if(clippingEnabled){
            if(y[i] >  level) y[i]= level;
            if(y[i] < -level) y[i]=-level;
        }
    }

    /* ---- Update Signal Plot (first 5ms) ---- */
    const sliceLen = Math.floor(0.005*fs); // 0-5 ms
    Plotly.react('plot-signal',
        [{
            x: t.slice(0,sliceLen),
            y: y.slice(0,sliceLen),
            mode:'lines',
            name:'y(t)',
            line:{color:'#1f77b4'}
        }],
        {
            title:'signal',
            xaxis:{title:'time [s]',range:[0,0.005],autorange:false},
            yaxis:{range:[-2,2],autorange:false},
            margin:{l:30,r:20,t:40,b:40},
            height:300
        }
    );

    /* ---- FFT ---- */
    const real = y.slice();              // copy
    const imag = new Array(N).fill(0);
    fft(real, imag);
    const mag = new Array(N/2);
    for(let k=0;k<N/2;k++){
        mag[k] = Math.sqrt(real[k]*real[k]+imag[k]*imag[k]);
    }
    /* ---- Normalize single-sided spectrum ---- */
    normalizedMag = mag.map((v,k)=> (k===0? v/N : 2*v/N));

    /* ---- Update Spectrum Plot ---- */
    updateSpectrumScale();

    /* ---- Update value displays ---- */
    displayC1.textContent = c1.toFixed(1);
    displayC2.textContent = c2.toFixed(1);
    displayLvl.textContent= level.toFixed(2);
}

/* ─────────────────── Spectrum Scale Update ─────────────────── */
function updateSpectrumScale(){
    const freq = new Array(N/2);
    for(let k=0;k<N/2;k++){ freq[k] = k*fs/N; }

    let yData, yAxis;
    if(currentScale === 'linear'){
        yData = normalizedMag;
        const maxY = Math.max(...yData)*1.1 || 1;
        yAxis = {range:[0,maxY],title:''};
    }else{ // decibel
        yData = normalizedMag.map(v => {
            const val = v>0 ? v : 1e-12;
            return 20*Math.log10(val);
        });
        yAxis = {range:[-100,0],title:''};
    }

    Plotly.react('plot-spectrum',
        [{
            type:'bar',
            x: freq,
            y: yData,
            marker:{color:'#ff7f0e'},
            width:5
        }],
        {
            title:'magnitude spectrum (DFT)',
            xaxis:{title:'frequency [Hz]',range:[0,8000],autorange:false},
            yaxis:yAxis,
            margin:{l:30,r:20,t:40,b:40},
            height:300
        }
    );
}

/* ─────────────────── Event Listeners ─────────────────── */
sliderC1.oninput = updateSimulation;
sliderC2.oninput = updateSimulation;
sliderLvl.oninput= updateSimulation;

document.getElementById('reset-c1').onclick = () => { sliderC1.value = 0; updateSimulation(); };
document.getElementById('reset-c2').onclick = () => { sliderC2.value = 0; updateSimulation(); };

checkboxClip.onchange = () =>{
    lvlGroup.style.display = checkboxClip.checked ? 'flex' : 'none';
    updateSimulation();
};

btnLinear.onclick = ()=>{
    currentScale='linear';
    btnLinear.classList.add('active');
    btnDecibel.classList.remove('active');
    updateSpectrumScale();
};
btnDecibel.onclick = ()=>{
    currentScale='decibel';
    btnDecibel.classList.add('active');
    btnLinear.classList.remove('active');
    updateSpectrumScale();
};

/* ─────────────────── Initial Draw ─────────────────── */
updateSimulation();
</script>
</body>
</html>
