                   min="-90" max="90" step="1" value="-45">
        </div>

        <div class="slider-group">
            <label for="slider-refraction-ratio">refraction ratio:
                <span id="value-refraction-ratio">2.00</span>
            </label>
            <input id="slider-refraction-ratio" type="range"
                   min="1.0" max="3.0" step="0.01" value="2.0">
        </div>

        <!-- Checkboxes -->
        <div class="checkbox-group">
            <label><input id="checkbox-injected"   type="checkbox" checked> injected</label>
            <label><input id="checkbox-refracted"  type="checkbox" checked> refracted</label>
            <label><input id="checkbox-reflected"  type="checkbox" checked> reflected</label>
            <label><input id="checkbox-ejected"    type="checkbox" checked> ejected</label>
            <label><input id="checkbox-normals"    type="checkbox"> normals</label>
            <label><input id="checkbox-intersections" type="checkbox"> intersections</label>
            <label><input id="checkbox-beam-top"   type="checkbox" checked> beam top half</label>
            <label><input id="checkbox-beam-bottom" type="checkbox" checked> beam bottom half</label>
        </div>
