    <!-- CANVAS ----------------------------------------------------------->
    <div id="canvas-container">
        <!-- p5.js will insert the canvas here -->
    </div>
</div>

<script>
// --------------------- p5.js Sketch -----------------------------------
let scaleFactor;               // pixels per world-unit
let paramElems = {};           // cache for DOM elements

function setup() {
    const size = Math.min(window.innerHeight, window.innerWidth - 260) - 20;
    let cnv = createCanvas(size, size);
    cnv.parent('canvas-container');
    cnv.attribute('id','p5-canvas');
    pixelDensity(1);

    scaleFactor = width / 5;   // world coords: -2.5 .. 2.5

    noLoop();                  // redraw only on demand

    cacheElements();
    attachListeners();
}

function windowResized() {
    const size = Math.min(window.innerHeight, window.innerWidth - 260) - 20;
    resizeCanvas(size, size);
    scaleFactor = width / 5;
    redraw();
}

// Cache frequently used DOM nodes
function cacheElements(){
    const ids = [
        "slider-ray-count", "slider-beam-width",
        "slider-incident-angle", "slider-refraction-ratio",
        "checkbox-injected","checkbox-refracted","checkbox-reflected","checkbox-ejected",
        "checkbox-normals","checkbox-intersections",
        "checkbox-beam-top","checkbox-beam-bottom"
    ];
    ids.forEach(id => paramElems[id]=document.getElementById(id));
}

// Connect UI -> redraw
function attachListeners(){
    // sliders
    [
        ["slider-ray-count","value-ray-count"],
        ["slider-beam-width","value-beam-width"],
        ["slider-incident-angle","value-incident-angle"],
        ["slider-refraction-ratio","value-refraction-ratio"]
    ].forEach(([sliderId,spanId])=>{
        const s=document.getElementById(sliderId);
        const sp=document.getElementById(spanId);
        s.addEventListener('input',()=>{
            sp.textContent=parseFloat(s.value).toFixed(s.step<1?2:0);
            redraw();
        });
    });

    // checkboxes
    const checkIds = [
        "checkbox-injected","checkbox-refracted","checkbox-reflected","checkbox-ejected",
        "checkbox-normals","checkbox-intersections",
        "checkbox-beam-top","checkbox-beam-bottom"
    ];
    checkIds.forEach(id=>{
        paramElems[id].addEventListener('change',redraw);
    });
}

// Obtain current parameter values
function getParams(){
    return {
        N:    parseInt(paramElems["slider-ray-count"].value,10),
        w:    parseFloat(paramElems["slider-beam-width"].value),
        ang:  radians(parseFloat(paramElems["slider-incident-angle"].value)),
        nRel: parseFloat(paramElems["slider-refraction-ratio"].value),
        showInjected:  paramElems["checkbox-injected"].checked,
        showRefracted: paramElems["checkbox-refracted"].checked,
        showReflected: paramElems["checkbox-reflected"].checked,
        showEjected:   paramElems["checkbox-ejected"].checked,
        showNormals:   paramElems["checkbox-normals"].checked,
        showIntersections: paramElems["checkbox-intersections"].checked,
        showTop:       paramElems["checkbox-beam-top"].checked,
        showBottom:    paramElems["checkbox-beam-bottom"].checked
    };
}

// Helper: draw dashed line (simple)
function dashedLine(x1,y1,x2,y2,segments=10){
    const dx=(x2-x1)/segments, dy=(y2-y1)/segments;
    for(let i=0;i<segments;i+=2){
        line(x1+dx*i, y1+dy*i, x1+dx*(i+1), y1+dy*(i+1));
    }
}

// Refract helper (returns null on total internal reflection)
function refract(I, n, eta){      // eta = n1 / n2
    const cosi = -vecDot(n,I);
    const k = 1 - eta*eta*(1 - cosi*cosi);
    if(k < 0) return null;        // total internal reflection
    return vecAdd( vecScale(I, eta),
                   vecScale(n, eta*cosi - Math.sqrt(k)) );
}

// Vector helpers -------------------------------------------------------
function vecAdd(a,b){ return [a[0]+b[0], a[1]+b[1]]; }
function vecSub(a,b){ return [a[0]-b[0], a[1]-b[1]]; }
function vecScale(a,s){ return [a[0]*s, a[1]*s]; }
function vecDot(a,b){ return a[0]*b[0] + a[1]*b[1]; }
function vecLen(a){ return Math.hypot(a[0],a[1]); }
function vecNorm(a){ const l=vecLen(a); return [a[0]/l, a[1]/l]; }

// Quadratic solver for |o + d*t| = 1
function sphereIntersect(o,d){
    const b = 2*vecDot(d,o);
    const c = vecDot(o,o) - 1;
    const disc = b*b - 4*c;
    if(disc < 0) return null;
    const sqrtD = Math.sqrt(disc);
    const t1 = (-b - sqrtD)/2;
    const t2 = (-b + sqrtD)/2;
    if(t1 > 1e-6) return t1;
    if(t2 > 1e-6) return t2;
    return null;
}

function draw() {
    const p = getParams();
    background(255);

    // --------------- prepare coordinate system -------------------------
    push();
    translate(width/2, height/2);
    scale(scaleFactor,-scaleFactor);   // y up
    strokeWeight(1/scaleFactor);

    // --------------- Draw axes -----------------------------------------
    stroke(150);
    line(-2.5,0,2.5,0);  // x-axis
    line(0,-2.5,0,2.5);  // y-axis
    for(let v=-2;v<=2;v++){
        if(v===0)continue;
        // x-ticks
        line(v, -0.05, v, 0.05);
        // y-ticks
        line(-0.05, v, 0.05, v);
        push();
        scale(1,-1); // invert for text
        noStroke();
        fill(120);
        textSize(0.1);
        textAlign(CENTER,CENTER);
        text(v, v*scaleFactor, -3);
        text(v, 3, -v*scaleFactor);
        pop();
    }

    // --------------- Draw sphere ---------------------------------------
    stroke(0); noFill();
    circle(0,0,2);   // radius=1 -> diameter 2

    // --------------- Ray tracing ---------------------------------------
    const dir = [Math.cos(p.ang), Math.sin(p.ang)];      // main direction
    const perp = [-dir[1], dir[0]];                      // perpendicular
    const half = (p.N-1)/2;
    const L = 2.5;                                       // starting offset magnitude

    for(let i=0;i<p.N;i++){
        const offset = (p.N===1)?0: ( (i - half) * (p.w/(p.N-1)) );
        if(offset>0 && !p.showTop) continue;
        if(offset<0 && !p.showBottom) continue;

        const o = vecAdd( vecScale(dir,-L), vecScale(perp, offset) ); // start point

        // Segment 0 : Injected (air) ------------------------------------
        const t1 = sphereIntersect(o, dir);
        if(t1===null) continue;
        const P1 = vecAdd(o, vecScale(dir,t1));

        if(p.showInjected){
            stroke('#FF0000');
            line(o[0],o[1], P1[0],P1[1]);
        }

        // Refraction into sphere ---------------------------------------
        const n1=1, n2=p.nRel;
        const nVec1 = vecNorm(P1);
        const transDir = refract(dir, nVec1, n1/n2);
        if(transDir===null) continue; // should not happen

        // Segment 1 : Refracted (inside)
        const o2 = vecAdd(P1, vecScale(transDir, 1e-4)); // shift inward
        const t2 = sphereIntersect(o2, transDir);
        if(t2===null) continue;
        const P2 = vecAdd(o2, vecScale(transDir,t2));

        if(p.showRefracted){
            stroke('#00AA00');
            line(P1[0],P1[1], P2[0],P2[1]);
        }

        // Reflection ----------------------------------------------------
        const nVec2 = vecNorm(P2);
        const reflDir = vecSub(transDir, vecScale(nVec2, 2*vecDot(transDir,nVec2)));

        // Segment 2 : Reflected (still inside)
        const o3 = vecAdd(P2, vecScale(reflDir,1e-4));
        const t3 = sphereIntersect(o3, reflDir);
        if(t3===null) continue;
        const P3 = vecAdd(o3, vecScale(reflDir,t3));

        if(p.showReflected){
            stroke('#0000FF');
            line(P2[0],P2[1], P3[0],P3[1]);
        }

        // Exit refraction ----------------------------------------------
        const transDir2 = refract(reflDir, nVec2, n2/n1);
        if(transDir2===null) continue;  // total internal reflection – skip ejected

        // Segment 3 : Ejected
        if(p.showEjected){
            stroke('#000000');
            const far = vecAdd(P3, vecScale(transDir2, 3)); // long line
            line(P3[0],P3[1], far[0],far[1]);
        }

        // --------- Intersections & Normals ----------------------------
        if(p.showIntersections){
            noStroke();
            const r=0.03;
            fill('#FF0000'); circle(P1[0],P1[1], r*2);
            fill('#00AA00'); circle(P2[0],P2[1], r*2);
            fill('#0000FF'); circle(P3[0],P3[1], r*2);
        }

        if(p.showNormals){
            stroke(0);
            const len=0.2;
            const nend1 = vecAdd(P1, vecScale(nVec1,len));
            const nend2 = vecAdd(P2, vecScale(nVec2,len));
            dashedLine(P1[0],P1[1], nend1[0], nend1[1]);
            dashedLine(P2[0],P2[1], nend2[0], nend2[1]);
        }
    }

    pop();
}
</script>
</body>
</html>
