<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Bipartite-Matching / Vertex-Cover Visualizer</title>

<!-- p5.js CDN -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.6.0/p5.min.js"></script>

<style>
    html,body{margin:0;padding:0;font-family:Arial,Helvetica,sans-serif;}
    #controls-container{
        display:flex;flex-wrap:wrap;align-items:center;
        padding:8px 10px;background:#f0f0f0;border-bottom:1px solid #ccc;
    }
    #controls-container span{margin:0 6px;font-weight:bold;}
    #controls-container button{
        margin:2px 3px;padding:3px 8px;border:1px solid #666;
        background:#fff;cursor:pointer;border-radius:3px;
    }
    #controls-container button.active{
        background:#007BFF;color:#fff;border-color:#0056b3;
    }
    #reset-button{font-weight:bold}
    #visualization-container{text-align:center}
    #status-text{margin:10px 0 5px 0;font-weight:bold;font-size:1.1em}
    #canvas-wrapper{display:flex;justify-content:center}
    canvas{border:1px solid #ccc}
</style>
</head>
<body>

<!-- --------------- 1. CONTROL PANEL ---------------- -->
<div id="controls-container">
    <span>size of bipartite graph</span>
    <div id="size-controls">
        <button id="size-6">6</button>
        <button id="size-10">10</button>
        <button id="size-14">14</button>
        <button id="size-18">18</button>
        <button id="size-22">22</button>
        <button id="size-26">26</button>
        <button id="size-30">30</button>
    </div>

    <span>maximum degree of lower part</span>
    <div id="degree-controls">
        <button id="degree-2">2</button>
        <button id="degree-3">3</button>
        <button id="degree-4">4</button>
        <button id="degree-5">5</button>
        <button id="degree-6">6</button>
    </div>

    <span>algorithm step</span>
    <div id="step-controls"></div>

    <button id="reset-button" title="Generate new graph">(+)</button>
</div>

<!-- --------------- 2. VISUALIZATION AREA ------------- -->
<div id="visualization-container">
    <p id="status-text"></p>
    <div id="canvas-wrapper">
        <canvas id="main-canvas"></canvas>
    </div>
</div>

<script>
/* ----------------------------- GLOBAL DATA ----------------------------- */
let graph={},             // {U:[],V:[],edges:[{u,v}]}
    history=[],           // each element = {matching,cover,bfsTree,highlightedPath,statusText}
    currentStep=0,
    MAX_CANVAS_W=1100,
    MAX_CANVAS_H=650,
    TREE_PORTION=0.42,    // percentage of canvas width reserved for BFS-tree
    canvas,pg;

/* ----------------------------- GRAPH GENERATION ----------------------------- */
function generateGraph(totalSize, maxDeg){
    if(totalSize%2===1) totalSize++;         // ensure even
    const n   = totalSize/2;                 // vertices per side
    const U   = [...Array(n).keys()];        // 0 .. n-1
    const V   = [...Array(n).keys()].map(i=>i+n); // n .. 2n-1
    const edges=[], adj={};
    U.concat(V).forEach(id=>adj[id]=[]);

    for(let v of V){
        const deg = 1+Math.floor(Math.random()*maxDeg);
        while(adj[v].length<deg){
            const u = U[Math.floor(Math.random()*U.length)];
            if(!adj[v].includes(u)){
                adj[v].push(u);
                adj[u].push(v);
                edges.push({u,v});
            }
        }
    }
    return {U,V,edges,adj};
}

/* ------------------------ MAXIMUM MATCHING  (KUHN/BFS) ------------------ */
function runAlgorithm(g){
    let n=g.U.length;
    const matchU=Array(g.U.length).fill(-1);
    const matchV=Array(g.V.length).fill(-1);   // index by (v-n)
    const hist=[];

    function addState(info){
        hist.push(JSON.parse(JSON.stringify(info)));
    }

    const initState={
        matching:[],
        cover:[],
        bfsTree:{nodes:[],edges:[]},
        highlightedPath:[],
        statusText:`initial state: 0 matched edges`
    };
    addState(initState);

    while(true){
        // ---------- BFS to build alternating tree -----------------
        const queue=[], depth={}, parent={}, visitedU={}, visitedV={};
        let rootId=null,augmentEnd=null;

        g.U.forEach((u,i)=>{
            if(matchU[i]===-1){
                queue.push(u);
                depth[u]=0;
                visitedU[u]=true;
                if(rootId===null) rootId=u;
            }
        });

        const treeEdges=[];
        while(queue.length){
            const x=queue.shift();
            if(g.U.includes(x)){          // x in U: explore unmatched edges
                for(let v of g.adj[x]){
                    if(visitedV[v]) continue;
                    visitedV[v]=true;
                    parent[v]=x;
                    treeEdges.push({from:x,to:v});
                    if(matchV[v-g.U.length]!==-1){
                        const u2 = matchV[v-g.U.length];
                        if(!visitedU[u2]){
                            visitedU[u2]=true;
                            parent[u2]=v;
                            treeEdges.push({from:v,to:u2});
                            depth[u2]=depth[x]+2;
                            queue.push(u2);
                        }
                    }else{
                        augmentEnd=v;
                        break;
                    }
                }
            }
            if(augmentEnd!==null) break;
        }

        // --------------------- compose BFS tree nodes ---------------------
        const treeNodes=[];
        Object.keys(visitedU).forEach(id=>{
            treeNodes.push({id:+id,depth:calcDepth(+id,parent)});
        });
        Object.keys(visitedV).forEach(id=>{
            treeNodes.push({id:+id,depth:calcDepth(+id,parent)});
        });

        addState({
            matching:matchingEdges(matchU,g),
            cover:computeCover(visitedU,visitedV,g),
            bfsTree:{nodes:treeNodes,edges:treeEdges},
            highlightedPath:[],
            statusText:`BFS built – matched edges: ${matchingEdges(matchU,g).length}`
        });

        if(augmentEnd===null){   // no augmenting path  -> algorithm finished
            break;
        }

        // --------------------- RECONSTRUCT PATH ---------------------
        const path=[];
        let v=augmentEnd;
        while(v!==undefined){
            const p=parent[v];
            if(p!==undefined) path.push({from:p,to:v});
            v=p;
        }
        // update matching along path
        for(let i=0;i<path.length;i++){
            const a=path[i].from,b=path[i].to;
            if(g.U.includes(a)){ // (a,b) edge where a in U, b in V
                matchU[a]=b;
                matchV[b-g.U.length]=a;
            }else{               // (a,b) where a in V, b in U  (matched edges to be removed)
                matchU[b]=-1;
                matchV[a-g.U.length]=-1;
            }
        }
        addState({
            matching:matchingEdges(matchU,g),
            cover:computeCover(visitedU,visitedV,g),
            bfsTree:{nodes:treeNodes,edges:treeEdges},
            highlightedPath:path,
            statusText:`augmenting path applied – matched edges: ${matchingEdges(matchU,g).length}`
        });
    }
    return hist;

    // ------------- helper internal -------------
    function calcDepth(id,parent){
        let d=0,cur=id;
        while(parent[cur]!==undefined){d++;cur=parent[cur];}
        return d;
    }
    function matchingEdges(mu,graph){
        const arr=[];
        mu.forEach((v,u)=>{
            if(v!==-1) arr.push({u:graph.U[u],v});
        });
        return arr;
    }
    function computeCover(visitedU,visitedV,graph){
        const cover=[];
        graph.U.forEach(u=>{
            if(!visitedU[u]) cover.push(u);
        });
        graph.V.forEach(v=>{
            if(visitedV[v]) cover.push(v);
        });
        return cover;
    }
}

/* ----------------------------- p5.js  DRAWING --------------------------- */
function sketch(p){
    p.setup=()=>{
        const cw=Math.min(window.innerWidth-20,MAX_CANVAS_W);
        const ch=Math.min(window.innerHeight-160,MAX_CANVAS_H);
        canvas=p.createCanvas(cw,ch);
        canvas.parent('canvas-wrapper');
        p.noLoop();
    };
    p.draw=()=>{
        p.clear();
        if(!history.length) return;
        const st=history[currentStep];
        const w=p.width,h=p.height;
        const treeW=w*TREE_PORTION, graphW=w-treeW;
        const margin=30;

        // ---- draw bipartite graph (right side) ----------------
        const n=graph.U.length;
        const spacing=(graphW-2*margin)/(n-1);
        const topY   = h*0.25;
        const bottomY= h*0.75;

        // vertex positions
        const vpos={};              // id -> {x,y}
        graph.U.forEach((u,i)=>{
            vpos[u]={x:treeW+margin+i*spacing,y:topY};
        });
        graph.V.forEach((v,i)=>{
            vpos[v]={x:treeW+margin+i*spacing,y:bottomY};
        });

        // edges   (default)
        p.stroke('#808080');p.strokeWeight(1);
        graph.edges.forEach(e=>{
            const {u,v}=e;
            p.line(vpos[u].x,vpos[u].y,vpos[v].x,vpos[v].y);
        });

        // matching edges
        p.stroke('#A0A0FF');p.strokeWeight(4);
        st.matching.forEach(e=>{
            p.line(vpos[e.u].x,vpos[e.u].y,vpos[e.v].x,vpos[e.v].y);
        });

        // highlighted path
        if(st.highlightedPath.length){
            p.stroke('#FF0000');p.strokeWeight(3);
            st.highlightedPath.forEach(e=>{
                p.line(vpos[e.from].x,vpos[e.from].y,vpos[e.to].x,vpos[e.to].y);
            });
        }

        // vertices
        graph.U.concat(graph.V).forEach(id=>{
            const pos=vpos[id];
            const inCover=st.cover.includes(id);
            p.stroke('#000');p.strokeWeight(1);
            if(inCover) p.fill('#00C000');
            else if(id===st.bfsTree.nodes[0]?.id) p.fill('#FF0000');
            else p.fill('#333333');
            p.ellipse(pos.x,pos.y,26,26);
            p.fill('#fff');p.noStroke();
            p.textAlign(p.CENTER,p.CENTER);
            p.textSize(12);
            p.text(id, pos.x, pos.y);
        });

        // ---- draw BFS tree (left side) ------------------------
        const depthGroups={}; let maxDepth=0;
        st.bfsTree.nodes.forEach(n=>{
            if(!depthGroups[n.depth]) depthGroups[n.depth]=[];
            depthGroups[n.depth].push(n.id);
            if(n.depth>maxDepth) maxDepth=n.depth;
        });
        const treePositions={};
        const levelSpacingY=h/(maxDepth+2);
        for(let d=0; d<=maxDepth; d++){
            const arr=depthGroups[d]||[];
            arr.forEach((id,idx)=>{
                const x=margin + (treeW-2*margin)/(arr.length+1)*(idx+1);
                const y=margin + d*levelSpacingY;
                treePositions[id]={x,y};
            });
        }

        // edges
        p.stroke('#FF0000');p.strokeWeight(1.5);
        st.bfsTree.edges.forEach(e=>{
            const a=treePositions[e.from], b=treePositions[e.to];
            if(a && b) p.line(a.x,a.y,b.x,b.y);
        });

        // nodes
        st.bfsTree.nodes.forEach(n=>{
            const pos=treePositions[n.id];
            p.fill('#FFA500');p.stroke('#000');
            p.rectMode(p.CENTER);
            p.rect(pos.x,pos.y,24,24);
            p.fill('#8080C0');p.noStroke();
            p.textAlign(p.CENTER,p.CENTER);
            p.textSize(12);
            p.text(n.id,pos.x,pos.y);
        });
    };
}
new p5(sketch,'main-canvas');

/* ----------------------------- UI HANDLERS ------------------------------ */
const defaultSizeBtn  ='size-18';
const defaultDegBtn   ='degree-3';

function setActive(containerId,btnId){
    document.querySelectorAll(`#${containerId} button`).forEach(b=>b.classList.remove('active'));
    document.getElementById(btnId).classList.add('active');
}

function getActiveValue(containerId,prefix){
    const btn=document.querySelector(`#${containerId} button.active`);
    return parseInt(btn.id.replace(prefix,''));
}

function buildStepButtons(count){
    const parent=document.getElementById('step-controls');
    parent.innerHTML='';
    for(let i=1;i<=count;i++){
        const btn=document.createElement('button');
        btn.id='step-'+i;
        btn.textContent=i;
        if(i===1) btn.classList.add('active');
        btn.addEventListener('click',()=>{
            setActive('step-controls',btn.id);
            currentStep=i-1;
            document.getElementById('status-text').textContent=history[currentStep].statusText;
            redrawCanvas();
        });
        parent.appendChild(btn);
    }
}

function redrawCanvas(){ if(window.redraw) window.redraw(); }

/* ----------------------------- RE-GENERATION ---------------------------- */
function regenerate(){
    const size  = getActiveValue('size-controls','size-');
    const deg   = getActiveValue('degree-controls','degree-');

    graph   = generateGraph(size,deg);
    history = runAlgorithm(graph);
    currentStep=0;

    buildStepButtons(history.length);
    document.getElementById('status-text').textContent=history[0].statusText;
    redrawCanvas();
}

/* ----------------------------- INITIALIZE ------------------------------- */
window.addEventListener('load',()=>{
    // set default active buttons
    setActive('size-controls',defaultSizeBtn);
    setActive('degree-controls',defaultDegBtn);
    regenerate();

    // size / degree controls
    document.querySelectorAll('#size-controls button').forEach(btn=>{
        btn.addEventListener('click',()=>{
            setActive('size-controls',btn.id);regenerate();
        });
    });
    document.querySelectorAll('#degree-controls button').forEach(btn=>{
        btn.addEventListener('click',()=>{
            setActive('degree-controls',btn.id);regenerate();
        });
    });
    // reset button
    document.getElementById('reset-button').addEventListener('click',regenerate);
});
</script>
</body>
</html>
