<script>
// ------------------ Helper functions ------------------------------
function factorial(n){
    let res = 1;
    for(let i=2;i<=n;i++) res *= i;
    return res;
}

function generateData(maxN){
    const x = Array.from({length: maxN}, (_,i)=> i+1);
    const log2 = n => Math.log(n)/Math.log(2);

    return {
        x,
        bubble:   x.map(v => Math.pow(v,2)),
        selection:x.map(v => 0.25*Math.pow(v,2)),
        insertion:x.map(v => v * factorial(v)),
        merge:    x.map(v => v*log2(v)),
        quick:    x.map(v => 1.2*v*log2(v)),
        heap:     x.map(v => 1.5*v*log2(v))
    };
}

