};

Plotly.newPlot('plot-container', traces, layout, {responsive:true});

// ------------------ DOM references --------------------------------
const sliderElements = document.getElementById('slider-elements');
const displayElements = document.getElementById('display-elements');

const radioScaleLinear       = document.getElementById('radio-scale-linear');
const radioScaleLogarithmic  = document.getElementById('radio-scale-logarithmic');
