const checkBogo      = document.getElementById('check-bogo');

// ------------------ Update functions ------------------------------
function updatePlot(){
    const n = parseInt(sliderElements.value,10);
    currentData = generateData(n);

    // Update traces' x and y
    traces[0].x = currentData.x; traces[0].y = currentData.bubble;
