        <label for="slider-xD">distillate composition</label>
        <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
        <span id="label-xD">0.84</span>
    </div>

    <div id="plot"></div>
</div>

<script>
(function(){

    const slider = document.getElementById('slider-xD');
    const label  = document.getElementById('label-xD');

    /* ---------- Core Functions ---------- */

    // VLE relation y(x)
    function yOfX(x){
        const a = 9;
        const b = -0.6;
        return (a * x) / (1 + (a - 1) * x + b * x * (1 - x));
    }

    // Reflux ratio r(x, xD)
    function rOfX(x, xD){
        const y = yOfX(x);
        return (xD - y) / (y - x);
    }

    // Main plot updater
    function updatePlot(xD){
        const x_vals = [];
        const r_vals = [];

        // Build bifurcation curve
        for(let x = 0.001; x <= xD - 0.0001; x += 0.001){
            const r = rOfX(x, xD);
            if(isFinite(r) && r >= 0 && r < 5){
                x_vals.push(x);
                r_vals.push(r);
            }
        }

        // Identify local maximum (tangent pinch point)
        let pinch_x = [];
        let pinch_y = [];
        for(let i = 1; i < r_vals.length - 1; i++){
            if(r_vals[i-1] < r_vals[i] && r_vals[i] > r_vals[i+1]){
                pinch_x = [x_vals[i]];
                pinch_y = [r_vals[i]];
                break;
            }
        }

        // Prepare Plotly traces
        const data = [
            {
                x: x_vals,
                y: r_vals,
                mode: 'lines',
                type: 'scatter',
                line:{color:'#8B0000', width:2.5}
            },
            {
                x: pinch_x,
                y: pinch_y,
                mode: 'markers',
                type: 'scatter',
                marker:{color:'royalblue', size:10}
            }
        ];

        const layout = {
            xaxis:{
                range:[0,1.0],
                gridcolor:'#e0e0e0',
                tick0:0,
                dtick:0.2,
                zeroline:false
            },
            yaxis:{
                range:[0,3.0],
                gridcolor:'#e0e0e0',
                tick0:0,
                dtick:0.5,
                zeroline:false
            },
            showlegend:false,
            margin:{l:40,r:20,b:40,t:20},
            plot_bgcolor:'white',
            paper_bgcolor:'white'
        };

        Plotly.react('plot', data, layout, {responsive:true});
    }

    /* ---------- Event Listeners ---------- */

    slider.addEventListener('input', () => {
        const xD = parseFloat(slider.value);
        label.textContent = xD.toFixed(2);
        updatePlot(xD);
    });

    /* ---------- Initial Rendering ---------- */

    updatePlot(parseFloat(slider.value));

})();
</script>

</body>
</html>
