        'btn-charge-2':'2'
    };
    Object.keys(chargeMap).forEach(id=>{
        const el = document.getElementById(id);
        el.addEventListener('click', ()=>{
            setSelectedTool({type:'charge', value:chargeMap[id]}, el);
        });
    });

    // Elements
    const elements = ['H','B','C','N','O','F','P','S','Cl','Se','Br','I',
                      'Li','Na','K','Be','Mg','Ca','Cr','Mn','Fe','Co','Cu','Zn','Al','Si'];
    elements.forEach(sym=>{
        const id = `btn-element-${sym}`;
        const el = document.getElementById(id);
        if(el){
            el.addEventListener('click', ()=>{
                setSelectedTool({type:'atom', value:sym}, el);
            });
        }
    });

    // Undo
    document.getElementById('btn-undo').addEventListener('click', ()=>{
        if(sceneObjects.length){
            sceneObjects.pop();
        }
    });

    // Add predefined structure
    document.getElementById('btn-add').addEventListener('click', ()=>{
        const sel = document.getElementById('select-structure');
        const value = sel.value;
        // Center position
        const x = width/2;
        const y = height/2;
        sceneObjects.push({type:'structure', value, x, y, rotation:0});
    });
});

/* ---------- P5.JS SKETCH ---------- */
function setup(){
    canvasParent = document.getElementById('canvas-container');
    const c = createCanvas(canvasParent.clientWidth, canvasParent.clientHeight);
    c.parent('canvas-container');
    textAlign(CENTER,CENTER);
    textSize(16);
}

function windowResized(){
    // keep canvas full size of container
    resizeCanvas(canvasParent.clientWidth, canvasParent.clientHeight);
}

function draw(){
    background(255);

    // Draw every object in scene
    sceneObjects.forEach(obj=>{
        push();
        translate(obj.x, obj.y);
        rotate(obj.rotation);
        if(obj.type === 'atom' || obj.type === 'charge'){
            drawAtomOrCharge(obj);
        }else if(obj.type === 'structure'){
            drawStructure(obj);
        }
        pop();
    });

    // Ghost preview for selected tool
    if(selectedTool){
        push();
        fill(0,120); // semi-transparent
        noStroke();
        text(selectedTool.value, mouseX, mouseY);
        pop();
    }
}

/* ---------- DRAWING HELPERS ---------- */
function drawAtomOrCharge(obj){
    noStroke();
    fill(0);
    text(obj.value,0,0);
}

function drawStructure(obj){
    switch(obj.value){
        case 'bond':
            stroke(0);
            strokeWeight(2);
            line(-20,0,20,0);
            break;
        case 'water':
            drawWater();
            break;
        case 'cyclopropyl_ethane':
            drawCyclopropylEthane();
            break;
        case 'borazine':
            drawBorazine();
            break;
        case 'isopropylamine':
            drawIsopropylamine();
            break;
        default:
            // placeholder
            rectMode(CENTER);
            stroke(0);
            noFill();
            rect(0,0,60,30);
    }
}

/* --- Individual structure drawings (simplified placeholders) --- */
function drawWater(){
    noStroke();
    fill(0);
    text('O',0,0);
    text('H',-25,-20);
    text('H',25,-20);
    stroke(0);
    line(-13,-10, -25,-17);
    line(13,-10, 25,-17);
}
function drawCyclopropylEthane(){
    stroke(0); strokeWeight(2);
    // triangle (cyclopropane)
    triangle(-20,0,0,-35,20,0);
    // ethane line
    line(20,0,60,0);
    // CH3
    noStroke(); fill(0);
    text('CH₃',75,0);
}
function drawBorazine(){
    stroke(0); strokeWeight(1.5);
    const r=30;
    for(let i=0;i<6;i++){
        const a1 = TWO_PI*i/6;
        const a2 = TWO_PI*(i+1)/6;
        line(r*cos(a1),r*sin(a1),r*cos(a2),r*sin(a2));
        const label = (i%2===0)?'B':'N';
        noStroke();
        fill(0);
        text(label,r*0.8*cos(a1),r*0.8*sin(a1));
        stroke(0);
    }
}
function drawIsopropylamine(){
    stroke(0); strokeWeight(2);
    line(-20,0,0,0); // main chain
    line(0,0,20,-15); // branch
    line(0,0,20,15); // branch
    noStroke(); fill(0);
    text('NH₂',40,0);
    text('CH₃',-30,0);
    text('CH₃',35,-20);
    text('CH₃',35,20);
}

/* ---------- INTERACTION HANDLERS ---------- */
function mousePressed(){
    // Only react if mouse over canvas
    if(mouseX<0 || mouseX>width || mouseY<0 || mouseY>height) return;

    // 1. If we have a selected tool, place it
    if(selectedTool){
        sceneObjects.push({
            type:selectedTool.type,
            value:selectedTool.value,
            x:mouseX,
            y:mouseY,
            rotation:0
        });
        clearSelectedTool();
        return;
    }

    // 2. Otherwise try picking existing object
    for(let i=sceneObjects.length-1;i>=0;i--){
        const obj = sceneObjects[i];
        const d = dist(mouseX,mouseY,obj.x,obj.y);
        const pickRadius = (obj.type==='structure')?40:20;
        if(d < pickRadius){
            activeObject = obj;
            if(d < 25){
                interactionMode = 'rotate';
                initialAngle = atan2(mouseY-obj.y, mouseX-obj.x) - obj.rotation;
            }else{
                interactionMode = 'translate';
                offsetX = mouseX - obj.x;
                offsetY = mouseY - obj.y;
            }
            // Bring to front
            sceneObjects.splice(i,1);
            sceneObjects.push(activeObject);
            break;
        }
    }
}

function mouseDragged(){
    if(!activeObject) return;
    if(interactionMode==='translate'){
        activeObject.x = mouseX - offsetX;
        activeObject.y = mouseY - offsetY;
    }else if(interactionMode==='rotate'){
        const angle = atan2(mouseY - activeObject.y, mouseX - activeObject.x);
        activeObject.rotation = angle - initialAngle;
    }
}

function mouseReleased(){
    activeObject = null;
    interactionMode = null;
}
</script>
</body>
</html>
