</head>
<body>

<!-- =========== HEADER CONTROLS =========== -->
<div id="controls-container">
    <!-- Amplitude -->
    <div class="control-row">
        <label for="slider-amplitude">Amplitude \\(a\\): <span id="value-amplitude"></span></label>
        <input type="range" id="slider-amplitude" min="0.1" max="2.0" step="0.01" value="1.5">
    </div>

    <!-- Frequency -->
    <div class="control-row">
        <label for="slider-frequency">Frequency \\(f\\): <span id="value-frequency"></span></label>
        <input type="range" id="slider-frequency" min="0.1" max="1.0" step="0.01" value="0.3">
    </div>

    <!-- Wavelength -->
    <div class="control-row">
        <label for="slider-wavelength">Wavelength \\(\\lambda\\): <span id="value-wavelength"></span></label>
        <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
    </div>

    <!-- Time -->
    <div class="control-row">
        <label for="slider-time">Time \\(t\\): <span id="value-time"></span></label>
        <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
    </div>

    <!-- Initial Phase -->
    <div class="control-row">
        <label for="select-phase">Initial phase \\(\\phi_0\\):</label>
        <select id="select-phase">
            <option value="-3.141592653589793">-π</option>
            <option value="-2.0943951023931953">-2π/3</option>
            <option value="-1.5707963267948966" selected>-π/2</option>
            <option value="-1.0471975511965976">-π/3</option>
            <option value="0">0</option>
            <option value="1.0471975511965976">π/3</option>
            <option value="1.5707963267948966">π/2</option>
            <option value="2.0943951023931953">2π/3</option>
            <option value="3.141592653589793">π</option>
        </select>
    </div>
</div>

<!-- =========== FORMULA DISPLAY =========== -->
<div id="formula-display"></div>

<!-- =========== VISUALIZATION CANVAS =========== -->
<div id="canvas-container">
    <!-- p5.js will inject the canvas here -->
</div>

<script>
/*****************************************************************
 * Global state & helpers
 *****************************************************************/
const state = {
    A: 1.5,
    f: 0.3,
    lambda: 4.5,
    t: 2.5,
    phi0: -Math.PI/2,
    k: 2*Math.PI/4.5,
    omega: 2*Math.PI*0.3
};

const PI = Math.PI;
const eps = 1e-4;

function approxEqual(a,b){ return Math.abs(a-b) < eps; }

/* ======== Update formula display using MathJax ========= */
function updateFormulaDisplay(){
    let {A, omega, k, phi0} = state;
    let latex;
    if(approxEqual(phi0,  PI/2)){
        latex = `y(x,t)=${A.toFixed(2)}\\sin\\,(kx-\\omega t)`;
    }else if(approxEqual(phi0,-PI/2)){
        latex = `y(x,t)=-${A.toFixed(2)}\\sin\\,(kx-\\omega t)`;
    }else if(approxEqual(phi0, PI)){
        latex = `y(x,t)=-${A.toFixed(2)}\\cos\\,(kx-\\omega t)`;
    }else if(approxEqual(phi0,0)){
        latex = `y(x,t)=${A.toFixed(2)}\\cos\\,(kx-\\omega t)`;
    }else{
        latex = `y(x,t)=${A.toFixed(2)}\\cos\\,(${omega.toFixed(2)}\\,t-${k.toFixed(2)}\\,x+${phi0.toFixed(2)})`;
    }

    document.getElementById("formula-display").innerHTML = `\\(${latex}\\)`;
    if(window.MathJax){
        MathJax.typesetPromise();
    }
}

/* ======== Update displayed slider/select numeric values ======== */
function updateNumericDisplays(){
    document.getElementById("value-amplitude").textContent = state.A.toFixed(2);
    document.getElementById("value-frequency").textContent = state.f.toFixed(2);
    document.getElementById("value-wavelength").textContent = state.lambda.toFixed(2);
    document.getElementById("value-time").textContent = state.t.toFixed(2);
}

/* ======== Recalculate derived quantities k and omega ======== */
function recalculateDerived(){
    state.k = 2*PI / state.lambda;
    state.omega = 2*PI * state.f;
}

/*****************************************************************
 * Event Listeners for controls
 *****************************************************************/
function attachControlListeners(){
    const ids = ["slider-amplitude","slider-frequency","slider-wavelength","slider-time","select-phase"];
    ids.forEach(id=>{
        const el = document.getElementById(id);
        const eventName = (id==='select-phase') ? 'change' : 'input';
        el.addEventListener(eventName,()=>{
            // Update state from controls
            state.A      = parseFloat(document.getElementById("slider-amplitude").value);
            state.f      = parseFloat(document.getElementById("slider-frequency").value);
            state.lambda = parseFloat(document.getElementById("slider-wavelength").value);
            state.t      = parseFloat(document.getElementById("slider-time").value);
            state.phi0   = parseFloat(document.getElementById("select-phase").value);

            recalculateDerived();
            updateNumericDisplays();
            updateFormulaDisplay();
            p.redraw(); // trigger p5.js redraw
        });
    });
}

/*****************************************************************
 * p5.js sketch
 *****************************************************************/
let p = new p5(p=>{
    // Canvas & coordinate settings
    const canvasW = 1000;
    const canvasH = 500;
    const originX = 60;            // pixels from left
    const originY = canvasH/2;     // vertical center
    const pixPerUnit = 30;         // scaling factor

    const vScale = 0.5;            // velocity vector scale (world units)
    const aScale = 0.05;           // acceleration vector scale (world units)

    p.setup = function(){
        const cnv = p.createCanvas(canvasW, canvasH);
        cnv.parent("canvas-container");
        p.frameRate(30);
        p.noLoop(); // redraw manually on interaction
        p.strokeWeight(1);
    };

    p.redraw = function(){
        // p.redraw overrides the original; call super?
    };

    p.draw = function(){
        p.clear();
        p.background(255);

        // Save original state
        p.push();
        // Move origin and flip y
        p.translate(originX, originY);
        p.scale(1, -1);

        drawAxes();
        drawGrid();
        drawWaveAndParticles();

        // Restore
        p.pop();
    };

    /*************** Helper drawing functions ****************/
    function drawAxes(){
        p.stroke(0);
        p.strokeWeight(1);

        // X-axis
        p.line(0,0, canvasW-originX-20, 0);
        // arrow head
        p.line(canvasW-originX-30, 5, canvasW-originX-20,0);
        p.line(canvasW-originX-30,-5, canvasW-originX-20,0);
        // label
        p.push();
        p.scale(1,-1);
        p.noStroke();
        p.fill(0);
        p.text('x', canvasW-originX-15, -5);
        p.pop();

        // Y-axis
        p.line(0, -canvasH/2 +20, 0, canvasH/2 -20);
        // arrow head
        p.line(-5, canvasH/2 -30, 0, canvasH/2 -20);
        p.line(5,  canvasH/2 -30, 0, canvasH/2 -20);

        p.push();
        p.scale(1,-1);
        p.noStroke();
        p.fill(0);
        p.text('y', 5,  -(canvasH/2)+30);
        p.pop();
    }

    function drawGrid(){
        const ctx = p.drawingContext;
        ctx.save();
        ctx.setLineDash([5,5]);
        p.stroke(0,180,0,80);
        p.strokeWeight(0.5);
        for(let xi=1; xi<=16; xi++){
            const xPos = xi*pixPerUnit;
            // vertical dashed line
            p.line(xPos, 0, xPos, canvasH/2 -20);
            // tick on axis
            p.line(xPos, -3, xPos, 3);
            // number label (draw in screen coords => flip back)
            p.push();
            p.scale(1,-1);
            p.noStroke();
            p.fill(0);
            p.text(xi, xPos-3, 15);
            p.pop();
        }
        ctx.restore();
    }

    function drawWaveAndParticles(){
        const {A, omega, k, t, phi0} = state;

        // Draw wave curve
        p.noFill();
        p.stroke(0,0,255);
        p.strokeWeight(2);
        p.beginShape();
        for(let px=0; px<=canvasW - originX -20; px++){
            let worldX = px / pixPerUnit;
            let y = A * Math.cos(omega*t - k*worldX + phi0);
            p.vertex(px, y*pixPerUnit);
        }
        p.endShape();

        // Draw points, velocity & acceleration vectors
        p.strokeWeight(1);
        for(let xi=1; xi<=16; xi++){
            let xw = xi;
            let y  = A * Math.cos(omega*t - k*xw + phi0);
            let v  = -A * omega * Math.sin(omega*t - k*xw + phi0);       // velocity
            let a  = -A * omega * omega * Math.cos(omega*t - k*xw + phi0); // acceleration

            let px = xw * pixPerUnit;
            let py = y  * pixPerUnit;

            // Point
            p.fill(0);
            p.noStroke();
            p.circle(px, py, 6);

            // Velocity vector (red)
            drawArrow(px, py, v * vScale, 'red', '\\vec{v}');

            // Acceleration vector (black)
            drawArrow(px+8, py, a * aScale, 'black', '\\vec{a}');
        }
    }

    // Draw vertical arrow; vecY in world units
    function drawArrow(baseX, baseY, vecY, clr, label){
        p.push();
        p.stroke(clr);
        p.fill(clr);
        p.strokeWeight(1);

        const tipX = baseX;
        const tipY = baseY + vecY*pixPerUnit;

        // line
        p.line(baseX, baseY, tipX, tipY);

        // arrow head
        const headSize = 6;
        const dir = Math.sign(vecY) || 1;
        p.push();
        p.translate(tipX, tipY);
        p.rotate(dir>0?0:PI);
        p.triangle(0,0, -headSize/2, -headSize*dir, headSize/2, -headSize*dir);
        p.pop();

        // label (draw in screen coords)
        p.push();
        p.scale(1,-1);
        p.noStroke();
        p.fill(clr);
        const screenY = -(tipY) + (dir>0? -5:15);
        p.text(label, tipX+4, screenY);
        p.pop();

        p.pop();
    }

});

/*****************************************************************
 * Initialize
 *****************************************************************/
attachControlListeners();
recalculateDerived();
updateNumericDisplays();
updateFormulaDisplay();
p5.instance = p; // expose if needed
</script>

</body>
</html>
