</head>
<body>

  <div id="app-root">

    <!-- ----------- Control Panel ---------- -->
    <div id="control-panel">
      <label for="slider-eccentricity">eccentricity ε</label>
      <input type="range" id="slider-eccentricity"
             min="0" max="1.5" step="0.01" value="0">
      <span id="value-eccentricity">0.0</span>
    </div>

    <!-- ----------- Visualization Area ---------- -->
    <div id="visualization-area">
      <h3 style="text-align:center;margin:0 0 6px 0;">Kepler orbits</h3>

      <div id="canvas-container"></div>

      <div id="energy-display">
        energy = <span id="energy-value">\( -\frac{1}{4} \)</span>
      </div>
    </div>

  </div>

  <script>
    /* ------------ Global state ------------ */
    let epsilon = 0.0;          // current eccentricity
    const p = 2;                // semi-latus rectum
    const scaleFactor = 30;     // pixels per unit
    let originX, originY;       // pixel coords of (0,0)

    const slider = document.getElementById('slider-eccentricity');
    const eccValueSpan = document.getElementById('value-eccentricity');
    const energySpan   = document.getElementById('energy-value');

    /* ------------ Slider interaction ------------ */
    slider.addEventListener('input', () => {
      epsilon = parseFloat(slider.value);
      eccValueSpan.textContent = epsilon.toFixed(1);
      updateEnergyDisplay();
      redraw();                 // p5.js – draw once
    });

    /* ------------ Energy calculation & display ------------ */
    function updateEnergyDisplay(){
      if (Math.abs(epsilon) < 1e-8){  // ε == 0
        energySpan.innerHTML = '\\(-\\frac{1}{4}\\)';
      }else{
        const E = -0.25 * (1 - epsilon*epsilon);
        energySpan.textContent = E.toFixed(2);
      }
      MathJax.typeset();              // re-typeset MathJax
    }

    /* ------------ p5.js sketch ------------ */
    function setup(){
      const canvas = createCanvas(700, 500);
      canvas.parent('canvas-container');
      pixelDensity(1);         // keep crisp lines

      originX = width * 0.7;   // move origin 70% to the right
      originY = height / 2;    // center vertically

      noLoop();                // draw only on demand
      redraw();                // initial render
    }

    function draw(){
      background('#f0f0f0');
      drawAxes();
      drawCentralBody();
      drawOrbitPath(epsilon);
    }

    /* ---------- Helper: convert plot (x,y) to pixel ---------- */
    function toPixel(x, y){
      return [originX + x*scaleFactor, originY - y*scaleFactor];
    }

    /* ---------- Draw coordinate axes ---------- */
    function drawAxes(){
      stroke(0); strokeWeight(1);
      // x-axis
      const [xStart, yAxisPix] = toPixel(-15, 0);
      const [xEnd,   _]        = toPixel(5, 0);
      line(xStart, yAxisPix, xEnd, yAxisPix);

      // y-axis
      const [xAxisPix, yStart] = toPixel(0, -7);
      const [__,       yEnd]   = toPixel(0, 7);
      line(xAxisPix, yStart, xAxisPix, yEnd);

      // Tick marks & labels
      textSize(10);
      noStroke();
      fill(0);

      // x ticks
      for(let i = -15; i <= 5; i++){
        const [tx, ty] = toPixel(i,0);
        stroke(0); line(tx, ty-3, tx, ty+3);
        noStroke(); if(i%5===0) text(i, tx-6, ty+15);
      }

      // y ticks
      for(let j = -7; j <= 7; j++){
        const [tx, ty] = toPixel(0,j);
        stroke(0); line(tx-3, ty, tx+3, ty);
        noStroke(); if(j!==0 && j%2===0) text(j, tx+6, ty+3);
      }

      // axis labels
      text('Global\'x', xEnd - 40, yAxisPix - 6);
      text('Global\'y', xAxisPix + 6, yEnd + 15);
    }

    /* ---------- Draw central attracting body ---------- */
    function drawCentralBody(){
      fill(0); noStroke();
      const [x0,y0] = toPixel(0,0);
      circle(x0, y0, 6);
    }

    /* ---------- Orbit path drawing ---------- */
    function drawOrbitPath(eps){
      stroke('#4a80b5');
      strokeWeight(2);
      noFill();

      beginShape();
      if(Math.abs(eps) < 1e-8){                    // ε = 0 → circle
        for(let theta = 0; theta <= TWO_PI+0.02; theta += 0.02){
          const x = 2 * Math.cos(theta) - 2;
          const y = 2 * Math.sin(theta);
          const [px,py] = toPixel(x,y);
          vertex(px,py);
        }
      }else if(eps > 1.0){                         // hyperbola
        const thetaMax = Math.acos(-1/eps) - 0.001;
        for(let theta = -thetaMax; theta <= thetaMax; theta += 0.002){
          const r = p / (1 + eps * Math.cos(theta));
          const x = r * Math.cos(theta);
          const y = r * Math.sin(theta);
          const [px,py] = toPixel(x,y);
          vertex(px,py);
        }
      }else if(Math.abs(eps-1.0) < 1e-8){          // parabola
        const tMin = -2.2, tMax = 2.2;
        for(let theta = tMin; theta <= tMax; theta += 0.002){
          const r = p / (1 + eps * Math.cos(theta)); // eps == 1
          const x = r * Math.cos(theta);
          const y = r * Math.sin(theta);
          const [px,py] = toPixel(x,y);
          vertex(px,py);
        }
      }else{                                       // ellipse 0 < ε < 1
        for(let theta = 0; theta <= TWO_PI+0.02; theta += 0.002){
          const r = p / (1 + eps * Math.cos(theta));
          const x = r * Math.cos(theta);
          const y = r * Math.sin(theta);
          const [px,py] = toPixel(x,y);
          vertex(px,py);
        }
      }
      endShape();
    }

    /* ------------ Initial energy render ------------ */
    updateEnergyDisplay();
  </script>
</body>
</html>
