<body>
<div class="main-container">
    <h2>Quantum Computer Search Algorithms</h2>

    <!-- ====================  CONTROL PANEL  ==================== -->
    <div id="control-panel">
        <!-- Algorithm Selector -->
        <div class="section" id="alg-selector">
            <span class="label">algorithm:</span>
            <button id="btn-unstructured">unstructured</button>
            <button id="btn-adiabatic">adiabatic</button>
            <button id="btn-discrete-adiabatic">discrete adiabatic</button>
            <button id="btn-heuristic" class="active">heuristic</button>
        </div>

        <!-- Total Steps Slider -->
        <div class="section">
            <label for="slider-total-steps">number of steps to complete the search:</label>
            <input type="range" id="slider-total-steps" min="1" max="200" step="1" value="10">
            <span id="display-total-steps" class="slider-value">10</span>
        </div>

        <!-- Current Step Slider -->
        <div class="section">
            <label id="label-current-step" for="slider-current-step">current step (0 to 10):</label>
            <input type="range" id="slider-current-step" min="0" max="10" step="1" value="0">
            <span id="display-current-step" class="slider-value">0</span>
        </div>
    </div>

    <!-- ====================  VISUALIZATION  ==================== -->
    <div id="viz-area">
        <!-- Amplitudes Plot -->
        <div class="plot-container">
            <h4>amplitudes</h4>
            <div id="plot-amplitudes" style="width:100%;height:450px;"></div>
        </div>

        <!-- Probability Plot -->
        <div class="plot-container">
            <div id="prob-mode-buttons">
                <button id="btn-conflicts" class="active">conflicts</button>
                <button id="btn-eigenvalues">eigenvalues</button>
            </div>
            <div id="plot-probability" style="width:100%;height:450px;"></div>
        </div>
    </div>
</div>

<!-- ========== LIBRARIES ========== -->
<script src="https://cdn.plot.ly/plotly-2.24.1.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/mathjs@11/dist/math.min.js"></script>

<!-- ==========  MAIN SCRIPT  ========== -->
<script>
/********************************************************************
 *  GLOBAL CONSTANTS & VARIABLES
 ********************************************************************/
const N = 64;                       // total states
const R = 2;                        // number of solution states
let solutionIndices = [];           // positions of solutions
let conflictNumbers = [];           // C(i)
let randomPhases = [];              // random phases (for visual variety)

const algorithmDefaults = {
    "btn-unstructured":5,
    "btn-adiabatic":200,
    "btn-discrete-adiabatic":200,
    "btn-heuristic":10
};

let currentAlgorithmBtn = "btn-heuristic";      // default
let currentProbModeBtn = "btn-conflicts";       // default

/********************************************************************
 *  DOM ELEMENTS
 ********************************************************************/
const btnsAlgorithms = {};
const btnsProbModes = {};

let sliderTotalSteps, sliderCurrentStep,
    displayTotalSteps, displayCurrentStep,
    labelCurrentStep;

/********************************************************************
 *  INITIALIZATION
 ********************************************************************/
document.addEventListener("DOMContentLoaded", init);

function init(){
    // cache DOM
    ["btn-unstructured","btn-adiabatic","btn-discrete-adiabatic","btn-heuristic"].forEach(id=>{
        btnsAlgorithms[id] = document.getElementById(id);
        btnsAlgorithms[id].addEventListener("click", onAlgorithmSelect);
    });
    ["btn-conflicts","btn-eigenvalues"].forEach(id=>{
        btnsProbModes[id] = document.getElementById(id);
        btnsProbModes[id].addEventListener("click", onProbModeSelect);
    });

    sliderTotalSteps  = document.getElementById("slider-total-steps");
    sliderCurrentStep = document.getElementById("slider-current-step");
    displayTotalSteps = document.getElementById("display-total-steps");
    displayCurrentStep= document.getElementById("display-current-step");
    labelCurrentStep  = document.getElementById("label-current-step");

    sliderTotalSteps.addEventListener("input", onTotalStepsChange);
    sliderCurrentStep.addEventListener("input", onCurrentStepChange);

    // generate problem instance
    initProblemInstance();

    // call first update
    update();
}

/********************************************************************
 *  PROBLEM GENERATION
 ********************************************************************/
function initProblemInstance(){
    // choose 2 unique random solution indices
    while(solutionIndices.length<R){
        const idx = Math.floor(Math.random()*N);
        if(!solutionIndices.includes(idx)) solutionIndices.push(idx);
    }

    // generate random phases
    for(let i=0;i<N;i++){
        randomPhases[i] = Math.random()*2*Math.PI;
    }

    // compute conflict numbers
    conflictNumbers = [];
    for(let i=0;i<N;i++){
        if(solutionIndices.includes(i)){
            conflictNumbers[i]=0;         // by definition, solution has 0 conflicts
        }else{
            let minDist = Infinity;
            solutionIndices.forEach(sIdx=>{
                const d = hammingDistance(i, sIdx);
                if(d<minDist) minDist=d;
            });
            conflictNumbers[i]=minDist;
        }
    }
}

/********************************************************************
 *  EVENT HANDLERS
 ********************************************************************/
function onAlgorithmSelect(e){
    // make active
    setActiveButton(btnsAlgorithms, e.target.id);
    currentAlgorithmBtn = e.target.id;

    // adjust total steps slider to algorithm default
    const def = algorithmDefaults[currentAlgorithmBtn];
    sliderTotalSteps.value = def;
    displayTotalSteps.textContent = def;

    // adjust current step slider
    sliderCurrentStep.max = def;
    sliderCurrentStep.value = 0;
    displayCurrentStep.textContent = 0;
    updateCurrentStepLabel(def);

    update();
}

function onProbModeSelect(e){
    setActiveButton(btnsProbModes, e.target.id);
    currentProbModeBtn = e.target.id;
    update();
}

function onTotalStepsChange(e){
    const newTotal = parseInt(e.target.value);
    displayTotalSteps.textContent = newTotal;

    // adjust current step slider range
    sliderCurrentStep.max = newTotal;
    if(parseInt(sliderCurrentStep.value) > newTotal){
        sliderCurrentStep.value = newTotal;
        displayCurrentStep.textContent = newTotal;
    }
    updateCurrentStepLabel(newTotal);
    update();
}

function onCurrentStepChange(e){
    displayCurrentStep.textContent = e.target.value;
    update();
}

/********************************************************************
 *  MAIN UPDATE
 ********************************************************************/
function update(){
    const totalSteps  = parseInt(sliderTotalSteps.value);
    const currentStep = parseInt(sliderCurrentStep.value);
    const s = totalSteps === 0 ? 0 : (currentStep/totalSteps);   // normalized progress (0..1)

    const algorithm = currentAlgorithmBtn;
    const probMode  = currentProbModeBtn;

    // 1. compute quantum state vector (array of math.complex)
    const stateVector = computeStateVector(algorithm, currentStep, totalSteps, s);

    // 2. build amplitudes plot
    plotAmplitudes(stateVector);

    // 3. probability plot
    plotProbability(stateVector, algorithm, probMode, currentStep, totalSteps);
}

/********************************************************************
 *  QUANTUM STATE CALCULATIONS   (simplified but illustrative)
 ********************************************************************/
function computeStateVector(algorithmBtn, k, T, s){
    const amplitudes = new Array(N).fill(null);

    // algorithm-specific probability on solution states
    let probSolTotal = 0;

    if(algorithmBtn === "btn-unstructured"){             // Grover closed-form
        const theta = Math.asin(Math.sqrt(R/N));
        const sinTerm = Math.sin((2*k+1)*theta);
        const cosTerm = Math.cos((2*k+1)*theta);
        const ampSol  = sinTerm/Math.sqrt(R);
        const ampNon  = cosTerm/Math.sqrt(N-R);

        probSolTotal  = R*ampSol*ampSol;

        for(let i=0;i<N;i++){
            const mag = solutionIndices.includes(i)?ampSol:ampNon;
            const phase = randomPhases[i];
            amplitudes[i] = math.multiply(mag, math.exp(math.complex(0,phase)));
        }
    }else{
        // smooth progress curve for other algorithms
        let progress;
        switch(algorithmBtn){
            case "btn-adiabatic":
                progress = Math.pow(Math.sin(s*Math.PI/2),2); // smooth ease-in
                break;
            case "btn-discrete-adiabatic":
                progress = Math.pow(Math.sin(s*Math.PI/2),2)*0.8; // slower
                break;
            case "btn-heuristic":
                progress = Math.min(1, Math.pow(s,0.5)); // heuristic faster initially
                break;
            default:
                progress = 0;
        }
        probSolTotal = progress;                 // fraction of total probability on solutions
        probSolTotal = Math.min(probSolTotal,1);

        const ampSol  = Math.sqrt(probSolTotal/R);
        const ampNon  = Math.sqrt((1-probSolTotal)/(N-R));

        for(let i=0;i<N;i++){
            const mag = solutionIndices.includes(i)?ampSol:ampNon;
            const phase = randomPhases[i];
            amplitudes[i] = math.multiply(mag, math.exp(math.complex(0,phase)));
        }
    }
    return amplitudes;
}

/********************************************************************
 *  PLOTS
 ********************************************************************/
function plotAmplitudes(stateVector){
    const xs=[], ys=[], colors=[], sizes=[];

    stateVector.forEach((c,idx)=>{
        xs.push(c.re);
        ys.push(c.im);

        // color based on conflict number
        const cNum = conflictNumbers[idx];
        const maxC = Math.max(...conflictNumbers);
        // simple blue (low) -> orange (high)
        const ratio = cNum/maxC;
        const color = `rgb(${Math.round(255*ratio)}, ${Math.round(140*(1-ratio))}, ${Math.round(255*(1-ratio))})`;
        colors.push(color);

        // marker size
        if(solutionIndices.includes(idx)){
            sizes.push(14);
        }else{
            sizes.push(8);
        }
    });

    // highlight a specific non-solution state in red when step 0
    const highlightIndices = (parseInt(sliderCurrentStep.value)===0)?[stateVector.findIndex((_,i)=>!solutionIndices.includes(i))]:[];
    const markerColors = colors.slice();
    highlightIndices.forEach(i=> markerColors[i]="red");

    const trace = {
        x:xs,
        y:ys,
        mode:'markers',
        type:'scattergl',
        marker:{
            color:markerColors,
            size:sizes,
            line:{width:1,color:'#333'}
        },
        hovertemplate:"State %{text}<extra></extra>",
        text:stateVector.map((c,i)=>`#${i}<br>Re=${c.re.toFixed(3)}<br>Im=${c.im.toFixed(3)}`)
    };

    const layout={
        xaxis:{title:"Re",zeroline:false},
        yaxis:{title:"Im",zeroline:false},
        margin:{t:10},
        width:undefined,
        height:450
    };

    Plotly.react("plot-amplitudes",[trace],layout,{displayModeBar:false});
}

function plotProbability(stateVector, algorithmBtn, probModeBtn, k, T){
    if(probModeBtn==="btn-conflicts" || (probModeBtn==="btn-eigenvalues" && algorithmBtn!=="btn-unstructured")){
        // conflicts-based bar plot
        const probByConflict = {};
        stateVector.forEach((c,i)=>{
            const cNum = conflictNumbers[i];
            const prob = math.pow(math.abs(c),2);
            if(probByConflict[cNum]===undefined) probByConflict[cNum]=0;
            probByConflict[cNum]+=prob;
        });

        const xs = Object.keys(probByConflict).map(x=>parseInt(x)).sort((a,b)=>a-b);
        const ys = xs.map(x=>probByConflict[x]);

        const trace = {
            x:xs,
            y:ys,
            type:'bar',
            width:0.6,
            marker:{color:'#4C72B0'}
        };

        const layout={
            xaxis:{title:(probModeBtn==="btn-conflicts")?"number of conflicts":"eigenvalue"},
            yaxis:{title:"probability",range:[0,1]},
            margin:{t:10},
            width:undefined,
            height:450
        };

        Plotly.react("plot-probability",[trace],layout,{displayModeBar:false});
    }else{
        // Grover eigenvalue decomposition
        const theta = Math.asin(Math.sqrt(R/N));
        const angle1 = 2*theta;
        const angle2 = -2*theta;
        const prob1 = Math.pow(Math.sin((2*k+1)*theta),2);
        const prob2 = Math.pow(Math.cos((2*k+1)*theta),2);

        const trace = {
            x:[angle1.toFixed(3), angle2.toFixed(3)],
            y:[prob1, prob2],
            type:'bar',
            width:0.3,
            marker:{color:'#D66B1F'}
        };
        const layout={
            xaxis:{title:"arg(eigenvalue)"},
            yaxis:{title:"probability",range:[0,1]},
            margin:{t:10},
            width:undefined,
            height:450
        };
        Plotly.react("plot-probability",[trace],layout,{displayModeBar:false});
    }
}

/********************************************************************
 *  HELPER FUNCTIONS
 ********************************************************************/
function setActiveButton(groupObj, id){
    Object.values(groupObj).forEach(btn=>btn.classList.remove("active"));
    groupObj[id].classList.add("active");
}

function updateCurrentStepLabel(max){
    labelCurrentStep.textContent = `current step (0 to ${max}):`;
}

function hammingDistance(a,b){
    let x = a ^ b;
    let dist = 0;
    while(x){
        dist += x & 1;
        x >>= 1;
    }
    return dist;
}
</script>
</body>
</html>
