<script>
/* -------- Utility functions -------- */
// Antoine equation for Plot 1 (returns bar)
function f_sat_T(T_C){ // T in °C
    return Math.pow(10, 8.65 - 5818 / (230 + T_C));
}
// Saturation temperature (°C) for given pressure (bar)
function T_sat_from_P(P_bar){
    return 5818 / (8.65 - Math.log10(P_bar)) - 230;
}
// Antoine-like equation for Plot 2 (returns MPa)
function P_sat_T(T_K){
    return Math.pow(10, 10.83 - 5047 / T_K);
}
// Real-gas vapor fugacity (MPa) for Plot 2
function f_vapor_real(P_MPa, T_K){
    return P_MPa * Math.exp(-25 * P_MPa / T_K);
}

/* -------- DOM elements -------- */
const selectPlot = document.getElementById('select-plot');
const sliderPressure = document.getElementById('slider-pressure');
const sliderTemperature = document.getElementById('slider-temperature');
const checkboxHighP = document.getElementById('checkbox-high-pressure');

const valuePressure = document.getElementById('value-pressure');
const valueTemperature = document.getElementById('value-temperature');

const groupPressure = document.getElementById('control-group-pressure');
const groupTemperature = document.getElementById('control-group-temperature');
const checkboxContainer = document.getElementById('checkbox-container');

/* -------- Initial display of slider values -------- */
function refreshValueDisplays(){
    valuePressure.textContent = parseFloat(sliderPressure.value).toFixed(3);
    valueTemperature.textContent = parseInt(sliderTemperature.value,10);
}

/* -------- Main updating routine -------- */
function updatePlot(){
    const mode = selectPlot.value;
    refreshValueDisplays();

    if(mode === 'vs_temp'){
        // Plot 1: fugacity vs temperature
        const P_bar = parseFloat(sliderPressure.value);
        const T_sat_C = T_sat_from_P(P_bar);

        // Build temperature array
        const T_start = 280, T_end = 400, step = 1;
        const temps = [], f_sat_vals = [];
        for(let t=T_start; t<=T_end; t+=step){
            temps.push(t);
            f_sat_vals.push(f_sat_T(t));
        }

        // Split arrays around Tsat
        const temps_liq_stable = temps.filter(t=>t<=T_sat_C);
        const f_liq_stable = temps_liq_stable.map(f_sat_T);

        const temps_liq_meta = temps.filter(t=>t>=T_sat_C);
        const f_liq_meta = temps_liq_meta.map(f_sat_T);

        const temps_vap_stable = temps.filter(t=>t>=T_sat_C);
        const f_vap_stable = temps_vap_stable.map(()=>P_bar);

        const temps_vap_meta = temps.filter(t=>t<=T_sat_C);
        const f_vap_meta = temps_vap_meta.map(()=>P_bar);

        const data = [
            {x:temps_liq_stable, y:f_liq_stable, mode:'lines', line:{color:'#0000FF', width:2}, name:'stable liquid', hoverinfo:'none'},
            {x:temps_vap_stable, y:f_vap_stable, mode:'lines', line:{color:'#0000FF', width:2}, name:'stable vapor', hoverinfo:'none'},
            {x:temps_liq_meta, y:f_liq_meta, mode:'lines', line:{color:'#A0A0FF', width:2, dash:'dash'}, name:'metastable liquid', hoverinfo:'none'},
            {x:temps_vap_meta, y:f_vap_meta, mode:'lines', line:{color:'#A0A0FF', width:2, dash:'dash'}, name:'metastable vapor', hoverinfo:'none'},
            {x:[T_sat_C], y:[P_bar], mode:'markers', marker:{color:'#000000', size:8}, name:'saturation point', hovertemplate:'T=%{x:.1f}°C<br>f=%{y:.3f} bar<extra></extra>'},
            {x:[T_sat_C, T_sat_C], y:[0, P_bar], mode:'lines', line:{color:'#000000', dash:'dash', width:1}, hoverinfo:'none', showlegend:false}
        ];

        const layout = {
            margin:{l:60,r:20,t:20,b:60},
            paper_bgcolor:'#ffffff',
            plot_bgcolor:'#ffffff',
            xaxis:{
                title:'temperature (°C)',
                range:[280,400],
                gridcolor:'#e0e0e0',
                zeroline:false
            },
            yaxis:{
                title:'fugacity (bar)',
                range:[0,0.22],
                gridcolor:'#e0e0e0',
                zeroline:false
            },
            showlegend:false,
            annotations:[
                {x:310, y:0.01, text:'liquid', showarrow:false},
                {x:390, y:P_bar, text:'vapor', showarrow:false},
                {x:T_sat_C, y:P_bar*1.05, text:'T<sup>sat</sup>', showarrow:false}
            ]
        };

        Plotly.react('plot-container', data, layout, {displayModeBar:false});
    }
    else{
        // Plot 2: fugacity vs pressure
        const T_K = parseFloat(sliderTemperature.value);
        const P_sat = P_sat_T(T_K); // MPa
        const highP = checkboxHighP.checked;

        // pressure array
        const P_start = 0, P_end = 3.0, step = 0.03;
        const pressures = [];
        for(let p=P_start; p<=P_end+1e-6; p+=step){ pressures.push(parseFloat(p.toFixed(3))); }

        // vapor fugacity function
        const fV = highP ? (p)=>f_vapor_real(p,T_K) : (p)=>p;

        const f_vap_all = pressures.map(fV);

        // Split regions
        const pressures_vap_stable = pressures.filter(p=>p<=P_sat);
        const f_vap_stable = pressures_vap_stable.map(fV);

        const pressures_vap_meta = pressures.filter(p=>p>=P_sat);
        const f_vap_meta = pressures_vap_meta.map(fV);

        const f_sat = fV(P_sat);

        const pressures_liq_stable = pressures.filter(p=>p>=P_sat);
        const f_liq_stable = pressures_liq_stable.map(()=>f_sat);

        const pressures_liq_meta = pressures.filter(p=>p<=P_sat);
        const f_liq_meta = pressures_liq_meta.map(()=>f_sat);

        const data = [
            {x:pressures_vap_stable, y:f_vap_stable, mode:'lines', line:{color:'#0000FF', width:2}, name:'stable vapor', hoverinfo:'none'},
            {x:pressures_liq_stable, y:f_liq_stable, mode:'lines', line:{color:'#0000FF', width:2}, name:'stable liquid', hoverinfo:'none'},
            {x:pressures_vap_meta, y:f_vap_meta, mode:'lines', line:{color:'#A0A0FF', width:2, dash:'dash'}, name:'metastable vapor', hoverinfo:'none'},
            {x:pressures_liq_meta, y:f_liq_meta, mode:'lines', line:{color:'#A0A0FF', width:2, dash:'dash'}, name:'metastable liquid', hoverinfo:'none'},
            {x:[P_sat], y:[f_sat], mode:'markers', marker:{color:'#000000', size:8}, name:'saturation point',
                hovertemplate:'P=%{x:.2f} MPa<br>f=%{y:.2f} MPa<extra></extra>'},
            {x:[P_sat,P_sat], y:[0,f_sat], mode:'lines', line:{color:'#000000', dash:'dash', width:1}, hoverinfo:'none', showlegend:false}
        ];

        const layout = {
            margin:{l:60,r:20,t:20,b:60},
            paper_bgcolor:'#ffffff',
            plot_bgcolor:'#ffffff',
            xaxis:{
                title:'pressure (MPa)',
                range:[0,3.0],
                gridcolor:'#e0e0e0',
                zeroline:false
            },
            yaxis:{
                title:'fugacity (MPa)',
                range:[0,3.0],
                gridcolor:'#e0e0e0',
                zeroline:false
            },
            showlegend:false,
            annotations:[
                {x:0.5, y:0.25, text:'vapor', showarrow:false},
                {x:2.7, y:f_sat, text:'liquid', showarrow:false},
                {x:P_sat, y:f_sat*1.05, text:'P<sup>sat</sup>', showarrow:false}
            ]
        };

        Plotly.react('plot-container', data, layout, {displayModeBar:false});
    }
}

/* -------- Event listeners -------- */
selectPlot.addEventListener('change', ()=>{
    const mode = selectPlot.value;
    if(mode==='vs_temp'){
        groupPressure.style.display = '';
        groupTemperature.style.display = 'none';
        checkboxContainer.style.display = 'none';
    }else{
        groupPressure.style.display = 'none';
        groupTemperature.style.display = '';
        checkboxContainer.style.display = '';
    }
    updatePlot();
});

sliderPressure.addEventListener('input', updatePlot);
sliderTemperature.addEventListener('input', updatePlot);
checkboxHighP.addEventListener('change', updatePlot);

/* -------- Initial call -------- */
refreshValueDisplays();
updatePlot();
</script>
</body>
</html>
