```html
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Measurement–Disturbance Trade-off Demo</title>
<style>
    body{font-family: Arial, Helvetica, sans-serif; margin:20px;}
    #visualization-container{display:flex;width:100%;}
    #canvas-container,#plot-container{flex:1;}
    canvas{border:1px solid #ccc;}
    .control-group{margin-bottom:10px;}
</style>
<!-- p5.js & Plotly.js -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.min.js"></script>
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>

<h2 style="text-align:center;">Measurement–Disturbance Trade-off on a Bloch-Sphere Slice</h2>

<!-- ------------- VISUALISATIONS ---------------- -->
<div id="visualization-container">
    <!-- Left : Bloch slice -->
    <div id="canvas-container">
        <canvas id="canvas-bloch"></canvas>
    </div>
    <!-- Right : error-plane -->
    <div id="plot-container">
        <div id="plot-error" style="width:100%;height:100%;"></div>
    </div>
</div>

<!-- ------------- CONTROLS ---------------- -->
<div id="control-panel">
    <!-- incompatibility -->
    <div class="control-group">
        <p><b>incompatibility of target observables</b></p>
        <label for="slider-theta">θ</label>
        <input type="range" id="slider-theta" min="0" max="1.57079" step="0.001" value="0.44879">
        <span id="span-theta-value"></span>
        <span style="margin-left:20px;">sin<sup>2</sup>θ = <span id="span-sinsq-theta-value"></span></span>
    </div>
    <hr>

    <!-- metric error measure -->
    <div class="control-group">
        <p><b>metric error measure</b></p>
        <input type="checkbox" id="checkbox-metric-circle">
        <label for="checkbox-metric-circle">display circle of equal metric error</label>

        <input type="checkbox" id="checkbox-metric-region" style="margin-left:20px;">
        <label for="checkbox-metric-region">display region of joint measurability</label>
    </div>
    <hr>

    <!-- first order -->
    <div class="control-group">
        <p><b>first-order optimization</b></p>
        <input type="checkbox" id="checkbox-first-order-vec">
        <label for="checkbox-first-order-vec">display first-order optimizing <i>d</i> vector</label>

        <input type="checkbox" id="checkbox-first-order-region" style="margin-left:20px;">
        <label for="checkbox-first-order-region">display regions of joint measurability</label>
    </div>
    <hr>

    <!-- optimal -->
    <div class="control-group">
        <p><b>optimal approximators</b></p>
        <input type="checkbox" id="checkbox-optimal-pair">
        <label for="checkbox-optimal-pair">display closest optimal pair of approximating vectors</label>

        <input type="checkbox" id="checkbox-optimal-region" style="margin-left:20px;">
        <label for="checkbox-optimal-region">display regions of joint measurability</label>
    </div>
</div>

<script>
/* ------------------- GLOBAL STATE ------------------- */
const R = 150;                              // drawing radius
let theta = 0.44879;                        // θ in rad
let a = {}, b = {};                         // target vectors (unit coords)
let c = {x:0.3, y:0.7};                     // movable vector (unit coords)
let draggingC = false;

const vecLen = v => Math.sqrt(v.x*v.x+v.y*v.y);
const clamp   = (v,maxLen=1)=>{             // returns new vector, length≤maxLen
    let l=vecLen(v);
    return l>maxLen? {x:v.x*maxLen/l, y:v.y*maxLen/l}: v;
};
const minus   = (v,w)=>({x:v.x-w.x,y:v.y-w.y});
const sq      = x=>x*x;

/* ------------------- DOM REFERENCES ------------------- */
const sliderTheta             = document.getElementById('slider-theta');
const spanTheta               = document.getElementById('span-theta-value');
const spanSinSq               = document.getElementById('span-sinsq-theta-value');
const chkMetricCircle         = document.getElementById('checkbox-metric-circle');
const chkMetricRegion         = document.getElementById('checkbox-metric-region');
const chkFirstOrderVec        = document.getElementById('checkbox-first-order-vec');
const chkFirstOrderRegion     = document.getElementById('checkbox-first-order-region');
const chkOptimalPair          = document.getElementById('checkbox-optimal-pair');
const chkOptimalRegion        = document.getElementById('checkbox-optimal-region');

/* ------------------- VECTOR & PLOT UPDATES ------------------- */
function updateAB(){
    a = {x: Math.sin(theta),  y: Math.cos(theta)};
    b = {x:-Math.sin(theta),  y: Math.cos(theta)};
}

function errorCA(){ let d=minus(c,a); return 0.5*(sq(d.x)+sq(d.y)); }
function errorDB(d){ let diff=minus(d,b); return 0.5*(sq(diff.x)+sq(diff.y)); }

/* simple “optimal” d :  projection of b onto circle with radius = |c|  */
function computeDopt(){
    const rc = vecLen(c);
    if(rc===0) return {x:0,y:0};
    const scale = rc/vecLen(b);
    return {x: b.x*scale, y: b.y*scale};
}

/* boundary curve for current θ */
function boundaryCurve(){
    const sin2 = Math.sin(2*theta);
    const xmax = sq(sin2);
    let xs=[], ys=[];
    for(let i=0;i<=100;i++){
        const x = xmax*i/100;
        xs.push(x);
        ys.push( sq( sin2 - Math.sqrt(x) ) );
    }
    return {xs,ys};
}

let plotInitialised=false;
function updatePlot(){
    const dOpt = computeDopt();
    const curErr = {x:errorCA(), y:errorDB(dOpt)};

    const curve = boundaryCurve();
    const greenPoint = {x:sq(Math.sin(2*theta)/2), y:sq(Math.sin(2*theta)/2)};

    const traces = [
        {x:curve.xs, y:curve.ys, mode:'lines', line:{color:'blue'}, name:'boundary'},
        {x:[curErr.x], y:[curErr.y], mode:'markers', marker:{color:'red', size:10}, name:'current'},
        {x:[greenPoint.x], y:[greenPoint.y], mode:'markers',
            marker:{color:'green', size:10}, name:'optimal', visible: chkOptimalPair.checked}
    ];

    const layout={
        margin:{l:50,r:10,b:40,t:10},
        xaxis:{title:'D(C, A)', range:[0,1.5]},
        yaxis:{title:'D(D, B)', range:[0,1.5]},
        showlegend:false
    };

    if(plotInitialised){
        Plotly.react('plot-error', traces, layout);
    }else{
        Plotly.newPlot('plot-error', traces, layout,{displayModeBar:false});
        plotInitialised=true;
    }
}

/* --------------- SPAN VALUES ---------------- */
function updateSpans(){
    spanTheta.textContent   = theta.toFixed(3);
    spanSinSq.textContent   = sq(Math.sin(theta)).toFixed(3);
}

/* --------------- SLIDER EVENT ---------------- */
sliderTheta.addEventListener('input',()=>{
    theta = parseFloat(sliderTheta.value);
    updateAB();
    updateSpans();
    updatePlot();
});

/* --------------- CHECKBOXES ---------------- */
const allCheckboxes=[chkMetricCircle,chkMetricRegion,chkFirstOrderVec,
                     chkFirstOrderRegion,chkOptimalPair,chkOptimalRegion];
allCheckboxes.forEach(chk=>chk.addEventListener('change',()=>{
    updatePlot();
}));

/* ---------------------- p5.js SKETCH ---------------------- */
let sketch = p=>{

    p.setup = ()=>{
        p.createCanvas(400,400).parent('canvas-bloch');
        p.pixelDensity(1);
        updateAB();
        updateSpans();
        updatePlot();
    };

    function toCanvas(v){
        /* convert unit-coords to canvas pixel coords after transform */
        return {x: v.x*R, y: v.y*R};
    }
    function drawArrow(v, col, dashed=false, label=''){
        const pt=toCanvas(v);
        p.push();
        if(dashed) p.drawingContext.setLineDash([6,6]);
        p.stroke(col); p.fill(col);
        p.line(0,0, pt.x, pt.y);
        // arrow head
        const headSize=7;
        const ang=Math.atan2(pt.y,pt.x);
        p.push();
        p.translate(pt.x,pt.y);
        p.rotate(ang);
        p.triangle(0,0, -headSize,-headSize/2, -headSize,headSize/2);
        p.pop();
        if(label){
            p.noStroke();
            p.textSize(14);
            p.text(label, pt.x+5, pt.y+5);
        }
        p.pop();
        p.drawingContext.setLineDash([]);
    }

    p.draw = ()=>{
        p.background(255);
        p.push();
        /* move origin to bottom centre, make y-axis upwards */
        p.translate(p.width/2, p.height-20);
        p.scale(1,-1);

        /* semi-circle */
        p.noFill();
        p.stroke(0);
        p.arc(0,0,2*R,2*R, Math.PI, 0);
        p.line(-R,0,R,0);

        /* vectors */
        drawArrow(a, 'blue', false,'a');
        drawArrow(b, 'red', false,'b');
        drawArrow(c, 'green', false,'c');

        /* optional: metric circle around a */
        if(chkMetricCircle.checked){
            const rad = vecLen(minus(c,a))*R;
            const centre = toCanvas(a);
            p.push();
            p.drawingContext.setLineDash([5,5]);
            p.noFill(); p.stroke('blue');
            p.ellipse(centre.x, centre.y, rad*2, rad*2);
            p.pop();
        }

        /* optional arcs for |c| and |d|  */
        if(chkMetricRegion.checked || chkFirstOrderRegion.checked){
            p.push();
            p.drawingContext.setLineDash([5,5]);
            p.noFill(); p.stroke('green');
            p.ellipse(0,0, 2*vecLen(c)*R, 2*vecLen(c)*R);
            p.pop();
        }
        const dOpt = computeDopt();
        if(chkFirstOrderVec.checked){
            // dashed orange vector from b to dOpt
            p.push();
            p.stroke('orange');
            p.drawingContext.setLineDash([6,6]);
            const bPt=toCanvas(b), dPt=toCanvas(dOpt);
            p.line(bPt.x,bPt.y,dPt.x,dPt.y);
            p.pop();
            drawArrow(dOpt,'orange',false,'d');
        }
        /* optimal pair vectors */
        if(chkOptimalPair.checked){
            const cOpt = {x:Math.sin(2*theta)/2, y:Math.cos(2*theta)/2};
            const dOptPair = {x:-cOpt.x, y:cOpt.y};
            drawArrow(cOpt,'#007700',false,'c*');
            drawArrow(dOptPair,'#225522',false,'d*');
            if(chkOptimalRegion.checked){
                p.push();
                p.drawingContext.setLineDash([5,5]);
                p.stroke('#007700'); p.noFill();
                p.ellipse(0,0, 2*vecLen(cOpt)*R, 2*vecLen(cOpt)*R);
                p.pop();
            }
        }
        p.pop();
    };

    /* ------------- MOUSE INTERACTION ------------- */
    function mouseToUnit(){
        const mx = p.mouseX - p.width/2;
        const my = (p.height-20) - p.mouseY; // flip y
        return {x: mx/R, y: my/R};
    }

    p.mousePressed = ()=>{
        const m = mouseToUnit();
        const dist = vecLen( minus(m,c) )*R;
        if(dist < 10) draggingC=true;
    };
    p.mouseDragged = ()=>{
        if(!draggingC) return;
        let m = mouseToUnit();
        if(m.y<0) m.y=0;                       // stay inside semi-circle
        m = clamp(m,1);
        c = m;
        updatePlot();
    };
    p.mouseReleased = ()=>{ draggingC=false; };
};
new p5(sketch);
</script>
</body>
</html>
