        }
        #plot-container{
            height:500px;
            border:1px solid #ddd;
            margin:0 16px 16px 16px;
        }
    </style>
</head>
<body>
<div id="app-container">
    <!-- Control Panel -->
    <div id="controls-container">
        <div class="control-row">
            <label for="slider-points">collocation points</label>
            <input type="range" id="slider-points" min="5" max="30" step="1" value="20">
            <span id="label-points">20</span>
        </div>
        <div class="control-row">
            <label for="slider-time">t</label>
            <input type="range" id="slider-time" min="0.001" max="0.2" step="0.001" value="0.05">
            <span id="label-time">0.050</span>
        </div>
    </div>

    <!-- Plot Area -->
    <div id="plot-container"></div>
</div>

<script>
/*****************************************************************
 * Utility functions
 *****************************************************************/
const PI = Math.PI;
const PI2 = PI*PI;

/* Fourier series for the temperature T(x,t)
   T(x,t) = (8/π²) Σ_{n=1}^∞ (1/n²) sin(nπ/2) sin(nπx) e^{-2 n² π² t}
   We'll truncate after maxN terms (maxN should be odd)           */
function T(x, t, maxN = 99){
    let sum = 0;
    // Only odd n contribute (since sin(nπ/2)=0 for even n), but we loop over all to keep code simple
    for(let n = 1; n <= maxN; n++){
        if(n % 2 === 0) continue; // skip even n
        const coeff = Math.sin(n*PI/2); // ±1 for odd n
        const term = (1/(n*n)) * coeff * Math.sin(n*PI*x) * Math.exp(-2*n*n*PI2*t);
        sum += term;
    }
    return (8/PI2)*sum;
}

/* Chebyshev–Gauss–Lobatto points in [0,1] */
function chebyshevPoints(numPoints){
    const N = numPoints - 1;
    const xs = [];
    for(let j=0;j<=N;j++){
        xs.push( (Math.cos(j*PI/N) + 1)/2 );
    }
    return xs;
}

/*****************************************************************
 * Plot updating
 *****************************************************************/
const sliderPoints = document.getElementById('slider-points');
const sliderTime   = document.getElementById('slider-time');
const labelPoints  = document.getElementById('label-points');
const labelTime    = document.getElementById('label-time');
const plotDiv      = document.getElementById('plot-container');

/* Create initial empty plot so we can call Plotly.react later */
Plotly.newPlot(plotDiv, [], {
    xaxis:{
        title:'x',
        range:[0,1],
        tickmode:'linear',
        dtick:0.2,
        gridcolor:'#e5e5e5'
    },
    yaxis:{
        title:'T',
        range:[-0.1,1.1],
        tickmode:'linear',
        dtick:0.2,
        gridcolor:'#e5e5e5'
    },
    showlegend:false,
    margin:{t:20,r:20,b:40,l:40}
}, {responsive:true});

/* Recalculate and update the plot */
function updatePlot(){
    const numPoints = parseInt(sliderPoints.value,10);
    const t = parseFloat(sliderTime.value);

    // Update labels
    labelPoints.textContent = numPoints.toString();
    labelTime.textContent = t.toFixed(3);

    /* Blue curve: dense grid */
    const xCurve = [];
    const yCurve = [];
    const NUM_CURVE_POINTS = 200;
    for(let i=0;i<=NUM_CURVE_POINTS;i++){
        const x = i/NUM_CURVE_POINTS;
        xCurve.push(x);
        yCurve.push( T(x, t) );
    }

    /* Red dots: collocation points */
    const xDots = chebyshevPoints(numPoints);
    const yDots = xDots.map(x => T(x, t));

    /* Build traces */
    const traceCurve = {
        x: xCurve,
        y: yCurve,
        type:'scatter',
        mode:'lines',
        line:{color:'blue', width:3}
    };

    const traceDots = {
        x: xDots,
        y: yDots,
        type:'scatter',
        mode:'markers',
        marker:{color:'red', size:8}
    };

    Plotly.react(plotDiv, [traceCurve, traceDots], {
        xaxis:{
            title:'x',
            range:[0,1],
            tickmode:'linear',
            dtick:0.2,
            gridcolor:'#e5e5e5'
        },
        yaxis:{
            title:'T',
            range:[-0.1,1.1],
            tickmode:'linear',
            dtick:0.2,
            gridcolor:'#e5e5e5'
        },
        showlegend:false,
        margin:{t:20,r:20,b:40,l:40}
    });
}

/*****************************************************************
 * Event listeners
 *****************************************************************/
sliderPoints.addEventListener('input', updatePlot);
sliderTime.addEventListener('input',   updatePlot);

/* Initial draw */
updatePlot();
</script>
</body>
</html>
