            <p>The total length of the network is:</p>
            <h3 id="text-total-length">—</h3>
        </div>
    </div>

    <!-- ============= VISUALISATION AREA ============= -->
    <div id="canvas-container">
        <!-- Placeholder canvas required by spec; replaced by p5 -->
        <canvas id="main-canvas"></canvas>
    </div>
</div>

<script>
/* --------------------------------------------------------------------------
 * --------------------------   GLOBAL STATE   ------------------------------
 * -------------------------------------------------------------------------- */
let numRegularPoints = 4;        // default
let numSteinerPoints = 2;        // default
let pointMode         = 'steiner';// default
let showAngles        = true;     // default

let regularPoints  = [];
let steinerPoints  = [];
let mstEdges       = [];
let totalLength    = 0;

let needRecalc     = true;        // flag for when MST must be recomputed

/* --------------------------------------------------------------------------
 * --------------------------   DOM INTERACTION   ---------------------------
 * -------------------------------------------------------------------------- */

// -----  helper to set active button class  -----
function setActiveButton(selectorGroup, activeBtn){
    document.querySelectorAll(selectorGroup).forEach(btn=>{
        btn.classList.toggle('active', btn === activeBtn);
    });
}

// -----  Regular points buttons  -----
document.getElementById('btn-regular-3').addEventListener('click', () => {updateRegularCount(3, event.target);});
document.getElementById('btn-regular-4').addEventListener('click', () => {updateRegularCount(4, event.target);});
document.getElementById('btn-regular-5').addEventListener('click', () => {updateRegularCount(5, event.target);});

function updateRegularCount(n, btn){
    if(numRegularPoints === n) return;
    numRegularPoints = n;
    initRegularPoints();
    resetSteinerPoints();   // spec: reset Steiner positions to centre
    needRecalc = true;
    setActiveButton('#regular-points-selector button', btn);
}

// -----  Steiner points buttons  -----
document.getElementById('btn-steiner-0').addEventListener('click', () => {updateSteinerCount(0, event.target);} );
document.getElementById('btn-steiner-1').addEventListener('click', () => {updateSteinerCount(1, event.target);} );
document.getElementById('btn-steiner-2').addEventListener('click', () => {updateSteinerCount(2, event.target);} );
document.getElementById('btn-steiner-3').addEventListener('click', () => {updateSteinerCount(3, event.target);} );

function updateSteinerCount(n, btn){
    if(numSteinerPoints === n) return;
    numSteinerPoints = n;

    // Adjust array length
    const center = {x: canvasWidth/2, y: canvasHeight/2};
    if(steinerPoints.length < n){
        while(steinerPoints.length < n){
            steinerPoints.push({...center});
        }
    }else{
        steinerPoints.length = n;
    }
    needRecalc = true;
    setActiveButton('#steiner-points-selector button', btn);
}

// -----  Radio buttons  -----
document.getElementById('radio-regular').addEventListener('change', ()=>{ if(event.target.checked){ pointMode='regular'; }});
document.getElementById('radio-steiner').addEventListener('change', ()=>{ if(event.target.checked){ pointMode='steiner'; }});

// -----  Checkbox  -----
document.getElementById('checkbox-show-angles').addEventListener('change', ()=>{
    showAngles = event.target.checked;
});

/* --------------------------------------------------------------------------
 * -----------------------------   p5 SKETCH   ------------------------------
 * -------------------------------------------------------------------------- */
const canvasWidth  = 600;
const canvasHeight = 600;

let draggedPointIndex = -1;   // index in whichever array is active
let draggingType      = null; // 'regular' | 'steiner'

const sketch = (p) => {

    p.setup = () => {
        // Remove placeholder canvas so we don't duplicate IDs
        const placeholder = document.getElementById('main-canvas');
        if(placeholder) placeholder.remove();

        let cnv = p.createCanvas(canvasWidth, canvasHeight);
        cnv.parent('canvas-container');
        cnv.id('main-canvas');

        // Initial point placement
        initRegularPoints();
        resetSteinerPoints();
        recomputeMST();
    };

    p.draw = () => {
        p.background('#EEEEEE');

        // recompute only if necessary
        if(needRecalc){
            recomputeMST();
            needRecalc = false;
        }

        // ----------- draw all complete-graph edges (light lines) -----------
        p.stroke('rgba(190,160,130,0.5)');
        p.strokeWeight(1);
        const allPoints = [...regularPoints, ...steinerPoints];
        for(let i=0;i<allPoints.length;i++){
            for(let j=i+1;j<allPoints.length;j++){
                p.line(allPoints[i].x, allPoints[i].y, allPoints[j].x, allPoints[j].y);
            }
        }

        // ----------- draw MST edges (dark thick lines) -----------
        p.stroke('#333333');
        p.strokeWeight(3);
        mstEdges.forEach(e =>{
            const a = allPoints[e.i], b = allPoints[e.j];
            p.line(a.x,a.y,b.x,b.y);
        });

        // ----------- draw regular points -----------
        p.stroke('#0000FF');    // blue
        p.strokeWeight(2);
        p.fill('#FFFFFF');
        regularPoints.forEach(pt=>{
            p.circle(pt.x, pt.y, 16); // radius 8px
        });

        // ----------- draw steiner points -----------
        p.stroke('#FF0000');    // red
        steinerPoints.forEach(pt=>{
            p.fill('#FFFFFF');
            p.circle(pt.x, pt.y, 16); // outer 8
            p.circle(pt.x, pt.y, 8);  // inner 4
        });

        // ----------- draw angles if enabled -----------
        if(showAngles){
            drawAngles(p, allPoints);
        }
    };

    /* --------------------  Dragging logic  -------------------- */
    p.mousePressed = () => {
        const mx = p.mouseX, my = p.mouseY;
        const radiusSq = 10*10;

        if(pointMode === 'regular'){
            for(let i=0;i<regularPoints.length;i++){
                if(sqDist(mx,my, regularPoints[i]) < radiusSq){
                    draggedPointIndex = i;
                    draggingType = 'regular';
                    return;
                }
            }
        }else{ // steiner
            for(let i=0;i<steinerPoints.length;i++){
                if(sqDist(mx,my, steinerPoints[i]) < radiusSq){
                    draggedPointIndex = i;
                    draggingType = 'steiner';
                    return;
                }
            }
        }
    };

    p.mouseDragged = () => {
        if(draggedPointIndex === -1) return;
        let pt;
        if(draggingType === 'regular'){
            pt = regularPoints[draggedPointIndex];
        }else{
            pt = steinerPoints[draggedPointIndex];
        }
        pt.x = p.constrain(p.mouseX, 0, canvasWidth);
        pt.y = p.constrain(p.mouseY, 0, canvasHeight);
        needRecalc = true;   // trigger update of MST & length
    };

    p.mouseReleased = () => {
        draggedPointIndex = -1;
        draggingType      = null;
    };
};

/* --------------------------------------------------------------------------
 * ----------------------   INITIALISATION HELPERS   ------------------------
 * -------------------------------------------------------------------------- */
function initRegularPoints(){
    regularPoints = [];
    const cx = canvasWidth/2, cy = canvasHeight/2;
    const radius = canvasWidth*0.35;
    for(let i=0;i<numRegularPoints;i++){
        const angle = i*2*Math.PI/numRegularPoints - Math.PI/2; // start at top
        regularPoints.push({
            x: cx + radius*Math.cos(angle),
            y: cy + radius*Math.sin(angle)
        });
    }
}

function resetSteinerPoints(){
    steinerPoints = [];
    const center = {x: canvasWidth/2, y: canvasHeight/2};
    for(let i=0;i<numSteinerPoints;i++){
        steinerPoints.push({...center});
    }
}

/* --------------------------------------------------------------------------
 * --------------------------   GEOMETRY UTILITIES   ------------------------
 * -------------------------------------------------------------------------- */
function sqDist(x,y, pt){
    const dx = x-pt.x, dy = y-pt.y;
    return dx*dx + dy*dy;
}

function recomputeMST(){
    const allPoints = [...regularPoints, ...steinerPoints];
    // --- Kruskal algorithm ---
    let edges = [];
    for(let i=0;i<allPoints.length;i++){
        for(let j=i+1;j<allPoints.length;j++){
            const dx = allPoints[i].x - allPoints[j].x;
            const dy = allPoints[i].y - allPoints[j].y;
            const d  = Math.hypot(dx,dy);
            edges.push({i,j,w:d});
        }
    }
    edges.sort((a,b)=>a.w - b.w);

    // Union-Find
    const uf = new UnionFind(allPoints.length);
    mstEdges = [];
    totalLength = 0;
    edges.forEach(e=>{
        if(uf.union(e.i, e.j)){
            mstEdges.push(e);
            totalLength += e.w;
        }
    });

    // Update length text
    document.getElementById('text-total-length').textContent = totalLength.toFixed(5);
}

class UnionFind{
    constructor(n){
        this.parent = Array.from({length:n}, (_,i)=>i);
        this.rank   = Array(n).fill(0);
    }
    find(x){
        if(this.parent[x] !== x) this.parent[x] = this.find(this.parent[x]);
        return this.parent[x];
    }
    union(a,b){
        const ra = this.find(a), rb = this.find(b);
        if(ra === rb) return false;
        if(this.rank[ra] < this.rank[rb])      this.parent[ra] = rb;
        else if(this.rank[ra] > this.rank[rb]) this.parent[rb] = ra;
        else { this.parent[rb] = ra; this.rank[ra]++; }
        return true;
    }
}

/* --------------------------------------------------------------------------
 * ----------------------   ANGLE DISPLAY FUNCTION   ------------------------
 * -------------------------------------------------------------------------- */
function drawAngles(p, allPoints){
    // Build adjacency map for quick lookup
    const neighbors = Array(allPoints.length).fill(null).map(()=>[]);
    mstEdges.forEach(e=>{
        neighbors[e.i].push(e.j);
        neighbors[e.j].push(e.i);
    });

    const stStart = regularPoints.length; // index where Steiner points start

    for(let sIdx=0; sIdx<steinerPoints.length; sIdx++){
        const globalIndex = stStart + sIdx;
        const nbrs = neighbors[globalIndex];
        if(nbrs.length < 2) continue;

        // Compute angles to each neighbor
        const base = allPoints[globalIndex];
        const angles = nbrs.map(n=>{
            const v = allPoints[n];
            return Math.atan2(v.y - base.y, v.x - base.x);
        });

        // Sort angles
        angles.sort((a,b)=>a-b);

        // Compute angle differences and bisectors
        for(let i=0;i<angles.length;i++){
            const a1 = angles[i];
            const a2 = angles[(i+1)%angles.length] + (i+1===angles.length ? 2*Math.PI : 0);
            const diff = (a2 - a1)*180/Math.PI;
            const bisector = a1 + (a2 - a1)/2;
            const tx = base.x + 25*Math.cos(bisector);
            const ty = base.y + 25*Math.sin(bisector);

            p.noStroke();
            p.fill(0);
            p.textSize(12);
            p.textAlign(p.CENTER, p.CENTER);
            p.text(Math.round(diff)+'°', tx, ty);
        }
    }
}

/* --------------------------------------------------------------------------
 * -----------------------------   START P5   -------------------------------
 * -------------------------------------------------------------------------- */
new p5(sketch);
</script>
</body>
</html>
