      document.getElementById("value-mean-w"),
      document.getElementById("value-mean-x"),
      document.getElementById("value-mean-y"),
      document.getElementById("value-mean-z")
    ];
    const sdValues = [
      document.getElementById("value-sd-w"),
      document.getElementById("value-sd-x"),
      document.getElementById("value-sd-y"),
      document.getElementById("value-sd-z")
    ];
    const fRatioDisplay = document.getElementById("f-ratio-display");

    /* ---------- State Variables ---------- */
    let groupMeans = [0,0,0,0];
    let groupSDs   = [1,1,1,1];

    let grandMean          = 0;
    let MS_between         = 0;
    let MS_within          = 0;
    let F_ratio            = 0;

    const n = 10;   // subjects per group
    const k = 4;    // number of groups

    /* ---------- Update Calculation & UI ---------- */
    function updateFromSliders(){
      // 1. read sliders & update spans
      for(let i=0;i<k;i++){
        groupMeans[i] = parseFloat(meanSliders[i].value);
        groupSDs[i]   = parseFloat(sdSliders[i].value);

        meanValues[i].textContent = groupMeans[i].toFixed(1);
        sdValues[i].textContent   = groupSDs[i].toFixed(1);
      }

      // 2. ANOVA calculations
      grandMean = groupMeans.reduce((a,b)=>a+b,0)/k;

      const SS_between = n * groupMeans.reduce((acc,gm)=>acc + Math.pow(gm-grandMean,2),0);
      MS_between = SS_between / (k-1);

      const SS_within = groupSDs.reduce((acc,sd)=>acc + Math.pow(sd,2)*(n-1),0);
      MS_within = SS_within / (n*k - k);

      F_ratio = MS_within === 0 ? Infinity : MS_between / MS_within;

      // 3. update textual F-ratio display
      if(!isFinite(F_ratio)){ fRatioDisplay.textContent = "F ratio = ∞"; }
      else{
        const digits = (F_ratio<1) ? 4 : 2;
        fRatioDisplay.textContent = "F ratio = " + F_ratio.toFixed(digits);
      }

      // 4. redraw p5 canvas
      if(p5Instance) p5Instance.redraw();
    }

    /* ---------- Attach Event Listeners ---------- */
    [...meanSliders,...sdSliders].forEach(slider=>{
      slider.addEventListener("input",updateFromSliders);
    });

    /* ---------- p5 Sketch ---------- */
    let p5Instance = new p5(sketch, document.getElementById("p5-holder"));

    function sketch(p){
      const CANVAS_W = 800;
      const CANVAS_H = 550;

      /* Colors */
      const COLOR_GROUP = "#F57F29";
      const COLOR_MS_B  = "#4691A5";
      const COLOR_MS_W  = "#D49D3C";
      const COLOR_F     = "#8C3A8D";

      p.setup = function(){
        p.createCanvas(CANVAS_W, CANVAS_H);
        p.noLoop();
        p.textFont("Arial");
      };

      p.draw = function(){
        p.clear();
        p.background(255);

        /* ----- Layout variables ----- */
        const topPlotHeight    = CANVAS_H * 0.55;
        const bottomPlotHeight = CANVAS_H - topPlotHeight;

        /* ================= TOP PLOT : GROUP DATA ================= */
        const margins = {left:60,right:30,top:40,bottom:30};
        const plotX0  = margins.left;
        const plotX1  = CANVAS_W - margins.right;
        const plotY0  = margins.top;
        const plotY1  = topPlotHeight - margins.bottom;

        // Axis
        p.stroke(0);
        p.line(plotX0, plotY0, plotX0, plotY1); // y-axis
        p.line(plotX0, plotY1, plotX1, plotY1); // x-axis

        // y ticks
        p.textAlign(p.RIGHT,p.CENTER);
        for(let v=-10; v<=10; v+=5){
          const y = p.map(v, -10, 10, plotY1, plotY0);
          p.noStroke();
          p.fill(0);
          p.text(v.toString(), plotX0-8, y);
          p.stroke(200);
          p.line(plotX0, y, plotX1, y);
        }

        // x positions for groups
        const groupWidth = (plotX1 - plotX0) / k;
        p.textAlign(p.CENTER,p.TOP);
        p.noStroke();
        p.fill(0);
        for(let i=0;i<k;i++){
          const x = plotX0 + groupWidth*(i+0.5);
          p.text(groupNames[i], x, plotY1+5);
        }

        // grand mean dashed line
        const gmY = p.map(grandMean, -10, 10, plotY1, plotY0);
        p.stroke(0);
        p.drawingContext.setLineDash([5,5]);
        p.line(plotX0, gmY, plotX1, gmY);
        p.drawingContext.setLineDash([]);

        // group bars
        p.rectMode(p.CENTER);
        for(let i=0;i<k;i++){
          const xCenter = plotX0 + groupWidth*(i+0.5);
          const barHalfWidth = groupWidth*0.25;

          const topY    = p.map(groupMeans[i]+groupSDs[i], -10, 10, plotY1, plotY0);
          const bottomY = p.map(groupMeans[i]-groupSDs[i], -10, 10, plotY1, plotY0);
          const barH    = Math.max(4, bottomY - topY);

          // SD rectangle
          p.noStroke();
          p.fill(COLOR_GROUP);
          p.rect(xCenter, (topY+bottomY)/2, barHalfWidth*2, barH);

          // mean line
          const meanY = p.map(groupMeans[i], -10, 10, plotY1, plotY0);
          p.stroke(0);
          p.line(xCenter - barHalfWidth, meanY, xCenter + barHalfWidth, meanY);
        }

        // F-ratio text inside canvas
        p.noStroke();
        p.fill(0);
        p.textAlign(p.LEFT,p.TOP);
        const txt = isFinite(F_ratio) ? "F ratio = " + ((F_ratio<1)?F_ratio.toFixed(4):F_ratio.toFixed(2)) : "F ratio = ∞";
        p.text(txt, plotX0, 10);

        /* ================= BOTTOM PLOT : ANOVA STATS ================= */
        const bpMargins = {left:100, right:30, top:topPlotHeight+40, barHeight:25, barGap:20};
        const axisX0 = bpMargins.left;
        const axisX1 = CANVAS_W - bpMargins.right;
        const axisMax = 200; // clamps bar widths

        // x-axis line
        p.stroke(0);
        p.line(axisX0, bpMargins.top-30, axisX1, bpMargins.top-30);

        // x ticks and labels
        p.textAlign(p.CENTER,p.TOP);
        for(let v=0; v<=axisMax; v+=50){
          const x = p.map(v,0,axisMax,axisX0,axisX1);
          p.noStroke();
          p.fill(0);
          p.text(v.toString(), x, bpMargins.top-25);
          p.stroke(220);
          p.line(x, bpMargins.top-30, x, CANVAS_H-10);
        }

        // Bars data
        const bars = [
          {label:"MS Between", value:MS_between, color:COLOR_MS_B},
          {label:"MS Within",  value:MS_within,  color:COLOR_MS_W},
          {label:"F-Ratio",    value:F_ratio,    color:COLOR_F}
        ];

        p.textAlign(p.RIGHT,p.CENTER);
        for(let i=0;i<bars.length;i++){
          const y = bpMargins.top + i*(bpMargins.barHeight + bpMargins.barGap);
          const wVal = Math.min(bars[i].value, axisMax);
          const wPx  = p.map(wVal, 0, axisMax, 0, axisX1-axisX0);

          // label
          p.noStroke();
          p.fill(0);
          p.text(bars[i].label, axisX0-10, y);

          // bar
          p.fill(bars[i].color);
          p.rectMode(p.CORNER);
          p.rect(axisX0, y - bpMargins.barHeight/2, wPx, bpMargins.barHeight);
        }

        // Title
        p.textAlign(p.CENTER,p.BOTTOM);
        p.fill(0);
        p.text("Analysis of the F-Ratio", (axisX0+axisX1)/2, topPlotHeight+10);
      };
    }

    /* ---------- Initial Draw ---------- */
    updateFromSliders();  // sets initial state and draws once
  </script>
</body>
</html>
